import React, { useState } from 'react';
import { 
  Share2, 
  Search, 
  Filter, 
  Eye, 
  Download, 
  User, 
  Calendar,
  Shield,
  Globe,
  Lock,
  UserCheck,
  Clock,
  File
} from 'lucide-react';

const SharedDocuments = () => {
  const [searchQuery, setSearchQuery] = useState('');
  const [filterType, setFilterType] = useState('all');
  const [activeTab, setActiveTab] = useState('shared-with-me'); // 'shared-with-me' or 'shared-by-me'

  // Mock data for documents shared with me
  const [sharedWithMe] = useState([
    {
      id: 1,
      name: 'Q3 Marketing Report.pdf',
      type: 'pdf',
      size: '3.2 MB',
      owner: 'Sarah Johnson',
      sharedDate: '2024-08-20',
      permission: 'read-only',
      lastAccessed: '2 hours ago',
      tags: ['marketing', 'report']
    },
    {
      id: 2,
      name: 'Team Meeting Notes.docx',
      type: 'document',
      size: '1.5 MB',
      owner: 'Mike Chen',
      sharedDate: '2024-08-19',
      permission: 'full-access',
      lastAccessed: '1 day ago',
      tags: ['meeting', 'notes']
    },
    {
      id: 3,
      name: 'Product Roadmap.xlsx',
      type: 'spreadsheet',
      size: '2.1 MB',
      owner: 'Emma Davis',
      sharedDate: '2024-08-18',
      permission: 'read-only',
      lastAccessed: '3 days ago',
      tags: ['product', 'roadmap']
    }
  ]);

  // Mock data for documents I've shared
  const [sharedByMe] = useState([
    {
      id: 4,
      name: 'Project Proposal.pdf',
      type: 'pdf',
      size: '2.4 MB',
      sharedWith: ['john@example.com', 'sarah@example.com'],
      sharedDate: '2024-08-20',
      permissions: { 'john@example.com': 'full-access', 'sarah@example.com': 'read-only' },
      views: 15,
      lastViewed: '1 hour ago',
      tags: ['proposal', 'business']
    },
    {
      id: 5,
      name: 'Design Guidelines.pdf',
      type: 'pdf',
      size: '5.8 MB',
      sharedWith: ['team@example.com'],
      sharedDate: '2024-08-19',
      permissions: { 'team@example.com': 'read-only' },
      views: 23,
      lastViewed: '2 hours ago',
      tags: ['design', 'guidelines']
    }
  ]);

  const currentDocuments = activeTab === 'shared-with-me' ? sharedWithMe : sharedByMe;

  const filteredDocuments = currentDocuments.filter(doc => {
    const matchesSearch = doc.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
                         doc.tags.some(tag => tag.toLowerCase().includes(searchQuery.toLowerCase()));
    const matchesFilter = filterType === 'all' || doc.type === filterType;
    return matchesSearch && matchesFilter;
  });

  const getFileIcon = (type) => {
    const icons = {
      pdf: '📄',
      image: '🖼️',
      document: '📝',
      spreadsheet: '📊',
      presentation: '📊',
      default: '📁'
    };
    return icons[type] || icons.default;
  };

  const getFileColor = (type) => {
    const colors = {
      pdf: 'from-red-400 to-red-600',
      image: 'from-purple-400 to-purple-600',
      document: 'from-blue-400 to-blue-600',
      spreadsheet: 'from-green-400 to-green-600',
      presentation: 'from-orange-400 to-orange-600',
      default: 'from-gray-400 to-gray-600'
    };
    return colors[type] || colors.default;
  };

  const getPermissionColor = (permission) => {
    return permission === 'full-access' ? 'text-green-600 bg-green-50' : 'text-yellow-600 bg-yellow-50';
  };

  const SharedWithMeCard = ({ doc }) => (
    <div className="bg-white/80 backdrop-blur-lg rounded-2xl shadow-lg border border-white/20 hover:shadow-xl transition-all duration-300 hover:-translate-y-1 group">
      <div className="p-6">
        {/* Header */}
        <div className="flex items-start justify-between mb-4">
          <div className={`w-12 h-12 bg-gradient-to-br ${getFileColor(doc.type)} rounded-xl flex items-center justify-center text-white text-xl shadow-lg`}>
            {getFileIcon(doc.type)}
          </div>
          <div className="flex items-center space-x-2">
            <span className={`px-2 py-1 text-xs font-medium rounded-lg ${getPermissionColor(doc.permission)}`}>
              {doc.permission}
            </span>
          </div>
        </div>

        {/* Content */}
        <div className="mb-4">
          <h3 className="font-semibold text-gray-800 mb-2 line-clamp-2">{doc.name}</h3>
          <div className="flex items-center space-x-2 text-sm text-gray-500 mb-2">
            <User className="w-4 h-4" />
            <span>Shared by {doc.owner}</span>
          </div>
          <div className="flex items-center space-x-3 text-sm text-gray-500 mb-2">
            <span>{doc.size}</span>
            <span>•</span>
            <span>Last accessed {doc.lastAccessed}</span>
          </div>
          <div className="flex flex-wrap gap-1">
            {doc.tags.slice(0, 2).map((tag, index) => (
              <span key={index} className="px-2 py-1 bg-[#B2B0E8]/20 text-[#3B38A0] text-xs rounded-lg">
                {tag}
              </span>
            ))}
          </div>
        </div>

        {/* Actions */}
        <div className="flex items-center justify-between pt-4 border-t border-gray-200/50">
          <div className="flex items-center space-x-2">
            <button className="p-2 text-gray-400 hover:text-[#3B38A0] hover:bg-[#B2B0E8]/20 rounded-lg transition-all">
              <Eye className="w-4 h-4" />
            </button>
            <button className="p-2 text-gray-400 hover:text-[#3B38A0] hover:bg-[#B2B0E8]/20 rounded-lg transition-all">
              <Download className="w-4 h-4" />
            </button>
          </div>
          <div className="flex items-center space-x-1 text-xs text-gray-500">
            <Calendar className="w-3 h-3" />
            <span>Shared {doc.sharedDate}</span>
          </div>
        </div>
      </div>
    </div>
  );

  const SharedByMeCard = ({ doc }) => (
    <div className="bg-white/80 backdrop-blur-lg rounded-2xl shadow-lg border border-white/20 hover:shadow-xl transition-all duration-300 hover:-translate-y-1 group">
      <div className="p-6">
        {/* Header */}
        <div className="flex items-start justify-between mb-4">
          <div className={`w-12 h-12 bg-gradient-to-br ${getFileColor(doc.type)} rounded-xl flex items-center justify-center text-white text-xl shadow-lg`}>
            {getFileIcon(doc.type)}
          </div>
          <div className="flex items-center space-x-2">
            <div className="flex items-center space-x-1 text-sm text-gray-500">
              <Eye className="w-4 h-4" />
              <span>{doc.views} views</span>
            </div>
          </div>
        </div>

        {/* Content */}
        <div className="mb-4">
          <h3 className="font-semibold text-gray-800 mb-2 line-clamp-2">{doc.name}</h3>
          <div className="flex items-center space-x-2 text-sm text-gray-500 mb-2">
            <UserCheck className="w-4 h-4" />
            <span>Shared with {doc.sharedWith.length} user{doc.sharedWith.length > 1 ? 's' : ''}</span>
          </div>
          <div className="flex items-center space-x-3 text-sm text-gray-500 mb-2">
            <span>{doc.size}</span>
            <span>•</span>
            <span>Last viewed {doc.lastViewed}</span>
          </div>
          <div className="flex flex-wrap gap-1 mb-3">
            {doc.tags.slice(0, 2).map((tag, index) => (
              <span key={index} className="px-2 py-1 bg-[#B2B0E8]/20 text-[#3B38A0] text-xs rounded-lg">
                {tag}
              </span>
            ))}
          </div>

          {/* Shared with users */}
          <div className="space-y-1">
            {doc.sharedWith.slice(0, 2).map((email, index) => (
              <div key={index} className="flex items-center justify-between text-xs">
                <span className="text-gray-600">{email}</span>
                <span className={`px-2 py-0.5 rounded text-xs ${getPermissionColor(doc.permissions[email])}`}>
                  {doc.permissions[email]}
                </span>
              </div>
            ))}
            {doc.sharedWith.length > 2 && (
              <span className="text-xs text-gray-500">+{doc.sharedWith.length - 2} more</span>
            )}
          </div>
        </div>

        {/* Actions */}
        <div className="flex items-center justify-between pt-4 border-t border-gray-200/50">
          <div className="flex items-center space-x-2">
            <button className="p-2 text-gray-400 hover:text-[#3B38A0] hover:bg-[#B2B0E8]/20 rounded-lg transition-all">
              <Eye className="w-4 h-4" />
            </button>
            <button className="p-2 text-gray-400 hover:text-[#3B38A0] hover:bg-[#B2B0E8]/20 rounded-lg transition-all">
              <Share2 className="w-4 h-4" />
            </button>
          </div>
          <div className="flex items-center space-x-1 text-xs text-gray-500">
            <Calendar className="w-3 h-3" />
            <span>Shared {doc.sharedDate}</span>
          </div>
        </div>
      </div>
    </div>
  );

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex flex-col sm:flex-row sm:items-center sm:justify-between gap-4">
        <div>
          <h1 className="text-3xl font-bold text-gray-800">Shared Documents</h1>
          <p className="text-gray-600 mt-1">Access and manage shared files</p>
        </div>
      </div>

      {/* Tabs */}
      <div className="bg-white/80 backdrop-blur-lg rounded-2xl shadow-lg border border-white/20 p-6">
        <div className="flex space-x-1 bg-gray-100/50 rounded-xl p-1 mb-6">
          <button
            onClick={() => setActiveTab('shared-with-me')}
            className={`flex-1 px-4 py-3 text-sm font-semibold rounded-lg transition-all duration-200 ${
              activeTab === 'shared-with-me'
                ? 'bg-white text-[#3B38A0] shadow-lg'
                : 'text-gray-600 hover:text-[#3B38A0]'
            }`}
          >
            Shared with Me ({sharedWithMe.length})
          </button>
          <button
            onClick={() => setActiveTab('shared-by-me')}
            className={`flex-1 px-4 py-3 text-sm font-semibold rounded-lg transition-all duration-200 ${
              activeTab === 'shared-by-me'
                ? 'bg-white text-[#3B38A0] shadow-lg'
                : 'text-gray-600 hover:text-[#3B38A0]'
            }`}
          >
            Shared by Me ({sharedByMe.length})
          </button>
        </div>

        {/* Filters and Search */}
        <div className="flex flex-col lg:flex-row lg:items-center gap-4">
          {/* Search */}
          <div className="relative flex-1 max-w-md">
            <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 w-5 h-5 text-gray-400" />
            <input
              type="text"
              placeholder="Search shared documents..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              className="w-full pl-10 pr-4 py-3 bg-white/50 border border-gray-200 rounded-xl focus:outline-none focus:ring-2 focus:ring-[#7A85C1] focus:border-transparent transition-all duration-200"
            />
          </div>

          {/* Filter */}
          <select
            value={filterType}
            onChange={(e) => setFilterType(e.target.value)}
            className="px-4 py-3 bg-white/50 border border-gray-200 rounded-xl focus:outline-none focus:ring-2 focus:ring-[#7A85C1] focus:border-transparent transition-all duration-200"
          >
            <option value="all">All Types</option>
            <option value="pdf">PDF</option>
            <option value="image">Images</option>
            <option value="document">Documents</option>
            <option value="spreadsheet">Spreadsheets</option>
          </select>
        </div>
      </div>

      {/* Statistics */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        <div className="bg-gradient-to-r from-[#1A2A80] to-[#3B38A0] rounded-2xl p-6 text-white">
          <div className="flex items-center space-x-3">
            <div className="w-12 h-12 bg-white/20 rounded-xl flex items-center justify-center">
              <Share2 className="w-6 h-6" />
            </div>
            <div>
              <p className="text-white/80 text-sm">Total Shared</p>
              <p className="text-2xl font-bold">{sharedWithMe.length + sharedByMe.length}</p>
            </div>
          </div>
        </div>

        <div className="bg-gradient-to-r from-[#3B38A0] to-[#7A85C1] rounded-2xl p-6 text-white">
          <div className="flex items-center space-x-3">
            <div className="w-12 h-12 bg-white/20 rounded-xl flex items-center justify-center">
              <Eye className="w-6 h-6" />
            </div>
            <div>
              <p className="text-white/80 text-sm">Total Views</p>
              <p className="text-2xl font-bold">
                {sharedByMe.reduce((sum, doc) => sum + doc.views, 0)}
              </p>
            </div>
          </div>
        </div>

        <div className="bg-gradient-to-r from-[#7A85C1] to-[#B2B0E8] rounded-2xl p-6 text-white">
          <div className="flex items-center space-x-3">
            <div className="w-12 h-12 bg-white/20 rounded-xl flex items-center justify-center">
              <UserCheck className="w-6 h-6" />
            </div>
            <div>
              <p className="text-white/80 text-sm">Active Shares</p>
              <p className="text-2xl font-bold">
                {sharedByMe.reduce((sum, doc) => sum + doc.sharedWith.length, 0)}
              </p>
            </div>
          </div>
        </div>
      </div>

      {/* Documents Grid */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {filteredDocuments.map(doc => (
          activeTab === 'shared-with-me' ? (
            <SharedWithMeCard key={doc.id} doc={doc} />
          ) : (
            <SharedByMeCard key={doc.id} doc={doc} />
          )
        ))}
      </div>

      {/* Empty State */}
      {filteredDocuments.length === 0 && (
        <div className="text-center py-12">
          <div className="w-24 h-24 bg-gray-200 rounded-full flex items-center justify-center mx-auto mb-4">
            <Share2 className="w-12 h-12 text-gray-400" />
          </div>
          <h3 className="text-xl font-semibold text-gray-800 mb-2">
            {activeTab === 'shared-with-me' ? 'No shared documents' : 'No documents shared by you'}
          </h3>
          <p className="text-gray-600">
            {activeTab === 'shared-with-me' 
              ? 'Documents shared with you will appear here' 
              : 'Start sharing documents with your team'
            }
          </p>
        </div>
      )}
    </div>
  );
};

export default SharedDocuments;
