import React, { useState, useEffect } from 'react';
import Sidebar from './components/Sidebar';
import Header from './components/Header';
import Dashboard from './components/Dashboard';
import Documents from './components/SharedDocuments';
import SharedDocuments from './components/SharedDocuments';
import Folders from './components/Folders';
import Activity from './components/Activity';
import Settings from './components/Settings';
import Login from './components/Login';
import Register from './components/Register';

const App = () => {
  const [currentView, setCurrentView] = useState('login');
  const [user, setUser] = useState(null);
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const [notifications, setNotifications] = useState([
    { id: 1, message: 'Design Mockups.jpg was shared with you', time: '2 hours ago', read: false },
    { id: 2, message: 'New folder "Projects" created', time: '1 day ago', read: true }
  ]);

  const handleLogin = (userData) => {
    setUser(userData);
    setCurrentView('dashboard');
  };

  const handleLogout = () => {
    setUser(null);
    setCurrentView('login');
  };

  const renderCurrentView = () => {
    if (!user) {
      switch (currentView) {
        case 'register':
          return <Register onSwitchToLogin={() => setCurrentView('login')} />;
        default:
          return <Login onLogin={handleLogin} onSwitchToRegister={() => setCurrentView('register')} />;
      }
    }

    switch (currentView) {
      case 'documents':
        return <Documents />;
      case 'shared':
        return <SharedDocuments />;
      case 'folders':
        return <Folders />;
      case 'activity':
        return <Activity />;
      case 'settings':
        return <Settings user={user} />;
      default:
        return <Dashboard />;
    }
  };

  if (!user) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-[#1A2A80] via-[#3B38A0] to-[#7A85C1]">
        {renderCurrentView()}
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-50 to-[#B2B0E8]/20">
      {/* Mobile overlay */}
      {sidebarOpen && (
        <div
          className="fixed inset-0 z-40 bg-black/50 backdrop-blur-sm lg:hidden"
          onClick={() => setSidebarOpen(false)}
        />
      )}

      {/* Sidebar */}
      <Sidebar
        currentView={currentView}
        setCurrentView={setCurrentView}
        sidebarOpen={sidebarOpen}
        setSidebarOpen={setSidebarOpen}
        user={user}
        onLogout={handleLogout}
      />

      {/* Main content */}
      <div className="lg:ml-64 flex flex-col min-h-screen">
        <Header
          user={user}
          setSidebarOpen={setSidebarOpen}
          notifications={notifications}
        />
        <main className="flex-1 p-6">
          {renderCurrentView()}
        </main>
      </div>
    </div>
  );
};

export default App;
