import React, { useState } from 'react';
import { 
  Folder, 
  FolderOpen, 
  MoreVertical, 
  Edit, 
  Trash2, 
  Share2,
  Calendar,
  FileText
} from 'lucide-react';

const FolderCard = ({ 
  folder, 
  onClick, 
  onEdit, 
  onDelete, 
  onShare,
  viewMode = 'grid' 
}) => {
  const [showActions, setShowActions] = useState(false);
  const [isHovered, setIsHovered] = useState(false);

  const formatDate = (dateString) => {
    if (!dateString) return '';
    
    try {
      const date = new Date(dateString);
      const now = new Date();
      const diffTime = Math.abs(now - date);
      const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));
      
      if (diffDays === 1) return 'Yesterday';
      if (diffDays < 7) return `${diffDays} days ago`;
      if (diffDays < 30) return `${Math.ceil(diffDays / 7)} weeks ago`;
      
      return date.toLocaleDateString();
    } catch {
      return dateString;
    }
  };

  const getFolderColor = () => {
    return folder.color || '#3B38A0';
  };

  if (viewMode === 'list') {
    return (
      <div 
        className="group bg-white rounded-lg border border-gray-200 hover:border-[#7A85C1] hover:shadow-md transition-all duration-200 cursor-pointer"
        onClick={() => onClick && onClick(folder)}
        onMouseEnter={() => setIsHovered(true)}
        onMouseLeave={() => setIsHovered(false)}
      >
        <div className="flex items-center justify-between p-4">
          <div className="flex items-center space-x-4 flex-1 min-w-0">
            <div className="flex-shrink-0">
              {isHovered ? (
                <FolderOpen 
                  className="w-6 h-6 transition-all duration-200" 
                  style={{ color: getFolderColor() }}
                />
              ) : (
                <Folder 
                  className="w-6 h-6 transition-all duration-200" 
                  style={{ color: getFolderColor() }}
                />
              )}
            </div>
            
            <div className="flex-1 min-w-0">
              <h3 className="font-medium text-gray-900 truncate mb-1">{folder.name}</h3>
              <div className="flex items-center space-x-4 text-sm text-gray-500">
                <div className="flex items-center space-x-1">
                  <FileText className="w-4 h-4" />
                  <span>{folder.documentCount || 0} documents</span>
                </div>
                {folder.modified && (
                  <div className="flex items-center space-x-1">
                    <Calendar className="w-4 h-4" />
                    <span>{formatDate(folder.modified)}</span>
                  </div>
                )}
              </div>
            </div>
          </div>

          {(onEdit || onDelete || onShare) && (
            <div className="flex items-center space-x-1 opacity-0 group-hover:opacity-100 transition-opacity duration-200">
              {onEdit && (
                <button
                  onClick={(e) => {
                    e.stopPropagation();
                    onEdit(folder);
                  }}
                  className="p-2 text-gray-400 hover:text-[#3B38A0] hover:bg-[#B2B0E8]/20 rounded-lg transition-colors"
                  title="Edit"
                >
                  <Edit className="w-4 h-4" />
                </button>
              )}
              
              {onShare && (
                <button
                  onClick={(e) => {
                    e.stopPropagation();
                    onShare(folder);
                  }}
                  className="p-2 text-gray-400 hover:text-[#3B38A0] hover:bg-[#B2B0E8]/20 rounded-lg transition-colors"
                  title="Share"
                >
                  <Share2 className="w-4 h-4" />
                </button>
              )}
              
              {onDelete && (
                <button
                  onClick={(e) => {
                    e.stopPropagation();
                    onDelete(folder);
                  }}
                  className="p-2 text-gray-400 hover:text-red-500 hover:bg-red-50 rounded-lg transition-colors"
                  title="Delete"
                >
                  <Trash2 className="w-4 h-4" />
                </button>
              )}
            </div>
          )}
        </div>
      </div>
    );
  }

  // Grid view
  return (
    <div 
      className="group relative bg-white rounded-xl border border-gray-200 hover:border-[#7A85C1] hover:shadow-lg transition-all duration-300 cursor-pointer"
      onClick={() => onClick && onClick(folder)}
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
    >
      <div className="p-6">
        {/* Header with icon and actions */}
        <div className="flex items-center justify-between mb-4">
          <div 
            className="p-3 rounded-xl transition-all duration-200"
            style={{ backgroundColor: `${getFolderColor()}20` }}
          >
            {isHovered ? (
              <FolderOpen 
                className="w-8 h-8 transition-all duration-200" 
                style={{ color: getFolderColor() }}
              />
            ) : (
              <Folder 
                className="w-8 h-8 transition-all duration-200" 
                style={{ color: getFolderColor() }}
              />
            )}
          </div>
          
          <div className="flex items-center space-x-1">
            <span className="text-2xl text-gray-300 group-hover:text-[#7A85C1] transition-colors duration-200">
              â†’
            </span>
            
            {(onEdit || onDelete || onShare) && (
              <div className="relative">
                <button
                  onClick={(e) => {
                    e.stopPropagation();
                    setShowActions(!showActions);
                  }}
                  className="p-1 text-gray-400 hover:text-gray-600 opacity-0 group-hover:opacity-100 transition-all duration-200"
                >
                  <MoreVertical className="w-4 h-4" />
                </button>

                {/* Actions dropdown */}
                {showActions && (
                  <div className="absolute right-0 mt-1 w-32 bg-white rounded-lg shadow-lg border border-gray-200 z-10">
                    <div className="p-1">
                      {onEdit && (
                        <button
                          onClick={(e) => {
                            e.stopPropagation();
                            onEdit(folder);
                            setShowActions(false);
                          }}
                          className="flex items-center space-x-2 w-full p-2 text-sm text-gray-700 hover:bg-gray-100 rounded-md"
                        >
                          <Edit className="w-4 h-4" />
                          <span>Edit</span>
                        </button>
                      )}
                      
                      {onShare && (
                        <button
                          onClick={(e) => {
                            e.stopPropagation();
                            onShare(folder);
                            setShowActions(false);
                          }}
                          className="flex items-center space-x-2 w-full p-2 text-sm text-gray-700 hover:bg-gray-100 rounded-md"
                        >
                          <Share2 className="w-4 h-4" />
                          <span>Share</span>
                        </button>
                      )}
                      
                      {onDelete && (
                        <>
                          <div className="border-t border-gray-100 my-1"></div>
                          <button
                            onClick={(e) => {
                              e.stopPropagation();
                              onDelete(folder);
                              setShowActions(false);
                            }}
                            className="flex items-center space-x-2 w-full p-2 text-sm text-red-600 hover:bg-red-50 rounded-md"
                          >
                            <Trash2 className="w-4 h-4" />
                            <span>Delete</span>
                          </button>
                        </>
                      )}
                    </div>
                  </div>
                )}
              </div>
            )}
          </div>
        </div>

        {/* Folder name */}
        <h3 className="font-semibold text-gray-900 mb-2 truncate">
          {folder.name}
        </h3>

        {/* Folder info */}
        <div className="space-y-2">
          <p className="text-sm text-gray-600">
            {folder.documentCount || 0} documents
          </p>
          
          {folder.modified && (
            <p className="text-xs text-gray-400">
              Modified {formatDate(folder.modified)}
            </p>
          )}
        </div>
      </div>

      {/* Click outside handler for actions dropdown */}
      {showActions && (
        <div
          className="fixed inset-0 z-5"
          onClick={(e) => {
            e.stopPropagation();
            setShowActions(false);
          }}
        />
      )}
    </div>
  );
};

export default FolderCard;
