import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate, useNavigate } from 'react-router-dom';

// Authentication Components
import LoginPage from './components/auth/LoginPage';
import Register from './components/auth/Register';
import ProtectedRoute from './components/auth/ProtectedRoute';

// User Components
import Header from './components/layout/Header';
import Sidebar from './components/layout/Sidebar';
import Footer from './components/layout/Footer';
import Dashboard from './components/user/Dashboard';
import MyDrive from './components/user/MyDrive';
import SharedWithMe from './components/layout/SharedWithMe';

// Admin Components
import AdminHeader from './components/admin/AdminHeader';
import AdminSidebar from './components/admin/AdminSidebar';
import AdminDashboard from './components/admin/AdminDashboard';

// Styles
import './App.css';

// User Dashboard Layout Component
const UserDashboardLayout = () => {
  const navigate = useNavigate();
  const [activeSection, setActiveSection] = useState('dashboard');

  const handleLogout = () => {
    // Clear authentication data
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    
    // Navigate to login page
    navigate('/login');
  };

  const renderContent = () => {
    switch (activeSection) {
      case 'dashboard':
        return <Dashboard />;
      case 'mydrive':
        return <MyDrive />;
      case 'shared':
        return <SharedWithMe />;
      default:
        return <Dashboard />;
    }
  };

  return (
    <div className="min-h-screen bg-gray-50 flex">
      <Sidebar 
        activeSection={activeSection} 
        setActiveSection={setActiveSection}
        onLogout={handleLogout}
      />
      <div className="flex-1 flex flex-col">
        <Header />
        <main className="flex-1 overflow-y-auto">
          {renderContent()}
        </main>
        <Footer />
      </div>
    </div>
  );
};

// Admin Dashboard Layout Component
const AdminDashboardLayout = () => {
  const navigate = useNavigate();
  const [activeSection, setActiveSection] = useState('admin-overview');

  const handleLogout = () => {
    // Clear authentication data
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    
    // Navigate to login page
    navigate('/login');
  };

  const renderContent = () => {
    switch (activeSection) {
      case 'admin-overview':
        return <AdminDashboard activeTab="overview" />;
      case 'admin-users':
        return <AdminDashboard activeTab="users" />;
      case 'admin-documents':
        return <AdminDashboard activeTab="documents" />;
      case 'admin-logs':
        return <AdminDashboard activeTab="logs" />;
      default:
        return <AdminDashboard activeTab="overview" />;
    }
  };

  return (
    <div className="min-h-screen bg-gray-50 flex">
      <AdminSidebar 
        activeSection={activeSection} 
        setActiveSection={setActiveSection}
        onLogout={handleLogout}
      />
      <div className="flex-1 flex flex-col">
        <AdminHeader />
        <main className="flex-1 overflow-y-auto">
          {renderContent()}
        </main>
        <Footer />
      </div>
    </div>
  );
};

// Authentication Guard Component
const AuthGuard = ({ children }) => {
  const [isLoading, setIsLoading] = useState(true);
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  useEffect(() => {
    // Check if user is authenticated
    const token = localStorage.getItem('token');
    const user = localStorage.getItem('user');
    
    if (token && user) {
      try {
        // Validate token format and user data
        const userData = JSON.parse(user);
        if (userData && userData.id) {
          setIsAuthenticated(true);
        }
      } catch (error) {
        console.error('Invalid user data in localStorage:', error);
        // Clear corrupted data
        localStorage.removeItem('token');
        localStorage.removeItem('user');
      }
    }
    
    setIsLoading(false);
  }, []);

  if (isLoading) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-[#1A2A80] via-[#3B38A0] to-[#7A85C1] flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-16 w-16 border-b-2 border-white mx-auto mb-4"></div>
          <p className="text-white text-lg font-medium">Loading...</p>
        </div>
      </div>
    );
  }

  return children;
};

// Main App Component
function App() {
  const [appError, setAppError] = useState(null);

  // Global error boundary
  useEffect(() => {
    const handleError = (event) => {
      console.error('Application Error:', event.error);
      setAppError('An unexpected error occurred. Please refresh the page.');
    };

    window.addEventListener('error', handleError);
    
    return () => {
      window.removeEventListener('error', handleError);
    };
  }, []);

  // Error boundary UI
  if (appError) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-[#1A2A80] via-[#3B38A0] to-[#7A85C1] flex items-center justify-center">
        <div className="max-w-md w-full bg-white rounded-2xl shadow-2xl p-8 text-center">
          <div className="w-16 h-16 mx-auto mb-4 rounded-full bg-red-100 flex items-center justify-center">
            <svg className="w-8 h-8 text-red-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M12 9v2m0 4h.01m-6.938 4h13.856c1.54 0 2.502-1.667 1.732-2.5L13.732 4c-.77-.833-1.964-.833-2.732 0L3.732 16.5c-.77.833.192 2.5 1.732 2.5z"></path>
            </svg>
          </div>
          <h1 className="text-xl font-bold text-gray-800 mb-2">Application Error</h1>
          <p className="text-gray-600 mb-4">{appError}</p>
          <button 
            onClick={() => window.location.reload()} 
            className="px-6 py-3 bg-[#3B38A0] text-white rounded-lg hover:bg-[#1A2A80] transition-colors"
          >
            Refresh Page
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="App">
      <Router>
        <AuthGuard>
          <Routes>
            {/* Public Routes */}
            <Route path="/login" element={<LoginPage />} />
            <Route path="/register" element={<Register />} />
            
            {/* Protected User Routes */}
            <Route 
              path="/user-dashboard" 
              element={
                <ProtectedRoute requiredRole="USER">
                  <UserDashboardLayout />
                </ProtectedRoute>
              } 
            />
            
            {/* Protected Admin Routes */}
            <Route 
              path="/admin-dashboard" 
              element={
                <ProtectedRoute requiredRole="ADMIN">
                  <AdminDashboardLayout />
                </ProtectedRoute>
              } 
            />
            
            {/* Default Routes */}
            <Route 
              path="/" 
              element={
                <Navigate 
                  to={
                    (() => {
                      const user = localStorage.getItem('user');
                      if (user) {
                        try {
                          const userData = JSON.parse(user);
                          return userData.role === 'ADMIN' ? '/admin-dashboard' : '/user-dashboard';
                        } catch {
                          return '/login';
                        }
                      }
                      return '/login';
                    })()
                  } 
                  replace 
                />
              } 
            />
            
            {/* Catch all route */}
            <Route 
              path="*" 
              element={
                <div className="min-h-screen bg-gradient-to-br from-[#1A2A80] via-[#3B38A0] to-[#7A85C1] flex items-center justify-center">
                  <div className="max-w-md w-full bg-white rounded-2xl shadow-2xl p-8 text-center">
                    <div className="w-16 h-16 mx-auto mb-4 rounded-full bg-gray-100 flex items-center justify-center">
                      <svg className="w-8 h-8 text-gray-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M9.172 16.172a4 4 0 015.656 0M9 12h6m-6 4h6m2 5H7a2 2 0 01-2-2V5a2 2 0 012-2h5.586a1 1 0 01.707.293l5.414 5.414a1 1 0 01.293.707V19a2 2 0 01-2 2z"></path>
                      </svg>
                    </div>
                    <h1 className="text-xl font-bold text-gray-800 mb-2">Page Not Found</h1>
                    <p className="text-gray-600 mb-4">The page you're looking for doesn't exist.</p>
                    <button 
                      onClick={() => window.history.back()} 
                      className="px-6 py-3 bg-[#3B38A0] text-white rounded-lg hover:bg-[#1A2A80] transition-colors mr-2"
                    >
                      Go Back
                    </button>
                    <button 
                      onClick={() => window.location.href = '/'} 
                      className="px-6 py-3 border border-[#3B38A0] text-[#3B38A0] rounded-lg hover:bg-[#3B38A0] hover:text-white transition-colors"
                    >
                      Go Home
                    </button>
                  </div>
                </div>
              } 
            />
          </Routes>
        </AuthGuard>
      </Router>
    </div>
  );
}

export default App;
