"use client"

import { useState } from "react"
import "../styles/DocumentForm.css"

const DocumentForm = () => {
  const [formData, setFormData] = useState({
    title: "",
    description: "",
    category: "",
    tags: "",
    author: "",
    fileType: "",
    accessPermission: "private",
    file: null,
  })

  const [errors, setErrors] = useState({})
  const [dragActive, setDragActive] = useState(false)

  const handleInputChange = (e) => {
    const { name, value } = e.target
    setFormData((prev) => ({
      ...prev,
      [name]: value,
    }))

    // Clear error when user starts typing
    if (errors[name]) {
      setErrors((prev) => ({
        ...prev,
        [name]: "",
      }))
    }
  }

  const handleFileChange = (e) => {
    const file = e.target.files[0]
    if (file) {
      setFormData((prev) => ({
        ...prev,
        file: file,
        fileType: file.type,
      }))
    }
  }

  const handleDrag = (e) => {
    e.preventDefault()
    e.stopPropagation()
    if (e.type === "dragenter" || e.type === "dragover") {
      setDragActive(true)
    } else if (e.type === "dragleave") {
      setDragActive(false)
    }
  }

  const handleDrop = (e) => {
    e.preventDefault()
    e.stopPropagation()
    setDragActive(false)

    if (e.dataTransfer.files && e.dataTransfer.files[0]) {
      const file = e.dataTransfer.files[0]
      setFormData((prev) => ({
        ...prev,
        file: file,
        fileType: file.type,
      }))
    }
  }

  const validateForm = () => {
    const newErrors = {}

    if (!formData.title.trim()) newErrors.title = "Document title is required"
    if (!formData.description.trim()) newErrors.description = "Description is required"
    if (!formData.category) newErrors.category = "Category is required"
    if (!formData.author.trim()) newErrors.author = "Author name is required"
    if (!formData.file) newErrors.file = "Please select a file to upload"

    setErrors(newErrors)
    return Object.keys(newErrors).length === 0
  }

  const handleSubmit = (e) => {
    e.preventDefault()

    if (validateForm()) {
      console.log("Form submitted:", formData)
      // Handle form submission here
      alert("Document submitted successfully!")
    }
  }

  return (
    <div className="document-form-container">
      <div className="form-header">
        <h1>Upload Document</h1>
        <p>Fill in the document details and upload your file</p>
      </div>

      <form onSubmit={handleSubmit} className="document-form">
        <div className="form-section">
          <h2>File Upload</h2>
          <div
            className={`file-upload-area ${dragActive ? "drag-active" : ""} ${formData.file ? "has-file" : ""}`}
            onDragEnter={handleDrag}
            onDragLeave={handleDrag}
            onDragOver={handleDrag}
            onDrop={handleDrop}
          >
            <input
              type="file"
              id="file-upload"
              onChange={handleFileChange}
              className="file-input"
              accept=".pdf,.doc,.docx,.txt,.jpg,.jpeg,.png"
            />
            <label htmlFor="file-upload" className="file-upload-label">
              <div className="upload-icon">📄</div>
              {formData.file ? (
                <div className="file-info">
                  <p className="file-name">{formData.file.name}</p>
                  <p className="file-size">{(formData.file.size / 1024 / 1024).toFixed(2)} MB</p>
                </div>
              ) : (
                <div className="upload-text">
                  <p>
                    Drag and drop your file here, or <span>browse</span>
                  </p>
                  <p className="upload-hint">Supports PDF, DOC, DOCX, TXT, JPG, PNG (Max 10MB)</p>
                </div>
              )}
            </label>
          </div>
          {errors.file && <span className="error-message">{errors.file}</span>}
        </div>

        {/* Document Details Section */}
        <div className="form-section">
          <h2>Document Details</h2>
          <div className="form-grid">
            <div className="form-group">
              <label htmlFor="title">Document Title *</label>
              <input
                type="text"
                id="title"
                name="title"
                value={formData.title}
                onChange={handleInputChange}
                className={errors.title ? "error" : ""}
                placeholder="Enter document title"
              />
              {errors.title && <span className="error-message">{errors.title}</span>}
            </div>

            <div className="form-group">
              <label htmlFor="author">Author *</label>
              <input
                type="text"
                id="author"
                name="author"
                value={formData.author}
                onChange={handleInputChange}
                className={errors.author ? "error" : ""}
                placeholder="Enter author name"
              />
              {errors.author && <span className="error-message">{errors.author}</span>}
            </div>

            <div className="form-group full-width">
              <label htmlFor="description">Description *</label>
              <textarea
                id="description"
                name="description"
                value={formData.description}
                onChange={handleInputChange}
                className={errors.description ? "error" : ""}
                placeholder="Enter document description"
                rows="4"
              />
              {errors.description && <span className="error-message">{errors.description}</span>}
            </div>

            <div className="form-group">
              <label htmlFor="category">Category *</label>
              <select
                id="category"
                name="category"
                value={formData.category}
                onChange={handleInputChange}
                className={errors.category ? "error" : ""}
              >
                <option value="">Select category</option>
                <option value="report">Report</option>
                <option value="presentation">Presentation</option>
                <option value="spreadsheet">Spreadsheet</option>
                <option value="image">Image</option>
                <option value="contract">Contract</option>
                <option value="other">Other</option>
              </select>
              {errors.category && <span className="error-message">{errors.category}</span>}
            </div>

            <div className="form-group">
              <label htmlFor="tags">Tags</label>
              <input
                type="text"
                id="tags"
                name="tags"
                value={formData.tags}
                onChange={handleInputChange}
                placeholder="Enter tags separated by commas"
              />
            </div>
          </div>
        </div>

        {/* Access Permissions Section */}
        <div className="form-section">
          <h2>Access Permissions</h2>
          <div className="radio-group">
            <div className="radio-option">
              <input
                type="radio"
                id="private"
                name="accessPermission"
                value="private"
                checked={formData.accessPermission === "private"}
                onChange={handleInputChange}
              />
              <label htmlFor="private">
                <span className="radio-title">Private</span>
                <span className="radio-description">Only you can access this document</span>
              </label>
            </div>
            <div className="radio-option">
              <input
                type="radio"
                id="shared"
                name="accessPermission"
                value="shared"
                checked={formData.accessPermission === "shared"}
                onChange={handleInputChange}
              />
              <label htmlFor="shared">
                <span className="radio-title">Shared</span>
                <span className="radio-description">Share with specific people</span>
              </label>
            </div>
            <div className="radio-option">
              <input
                type="radio"
                id="public"
                name="accessPermission"
                value="public"
                checked={formData.accessPermission === "public"}
                onChange={handleInputChange}
              />
              <label htmlFor="public">
                <span className="radio-title">Public</span>
                <span className="radio-description">Anyone with the link can view</span>
              </label>
            </div>
          </div>
        </div>

        {/* Submit Button */}
        <div className="form-actions">
          <button type="button" className="btn-secondary">
            Cancel
          </button>
          <button type="submit" className="btn-primary">
            Submit Document
          </button>
        </div>
      </form>
    </div>
  )
}

export default DocumentForm
