// LoadingSpinner.js
import React from 'react';
import { FileText } from 'lucide-react';

export const LoadingSpinner = ({ size = 'large', message = 'Loading...' }) => {
  const spinnerSize = size === 'small' ? 'w-6 h-6' : size === 'medium' ? 'w-8 h-8' : 'w-12 h-12';
  
  return (
    <div className="flex items-center justify-center min-h-screen bg-gray-50">
      <div className="text-center">
        <div className="relative">
          <div className={`${spinnerSize} border-4 border-gray-200 border-t-[#3B38A0] rounded-full animate-spin mx-auto mb-4`}></div>
          <FileText className="absolute inset-0 w-6 h-6 text-[#3B38A0] m-auto" />
        </div>
        <p className="text-gray-600">{message}</p>
      </div>
    </div>
  );
};
