import React from 'react';
import '../../styles/components/FolderCard.css';

const FolderCard = ({ folder, onOpen, onShare, onDelete, showActions = true }) => {
  const handleClick = () => {
    onOpen(folder);
  };

  return (
    <div className="folder-card" onClick={handleClick}>
      <div className="folder-icon">
        <svg width="48" height="48" viewBox="0 0 24 24" fill="none">
          <path 
            d="M10 4H4c-1.11 0-2 .89-2 2v12c0 1.11.89 2 2 2h16c1.11 0 2-.89 2-2V8c0-1.11-.89-2-2-2h-8l-2-2z" 
            fill="#4285f4"
          />
        </svg>
      </div>
      <div className="folder-info">
        <h3 className="folder-name">{folder.name}</h3>
        <p className="folder-date">Created: {new Date(folder.createdAt).toLocaleDateString()}</p>
      </div>
      {showActions && (
        <div className="folder-actions" onClick={(e) => e.stopPropagation()}>
          <button onClick={() => onShare(folder)} className="action-btn share-btn">
            Share
          </button>
          <button onClick={() => onDelete(folder.id)} className="action-btn delete-btn">
            Delete
          </button>
        </div>
      )}
    </div>
  );
};

export default FolderCard;
