import React, { useState } from 'react';
import { 
  FileText, 
  Eye, 
  Edit, 
  Trash2, 
  Share2, 
  Download,
  MoreVertical,
  Calendar,
  FolderOpen,
  Star
} from 'lucide-react';

const DocumentCard = ({ 
  document, 
  viewMode = 'grid', 
  onView, 
  onEdit, 
  onShare, 
  onDelete, 
  onDownload,
  onStar 
}) => {
  const [showActions, setShowActions] = useState(false);
  const [isStarred, setIsStarred] = useState(document.starred || false);

  const getFileIcon = (type) => {
    const iconClass = viewMode === 'grid' ? "w-8 h-8" : "w-6 h-6";
    
    switch (type?.toLowerCase()) {
      case 'pdf':
        return <FileText className={`${iconClass} text-red-500`} />;
      case 'doc':
      case 'docx':
        return <FileText className={`${iconClass} text-blue-500`} />;
      case 'xls':
      case 'xlsx':
        return <FileText className={`${iconClass} text-green-500`} />;
      case 'ppt':
      case 'pptx':
        return <FileText className={`${iconClass} text-orange-500`} />;
      case 'jpg':
      case 'jpeg':
      case 'png':
      case 'gif':
        return <FileText className={`${iconClass} text-purple-500`} />;
      case 'txt':
        return <FileText className={`${iconClass} text-gray-500`} />;
      default:
        return <FileText className={`${iconClass} text-gray-500`} />;
    }
  };

  const getFileTypeColor = (type) => {
    switch (type?.toLowerCase()) {
      case 'pdf': return 'bg-red-100 text-red-700';
      case 'doc':
      case 'docx': return 'bg-blue-100 text-blue-700';
      case 'xls':
      case 'xlsx': return 'bg-green-100 text-green-700';
      case 'ppt':
      case 'pptx': return 'bg-orange-100 text-orange-700';
      case 'jpg':
      case 'jpeg':
      case 'png':
      case 'gif': return 'bg-purple-100 text-purple-700';
      default: return 'bg-gray-100 text-gray-700';
    }
  };

  const formatFileSize = (size) => {
    if (!size) return '0 B';
    if (typeof size === 'string') return size;
    
    const units = ['B', 'KB', 'MB', 'GB'];
    let bytes = size;
    let unitIndex = 0;
    
    while (bytes >= 1024 && unitIndex < units.length - 1) {
      bytes /= 1024;
      unitIndex++;
    }
    
    return `${bytes.toFixed(1)} ${units[unitIndex]}`;
  };

  const formatDate = (dateString) => {
    if (!dateString) return '';
    
    try {
      const date = new Date(dateString);
      const now = new Date();
      const diffTime = Math.abs(now - date);
      const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));
      
      if (diffDays === 1) return 'Yesterday';
      if (diffDays < 7) return `${diffDays} days ago`;
      if (diffDays < 30) return `${Math.ceil(diffDays / 7)} weeks ago`;
      
      return date.toLocaleDateString();
    } catch {
      return dateString;
    }
  };

  const handleStarClick = (e) => {
    e.stopPropagation();
    setIsStarred(!isStarred);
    if (onStar) {
      onStar(document, !isStarred);
    }
  };

  if (viewMode === 'list') {
    return (
      <div className="group bg-white rounded-lg border border-gray-200 hover:border-[#7A85C1] hover:shadow-md transition-all duration-200">
        <div className="flex items-center justify-between p-4">
          <div className="flex items-center space-x-4 flex-1 min-w-0">
            <div className="flex-shrink-0">
              {getFileIcon(document.type)}
            </div>
            
            <div className="flex-1 min-w-0">
              <div className="flex items-center space-x-2 mb-1">
                <h3 className="font-medium text-gray-900 truncate">{document.name}</h3>
                <button
                  onClick={handleStarClick}
                  className={`flex-shrink-0 p-1 rounded ${
                    isStarred 
                      ? 'text-yellow-500 hover:text-yellow-600' 
                      : 'text-gray-300 hover:text-yellow-500'
                  }`}
                >
                  <Star className={`w-4 h-4 ${isStarred ? 'fill-current' : ''}`} />
                </button>
              </div>
              
              <div className="flex items-center space-x-4 text-sm text-gray-500">
                {document.folder && (
                  <div className="flex items-center space-x-1">
                    <FolderOpen className="w-4 h-4" />
                    <span>{document.folder}</span>
                  </div>
                )}
                <span>{formatFileSize(document.size)}</span>
                <div className="flex items-center space-x-1">
                  <Calendar className="w-4 h-4" />
                  <span>{formatDate(document.modified || document.updatedAt)}</span>
                </div>
              </div>
            </div>
          </div>

          <div className="flex items-center space-x-1 opacity-0 group-hover:opacity-100 transition-opacity duration-200">
            <button
              onClick={(e) => {
                e.stopPropagation();
                onView(document);
              }}
              className="p-2 text-gray-400 hover:text-[#3B38A0] hover:bg-[#B2B0E8]/20 rounded-lg transition-colors"
              title="View"
            >
              <Eye className="w-4 h-4" />
            </button>
            
            <button
              onClick={(e) => {
                e.stopPropagation();
                onDownload(document);
              }}
              className="p-2 text-gray-400 hover:text-[#3B38A0] hover:bg-[#B2B0E8]/20 rounded-lg transition-colors"
              title="Download"
            >
              <Download className="w-4 h-4" />
            </button>
            
            <button
              onClick={(e) => {
                e.stopPropagation();
                onEdit(document);
              }}
              className="p-2 text-gray-400 hover:text-[#3B38A0] hover:bg-[#B2B0E8]/20 rounded-lg transition-colors"
              title="Edit"
            >
              <Edit className="w-4 h-4" />
            </button>
            
            <button
              onClick={(e) => {
                e.stopPropagation();
                onShare(document);
              }}
              className="p-2 text-gray-400 hover:text-[#3B38A0] hover:bg-[#B2B0E8]/20 rounded-lg transition-colors"
              title="Share"
            >
              <Share2 className="w-4 h-4" />
            </button>
            
            <button
              onClick={(e) => {
                e.stopPropagation();
                onDelete(document);
              }}
              className="p-2 text-gray-400 hover:text-red-500 hover:bg-red-50 rounded-lg transition-colors"
              title="Delete"
            >
              <Trash2 className="w-4 h-4" />
            </button>
          </div>
        </div>
      </div>
    );
  }

  // Grid view
  return (
    <div className="group relative bg-white rounded-xl border border-gray-200 hover:border-[#7A85C1] hover:shadow-lg transition-all duration-300 cursor-pointer">
      <div className="p-6" onClick={() => onView(document)}>
        {/* Header with icon and actions */}
        <div className="flex items-start justify-between mb-4">
          <div className="flex-shrink-0">
            {getFileIcon(document.type)}
          </div>
          
          <div className="flex items-center space-x-1">
            <button
              onClick={handleStarClick}
              className={`p-1 rounded ${
                isStarred 
                  ? 'text-yellow-500 hover:text-yellow-600' 
                  : 'text-gray-300 hover:text-yellow-500 opacity-0 group-hover:opacity-100'
              } transition-all duration-200`}
            >
              <Star className={`w-4 h-4 ${isStarred ? 'fill-current' : ''}`} />
            </button>
            
            <div className="relative">
              <button
                onClick={(e) => {
                  e.stopPropagation();
                  setShowActions(!showActions);
                }}
                className="p-1 text-gray-400 hover:text-gray-600 opacity-0 group-hover:opacity-100 transition-all duration-200"
              >
                <MoreVertical className="w-4 h-4" />
              </button>

              {/* Actions dropdown */}
              {showActions && (
                <div className="absolute right-0 mt-1 w-32 bg-white rounded-lg shadow-lg border border-gray-200 z-10">
                  <div className="p-1">
                    <button
                      onClick={(e) => {
                        e.stopPropagation();
                        onView(document);
                        setShowActions(false);
                      }}
                      className="flex items-center space-x-2 w-full p-2 text-sm text-gray-700 hover:bg-gray-100 rounded-md"
                    >
                      <Eye className="w-4 h-4" />
                      <span>View</span>
                    </button>
                    
                    <button
                      onClick={(e) => {
                        e.stopPropagation();
                        onDownload(document);
                        setShowActions(false);
                      }}
                      className="flex items-center space-x-2 w-full p-2 text-sm text-gray-700 hover:bg-gray-100 rounded-md"
                    >
                      <Download className="w-4 h-4" />
                      <span>Download</span>
                    </button>
                    
                    <button
                      onClick={(e) => {
                        e.stopPropagation();
                        onEdit(document);
                        setShowActions(false);
                      }}
                      className="flex items-center space-x-2 w-full p-2 text-sm text-gray-700 hover:bg-gray-100 rounded-md"
                    >
                      <Edit className="w-4 h-4" />
                      <span>Edit</span>
                    </button>
                    
                    <button
                      onClick={(e) => {
                        e.stopPropagation();
                        onShare(document);
                        setShowActions(false);
                      }}
                      className="flex items-center space-x-2 w-full p-2 text-sm text-gray-700 hover:bg-gray-100 rounded-md"
                    >
                      <Share2 className="w-4 h-4" />
                      <span>Share</span>
                    </button>
                    
                    <div className="border-t border-gray-100 my-1"></div>
                    
                    <button
                      onClick={(e) => {
                        e.stopPropagation();
                        onDelete(document);
                        setShowActions(false);
                      }}
                      className="flex items-center space-x-2 w-full p-2 text-sm text-red-600 hover:bg-red-50 rounded-md"
                    >
                      <Trash2 className="w-4 h-4" />
                      <span>Delete</span>
                    </button>
                  </div>
                </div>
              )}
            </div>
          </div>
        </div>

        {/* Document name */}
        <h3 className="font-semibold text-gray-900 mb-3 truncate text-sm">
          {document.name}
        </h3>

        {/* Document info */}
        <div className="space-y-2">
          <div className="flex items-center justify-between">
            {document.folder && (
              <span className="inline-flex items-center px-2 py-1 rounded-full text-xs font-medium bg-[#B2B0E8]/30 text-[#1A2A80]">
                {document.folder}
              </span>
            )}
            
            <span className={`px-2 py-1 rounded text-xs font-medium ${getFileTypeColor(document.type)}`}>
              {document.type?.toUpperCase() || 'FILE'}
            </span>
          </div>
          
          <div className="flex items-center justify-between text-xs text-gray-500">
            <span>{formatFileSize(document.size)}</span>
            <span>{formatDate(document.modified || document.updatedAt)}</span>
          </div>
        </div>
      </div>

      {/* Click outside handler for actions dropdown */}
      {showActions && (
        <div
          className="fixed inset-0 z-5"
          onClick={(e) => {
            e.stopPropagation();
            setShowActions(false);
          }}
        />
      )}
    </div>
  );
};

export default DocumentCard;
