import React, { useState } from 'react';
import { Eye, EyeOff, Mail, Lock, User, Loader2, FileText, Shield, Users, Zap } from 'lucide-react';

const Login = () => {
  const [isLogin, setIsLogin] = useState(true);
  const [showPassword, setShowPassword] = useState(false);
  const [formData, setFormData] = useState({
    username: '',
    password: '',
    email: ''
  });
  const [errors, setErrors] = useState({});
  const [loading, setLoading] = useState(false);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
    if (errors[name]) {
      setErrors(prev => ({
        ...prev,
        [name]: ''
      }));
    }
  };

  const validateForm = () => {
    const newErrors = {};

    if (!formData.username.trim()) {
      newErrors.username = 'Username is required';
    } else if (formData.username.length < 3) {
      newErrors.username = 'Username must be at least 3 characters';
    }

    if (!formData.password) {
      newErrors.password = 'Password is required';
    } else if (formData.password.length < 6) {
      newErrors.password = 'Password must be at least 6 characters';
    }

    if (!isLogin) {
      if (!formData.email.trim()) {
        newErrors.email = 'Email is required';
      } else if (!/\S+@\S+\.\S+/.test(formData.email)) {
        newErrors.email = 'Please enter a valid email';
      }
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!validateForm()) return;

    setLoading(true);
    
    // Simulate API call
    setTimeout(() => {
      setLoading(false);
      if (isLogin) {
        console.log('Login successful');
      } else {
        setIsLogin(true);
        setFormData({ username: '', password: '', email: '' });
        console.log('Registration successful');
      }
    }, 1500);
  };

  const switchMode = () => {
    setIsLogin(!isLogin);
    setFormData({ username: '', password: '', email: '' });
    setErrors({});
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-50 to-gray-100 flex">
      {/* Left Side - Branding */}
      <div className="hidden lg:flex lg:w-3/5 relative overflow-hidden">
        {/* Background with custom color palette */}
        <div 
          className="absolute inset-0"
          style={{
            background: `linear-gradient(135deg, #1A2A80 0%, #3B38A0 50%, #7A85C1 100%)`
          }}
        />
        
        {/* Decorative Elements */}
        <div className="absolute inset-0 overflow-hidden">
          {/* Floating geometric shapes */}
          <div className="absolute top-20 left-20 w-32 h-32 bg-white bg-opacity-10 rounded-full blur-xl animate-pulse" />
          <div className="absolute top-1/3 right-20 w-24 h-24 bg-white bg-opacity-5 rounded-full blur-lg" />
          <div className="absolute bottom-1/4 left-1/3 w-40 h-40 bg-white bg-opacity-5 rounded-full blur-2xl" />
          
          {/* Grid pattern overlay */}
          <svg className="absolute inset-0 w-full h-full opacity-10" xmlns="http://www.w3.org/2000/svg">
            <defs>
              <pattern id="grid" width="60" height="60" patternUnits="userSpaceOnUse">
                <path d="M 60 0 L 0 0 0 60" fill="none" stroke="white" strokeWidth="1"/>
              </pattern>
            </defs>
            <rect width="100%" height="100%" fill="url(#grid)" />
          </svg>
        </div>
        
        {/* Content */}
        <div className="relative z-10 flex flex-col justify-center px-16 py-20 text-white">
          {/* Logo and Brand */}
          <div className="mb-12">
            <div className="flex items-center space-x-4 mb-8">
              <div 
                className="w-16 h-16 rounded-2xl flex items-center justify-center shadow-lg"
                style={{ backgroundColor: 'rgba(255, 255, 255, 0.15)' }}
              >
                <FileText className="w-8 h-8 text-white" />
              </div>
              <div>
                <h1 className="text-4xl font-bold tracking-tight">DocShare</h1>
                <p className="text-white/80 text-sm font-medium">Professional Edition</p>
              </div>
            </div>
            
            <div className="space-y-6">
              <h2 className="text-5xl font-bold leading-tight">
                Secure Document
                <br />
                <span style={{ color: '#B2B0E8' }}>Collaboration</span>
                <br />
                Platform
              </h2>
              
              <p className="text-xl text-white/90 leading-relaxed max-w-lg">
                Streamline your workflow with enterprise-grade security, 
                real-time collaboration, and intelligent document management.
              </p>
            </div>
          </div>
          
          {/* Features */}
          <div className="space-y-6">
            <div className="grid grid-cols-2 gap-6">
              <div className="flex items-start space-x-4">
                <div 
                  className="w-12 h-12 rounded-xl flex items-center justify-center flex-shrink-0"
                  style={{ backgroundColor: 'rgba(178, 176, 232, 0.2)' }}
                >
                  <Shield className="w-6 h-6" style={{ color: '#B2B0E8' }} />
                </div>
                <div>
                  <h3 className="font-semibold text-white mb-1">Enterprise Security</h3>
                  <p className="text-white/75 text-sm">End-to-end encryption with advanced access controls</p>
                </div>
              </div>
              
              <div className="flex items-start space-x-4">
                <div 
                  className="w-12 h-12 rounded-xl flex items-center justify-center flex-shrink-0"
                  style={{ backgroundColor: 'rgba(178, 176, 232, 0.2)' }}
                >
                  <Users className="w-6 h-6" style={{ color: '#B2B0E8' }} />
                </div>
                <div>
                  <h3 className="font-semibold text-white mb-1">Team Collaboration</h3>
                  <p className="text-white/75 text-sm">Real-time editing and seamless team workflows</p>
                </div>
              </div>
              
              <div className="flex items-start space-x-4">
                <div 
                  className="w-12 h-12 rounded-xl flex items-center justify-center flex-shrink-0"
                  style={{ backgroundColor: 'rgba(178, 176, 232, 0.2)' }}
                >
                  <Zap className="w-6 h-6" style={{ color: '#B2B0E8' }} />
                </div>
                <div>
                  <h3 className="font-semibold text-white mb-1">Lightning Fast</h3>
                  <p className="text-white/75 text-sm">Optimized performance for large documents</p>
                </div>
              </div>
              
              <div className="flex items-start space-x-4">
                <div 
                  className="w-12 h-12 rounded-xl flex items-center justify-center flex-shrink-0"
                  style={{ backgroundColor: 'rgba(178, 176, 232, 0.2)' }}
                >
                  <FileText className="w-6 h-6" style={{ color: '#B2B0E8' }} />
                </div>
                <div>
                  <h3 className="font-semibold text-white mb-1">Smart Organization</h3>
                  <p className="text-white/75 text-sm">AI-powered categorization and search</p>
                </div>
              </div>
            </div>
          </div>
          
          {/* Trust indicators */}
          <div className="mt-16 pt-8 border-t border-white/20">
            <div className="flex items-center space-x-8 text-white/60">
              <div className="text-center">
                <div className="text-2xl font-bold text-white">99.9%</div>
                <div className="text-xs uppercase tracking-wide">Uptime</div>
              </div>
              <div className="text-center">
                <div className="text-2xl font-bold text-white">500K+</div>
                <div className="text-xs uppercase tracking-wide">Users</div>
              </div>
              <div className="text-center">
                <div className="text-2xl font-bold text-white">ISO 27001</div>
                <div className="text-xs uppercase tracking-wide">Certified</div>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Right Side - Form */}
      <div className="flex-1 lg:w-2/5 flex items-center justify-center p-8">
        <div className="w-full max-w-md">
          {/* Mobile Logo */}
          <div className="lg:hidden text-center mb-8">
            <div className="flex items-center justify-center space-x-3 mb-4">
              <div 
                className="w-12 h-12 rounded-xl flex items-center justify-center"
                style={{ backgroundColor: '#1A2A80' }}
              >
                <FileText className="w-6 h-6 text-white" />
              </div>
              <h1 className="text-2xl font-bold" style={{ color: '#1A2A80' }}>DocShare</h1>
            </div>
          </div>
          
          {/* Form Container */}
          <div className="bg-white rounded-3xl shadow-2xl p-8 border border-gray-100">
            {/* Header */}
            <div className="text-center mb-8">
              <h2 className="text-3xl font-bold mb-3" style={{ color: '#1A2A80' }}>
                {isLogin ? 'Welcome Back' : 'Join DocShare'}
              </h2>
              <p className="text-gray-600 leading-relaxed">
                {isLogin 
                  ? 'Sign in to access your secure document workspace' 
                  : 'Create your account and start collaborating securely'
                }
              </p>
            </div>

            {/* Form */}
            <div className="space-y-6">
              {/* Username Field */}
              <div className="space-y-2">
                <label className="block text-sm font-semibold" style={{ color: '#3B38A0' }}>
                  Username
                </label>
                <div className="relative">
                  <User className="absolute left-4 top-1/2 transform -translate-y-1/2 h-5 w-5 text-gray-400" />
                  <input
                    type="text"
                    name="username"
                    value={formData.username}
                    onChange={handleInputChange}
                    className={`w-full pl-12 pr-4 py-4 border-2 rounded-xl transition-all duration-200 bg-gray-50 focus:bg-white focus:outline-none ${
                      errors.username 
                        ? 'border-red-400 focus:border-red-500' 
                        : 'border-gray-200 focus:border-[#7A85C1]'
                    }`}
                    placeholder="Enter your username"
                  />
                </div>
                {errors.username && (
                  <p className="text-sm text-red-500 flex items-center space-x-1">
                    <span></span>
                    <span>{errors.username}</span>
                  </p>
                )}
              </div>

              {/* Email Field (Register only) */}
              {!isLogin && (
                <div className="space-y-2">
                  <label className="block text-sm font-semibold" style={{ color: '#3B38A0' }}>
                    Email Address
                  </label>
                  <div className="relative">
                    <Mail className="absolute left-4 top-1/2 transform -translate-y-1/2 h-5 w-5 text-gray-400" />
                    <input
                      type="email"
                      name="email"
                      value={formData.email}
                      onChange={handleInputChange}
                      className={`w-full pl-12 pr-4 py-4 border-2 rounded-xl transition-all duration-200 bg-gray-50 focus:bg-white focus:outline-none ${
                        errors.email 
                          ? 'border-red-400 focus:border-red-500' 
                          : 'border-gray-200 focus:border-[#7A85C1]'
                      }`}
                      placeholder="Enter your email address"
                    />
                  </div>
                  {errors.email && (
                    <p className="text-sm text-red-500 flex items-center space-x-1">
                      <span></span>
                      <span>{errors.email}</span>
                    </p>
                  )}
                </div>
              )}

              {/* Password Field */}
              <div className="space-y-2">
                <label className="block text-sm font-semibold" style={{ color: '#3B38A0' }}>
                  Password
                </label>
                <div className="relative">
                  <Lock className="absolute left-4 top-1/2 transform -translate-y-1/2 h-5 w-5 text-gray-400" />
                  <input
                    type={showPassword ? 'text' : 'password'}
                    name="password"
                    value={formData.password}
                    onChange={handleInputChange}
                    className={`w-full pl-12 pr-12 py-4 border-2 rounded-xl transition-all duration-200 bg-gray-50 focus:bg-white focus:outline-none ${
                      errors.password 
                        ? 'border-red-400 focus:border-red-500' 
                        : 'border-gray-200 focus:border-[#7A85C1]'
                    }`}
                    placeholder="Enter your password"
                  />
                  <button
                    type="button"
                    onClick={() => setShowPassword(!showPassword)}
                    className="absolute right-4 top-1/2 transform -translate-y-1/2 text-gray-400 hover:text-gray-600 transition-colors"
                  >
                    {showPassword ? <EyeOff className="h-5 w-5" /> : <Eye className="h-5 w-5" />}
                  </button>
                </div>
                {errors.password && (
                  <p className="text-sm text-red-500 flex items-center space-x-1">
                    <span></span>
                    <span>{errors.password}</span>
                  </p>
                )}
              </div>

              {/* Submit Button */}
              <button
                type="submit"
                disabled={loading}
                className="w-full flex items-center justify-center px-6 py-4 text-white font-semibold rounded-xl transition-all duration-300 transform hover:scale-[1.02] focus:outline-none focus:ring-4 focus:ring-opacity-50 disabled:opacity-50 disabled:cursor-not-allowed disabled:hover:scale-100 shadow-lg hover:shadow-xl"
                style={{ 
                  background: loading ? '#7A85C1' : `linear-gradient(135deg, #1A2A80 0%, #3B38A0 100%)`,
                  focusRingColor: '#B2B0E8'
                }}
              >
                {loading ? (
                  <>
                    <Loader2 className="animate-spin h-5 w-5 mr-3" />
                    {isLogin ? 'Signing In...' : 'Creating Account...'}
                  </>
                ) : (
                  <>
                    {isLogin ? 'Sign In to DocShare' : 'Create Your Account'}
                  </>
                )}
              </button>
            </div>

            {/* Forgot Password Link (Login only) */}
            {isLogin && (
              <div className="text-center mt-4">
                <button 
                  type="button"
                  className="text-sm font-medium hover:underline transition-colors"
                  style={{ color: '#7A85C1' }}
                >
                  Forgot your password?
                </button>
              </div>
            )}

            {/* Divider */}
            <div className="my-8 flex items-center">
              <div className="flex-1 h-px bg-gray-200"></div>
              <span className="px-4 text-sm text-gray-500 font-medium">OR</span>
              <div className="flex-1 h-px bg-gray-200"></div>
            </div>

            {/* Switch Mode */}
            <div className="text-center">
              <p className="text-gray-600 mb-4">
                {isLogin ? "New to DocShare?" : 'Already have an account?'}
              </p>
              <button
                type="button"
                onClick={switchMode}
                className="w-full px-6 py-4 border-2 rounded-xl font-semibold transition-all duration-300 transform hover:scale-[1.02] focus:outline-none focus:ring-4 focus:ring-opacity-50"
                style={{ 
                  borderColor: '#B2B0E8',
                  color: '#3B38A0',
                  background: 'linear-gradient(135deg, rgba(178, 176, 232, 0.1) 0%, rgba(122, 133, 193, 0.1) 100%)'
                }}
              >
                {isLogin ? 'Create New Account' : 'Sign In Instead'}
              </button>
            </div>
          </div>

          {/* Footer */}
          <div className="text-center mt-8 text-sm text-gray-500">
            <p> 2024 DocShare. All rights reserved.</p>
            <div className="flex items-center justify-center space-x-4 mt-2">
              <button className="hover:underline">Privacy Policy</button>
              <span></span>
              <button className="hover:underline">Terms of Service</button>
              <span></span>
              <button className="hover:underline">Support</button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Login;
