import React, { useState } from "react";
import Header from "./components/user/Header";
import Sidebar from "./components/user/Sidebar";
import AdminSidebar from "./components/admin/AdminSidebar";
import Footer from "./components/user/Footer";
import Dashboard from "./components/user/Dashboard";
import MyDrive from "./components/user/MyDrive";
import SharedWithMe from "./components/user/SharedWithMe";
import AdminDashboard from "./components/admin/AdminDashboard";
import "./App.css";

// Simple Settings component
const Settings = () => (
  <div className="p-6">
    <h1 className="text-3xl font-bold text-gray-800 mb-6">Settings</h1>
    <div className="bg-white rounded-xl shadow-lg p-6">
      <h2 className="text-xl font-semibold text-gray-800 mb-4">Account Settings</h2>
      <p className="text-gray-600 mb-4">
        Manage your account preferences and settings.
      </p>

      <div className="space-y-4">
        <div className="border-b border-gray-200 pb-4">
          <h3 className="font-medium text-gray-800 mb-2">Profile Information</h3>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Name
              </label>
              <input
                type="text"
                defaultValue="John Doe"
                className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:border-transparent"
                style={{ "--tw-ring-color": "#7A85C1" }}
              />
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Email
              </label>
              <input
                type="email"
                defaultValue="john.doe@example.com"
                className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:border-transparent"
                style={{ "--tw-ring-color": "#7A85C1" }}
              />
            </div>
          </div>
        </div>

        <div className="border-b border-gray-200 pb-4">
          <h3 className="font-medium text-gray-800 mb-2">Notifications</h3>
          <div className="space-y-2">
            <label className="flex items-center space-x-3">
              <input type="checkbox" defaultChecked className="rounded" />
              <span className="text-sm text-gray-700">
                Email notifications for shared files
              </span>
            </label>
            <label className="flex items-center space-x-3">
              <input type="checkbox" defaultChecked className="rounded" />
              <span className="text-sm text-gray-700">
                Push notifications for file updates
              </span>
            </label>
          </div>
        </div>

        <div className="flex space-x-4">
          <button
            className="px-6 py-2 text-white font-medium rounded-lg hover:opacity-90 transition-opacity"
            style={{ backgroundColor: "#3B38A0" }}
          >
            Save Changes
          </button>
          <button className="px-6 py-2 border border-gray-300 text-gray-700 font-medium rounded-lg hover:bg-gray-50 transition-colors">
            Cancel
          </button>
        </div>
      </div>
    </div>
  </div>
);

// Simple login form
const LoginForm = ({ handleLogin }) => (
  <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-blue-50 to-indigo-100">
    <div className="bg-white p-8 rounded-xl shadow-2xl w-full max-w-md">
      <div className="text-center mb-8">
        <div
          className="w-16 h-16 rounded-xl flex items-center justify-center text-white font-bold text-2xl mx-auto mb-4"
          style={{ backgroundColor: "#1A2A80" }}
        >
          DS
        </div>
        <h1 className="text-2xl font-bold text-gray-800">Document Sharing</h1>
        <p className="text-gray-600">Sign in to your account</p>
      </div>

      <form onSubmit={(e) => { e.preventDefault(); handleLogin(); }}>
        <div className="space-y-4">
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">
              Email
            </label>
            <input
              type="email"
              required
              className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:border-transparent"
              style={{ "--tw-ring-color": "#7A85C1" }}
              placeholder="Enter your email"
            />
          </div>
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">
              Password
            </label>
            <input
              type="password"
              required
              className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:border-transparent"
              style={{ "--tw-ring-color": "#7A85C1" }}
              placeholder="Enter your password"
            />
          </div>
        </div>
        <div className="flex space-x-2 mt-6">
          <button
            type="submit"
            className="w-full px-4 py-2 text-white rounded-lg font-medium"
            style={{ backgroundColor: "#3B38A0" }}
          >
            Sign In
          </button>
        </div>
      </form>
    </div>
  </div>
);

const App = () => {
  const [activeSection, setActiveSection] = useState("dashboard");
  const [isLoggedIn, setIsLoggedIn] = useState(true);
  const [userRole, setUserRole] = useState("ADMIN"); // 'USER' or 'ADMIN'

  const handleLogout = () => {
    setIsLoggedIn(false);
    console.log("User logged out");
  };

  const handleLogin = (role = "USER") => {
    setIsLoggedIn(true);
    setUserRole(role);
    setActiveSection(role === "ADMIN" ? "admin-overview" : "dashboard");
  };

  const renderMainContent = () => {
    if (userRole === "ADMIN") {
      switch (activeSection) {
        case "admin-overview":
        case "admin-users":
        case "admin-documents":
        case "admin-logs":
        case "admin-settings":
          return (
            <AdminDashboard activeTab={activeSection.replace("admin-", "")} />
          );
        default:
          return <AdminDashboard activeTab="overview" />;
      }
    } else {
      switch (activeSection) {
        case "dashboard":
          return <Dashboard />;
        case "mydrive":
          return <MyDrive />;
        case "shared":
          return <SharedWithMe />;
        case "settings":
          return <Settings />;
        default:
          return <Dashboard />;
      }
    }
  };

  if (!isLoggedIn) {
    return <LoginForm handleLogin={handleLogin} />;
  }

  return (
    <div className="min-h-screen bg-gray-50 flex flex-col">
      <Header />
      <div className="flex flex-1">
        {userRole === "ADMIN" ? (
          <AdminSidebar
            activeSection={activeSection}
            setActiveSection={setActiveSection}
            onLogout={handleLogout}
          />
        ) : (
          <Sidebar
            activeSection={activeSection}
            setActiveSection={setActiveSection}
            onLogout={handleLogout}
          />
        )}
        <main className="flex-1 overflow-y-auto">{renderMainContent()}</main>
      </div>
      <Footer />
    </div>
  );
};

export default App;
