import React, { useState, useEffect } from 'react';
import { AuthProvider, useAuth } from './context/AuthContext';
import { AppProvider } from './context/AppContext';

// Components
import LoginForm from './components/auth/LoginForm';
import RegisterForm from './components/auth/RegisterForm';
import Layout from './components/common/Layout';
import DashboardPage from './pages/DashboardPage';
import DocumentsPage from './pages/DocumentsPage';
import FoldersPage from './pages/FoldersPage';
import UsersPage from './pages/UsersPage';
import ProfilePage from './pages/ProfilePage';

// Simple router implementation
const routes = {
  '/': 'dashboard',
  '/dashboard': 'dashboard',
  '/documents': 'documents',
  '/folders': 'folders',
  '/users': 'users',
  '/profile': 'profile'
};

function Router() {
  const [currentRoute, setCurrentRoute] = useState(window.location.hash.substring(1) || '/dashboard');
  
  useEffect(() => {
    const handleHashChange = () => {
      setCurrentRoute(window.location.hash.substring(1) || '/dashboard');
    };
    
    window.addEventListener('hashchange', handleHashChange);
    return () => window.removeEventListener('hashchange', handleHashChange);
  }, []);

  const navigate = (path) => {
    window.location.hash = path;
    setCurrentRoute(path);
  };

  const renderPage = () => {
    const route = routes[currentRoute] || 'dashboard';
    
    switch (route) {
      case 'dashboard':
        return <DashboardPage />;
      case 'documents':
        return <DocumentsPage />;
      case 'folders':
        return <FoldersPage />;
      case 'users':
        return <UsersPage />;
      case 'profile':
        return <ProfilePage />;
      default:
        return <DashboardPage />;
    }
  };

  return { currentRoute, navigate, renderPage };
}

function AppContent() {
  const { user, isLoading } = useAuth();
  const [authMode, setAuthMode] = useState('login');
  const { currentRoute, navigate, renderPage } = Router();

  if (isLoading) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-primary-600 via-primary-500 to-primary-700">
        <div className="animate-spin rounded-full h-16 w-16 border-4 border-white border-t-transparent"></div>
      </div>
    );
  }

  if (!user) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-primary-600 via-primary-500 to-primary-700 flex items-center justify-center p-4">
        <div className="w-full max-w-md">
          <div className="bg-white rounded-2xl shadow-2xl p-8">
            <div className="text-center mb-8">
              <div className="w-16 h-16 bg-primary-600 rounded-2xl flex items-center justify-center mx-auto mb-4">
                <svg className="w-8 h-8 text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 12h6m-6 4h6m2 5H7a2 2 0 01-2-2V5a2 2 0 012-2h5.586a1 1 0 01.707.293l5.414 5.414a1 1 0 01.293.707V19a2 2 0 01-2 2z" />
                </svg>
              </div>
              <h1 className="text-3xl font-bold text-gray-900 mb-2">
                Document Manager
              </h1>
              <p className="text-gray-600">
                {authMode === 'login' ? 'Welcome back! Please sign in to continue.' : 'Create your account to get started.'}
              </p>
            </div>

            <div className="flex mb-6">
              <button
                onClick={() => setAuthMode('login')}
                className={`flex-1 py-2 px-4 text-sm font-medium rounded-l-lg transition-colors ${
                  authMode === 'login'
                    ? 'bg-primary-600 text-white'
                    : 'bg-gray-100 text-gray-600 hover:bg-gray-200'
                }`}
              >
                Sign In
              </button>
              <button
                onClick={() => setAuthMode('register')}
                className={`flex-1 py-2 px-4 text-sm font-medium rounded-r-lg transition-colors ${
                  authMode === 'register'
                    ? 'bg-primary-600 text-white'
                    : 'bg-gray-100 text-gray-600 hover:bg-gray-200'
                }`}
              >
                Sign Up
              </button>
            </div>

            {authMode === 'login' ? <LoginForm /> : <RegisterForm />}
          </div>
        </div>
      </div>
    );
  }

  return (
    <Layout currentRoute={currentRoute} navigate={navigate}>
      {renderPage()}
    </Layout>
  );
}

function App() {
  return (
    <AuthProvider>
      <AppProvider>
        <div className="min-h-screen bg-gray-50">
          {/* Toast notifications would go here */}
          <AppContent />
        </div>
      </AppProvider>
    </AuthProvider>
  );
}

export default App;
