import React, { useState } from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";

import Header from "./components/user/Header";
import Sidebar from "./components/user/Sidebar";
import AdminSidebar from "./components/admin/AdminSidebar";
import Footer from "./components/user/Footer";
import Dashboard from "./components/user/Dashboard";
import MyDrive from "./components/user/MyDrive";
import SharedWithMe from "./components/user/SharedWithMe";
import AdminDashboard from "./components/admin/AdminDashboard";
import Login from "./components/auth/LoginPage";
import Register from "./components/auth/Register";
import ProtectedRoute from "./components/auth/ProtectedRoute";
import "./App.css";

// Settings Component
const Settings = () => (
  <div className="p-6">
    <h1 className="text-3xl font-bold text-gray-800 mb-6">Settings</h1>
    <div className="bg-white rounded-xl shadow-lg p-6">
      {/* Your settings form here */}
      <p>Manage account settings</p>
    </div>
  </div>
);

// Layout for authenticated users
const UserLayout = ({ userRole, activeSection, setActiveSection, handleLogout }) => {
  const renderMainContent = () => {
    if (userRole === "ADMIN") {
      return <AdminDashboard activeTab={activeSection.replace("admin-", "")} />;
    } else {
      switch (activeSection) {
        case "dashboard":
          return <Dashboard />;
        case "mydrive":
          return <MyDrive />;
        case "shared":
          return <SharedWithMe />;
        case "settings":
          return <Settings />;
        default:
          return <Dashboard />;
      }
    }
  };

  return (
    <div className="min-h-screen bg-gray-50 flex flex-col">
      <Header />
      <div className="flex flex-1">
        {userRole === "ADMIN" ? (
          <AdminSidebar
            activeSection={activeSection}
            setActiveSection={setActiveSection}
            onLogout={handleLogout}
          />
        ) : (
          <Sidebar
            activeSection={activeSection}
            setActiveSection={setActiveSection}
            onLogout={handleLogout}
          />
        )}
        <main className="flex-1 overflow-y-auto">{renderMainContent()}</main>
      </div>
      <Footer />
    </div>
  );
};

// Helper functions
const isAuthenticated = () => !!localStorage.getItem("token");
const getUserRole = () => {
  const user = localStorage.getItem("user");
  if (user) {
    try {
      const userData = JSON.parse(user);
      return userData.role;
    } catch {
      return null;
    }
  }
  return null;
};

function App() {
  const [activeSection, setActiveSection] = useState("dashboard");

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("user");
    window.location.href = "/login";
  };

  const userRole = getUserRole();

  return (
    <Router>
      <Routes>
        {/* Public Routes */}
        <Route
          path="/login"
          element={
            !isAuthenticated() ? (
              <Login />
            ) : (
              <Navigate
                to={userRole === "ADMIN" ? "/admin-dashboard" : "/user-dashboard"}
                replace
              />
            )
          }
        />
        <Route
          path="/register"
          element={
            !isAuthenticated() ? (
              <Register />
            ) : (
              <Navigate
                to={userRole === "ADMIN" ? "/admin-dashboard" : "/user-dashboard"}
                replace
              />
            )
          }
        />

        {/* Protected Routes */}
        <Route
          path="/admin-dashboard/*"
          element={
            <ProtectedRoute requiredRole="ADMIN">
              <UserLayout
                userRole="ADMIN"
                activeSection={activeSection}
                setActiveSection={setActiveSection}
                handleLogout={handleLogout}
              />
            </ProtectedRoute>
          }
        />
        <Route
          path="/user-dashboard/*"
          element={
            <ProtectedRoute requiredRole="USER">
              <UserLayout
                userRole="USER"
                activeSection={activeSection}
                setActiveSection={setActiveSection}
                handleLogout={handleLogout}
              />
            </ProtectedRoute>
          }
        />

        {/* Default Route */}
        <Route
          path="/"
          element={
            isAuthenticated() ? (
              <Navigate
                to={userRole === "ADMIN" ? "/admin-dashboard" : "/user-dashboard"}
                replace
              />
            ) : (
              <Navigate to="/login" replace />
            )
          }
        />

        {/* Catch-all */}
        <Route path="*" element={<Navigate to="/login" replace />} />
      </Routes>
    </Router>
  );
}

export default App;
