import React, { useState } from 'react';
import { X, Users, Mail, Share2, Copy, Check } from 'lucide-react';

const ShareModal = ({ isOpen, onClose, item, itemType, onShare }) => {
  const [shareWith, setShareWith] = useState('');
  const [loading, setLoading] = useState(false);
  const [copied, setCopied] = useState(false);
  const [shareMode, setShareMode] = useState('email'); // 'email' or 'link'

  const handleShare = async (e) => {
    e.preventDefault();
    if (!shareWith.trim()) return;

    setLoading(true);
    try {
      await onShare(item, shareWith.trim());
      setShareWith('');
      onClose();
    } catch (error) {
      console.error('Error sharing item:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleCopyLink = async () => {
    try {
      // Generate a shareable link (you can customize this based on your needs)
      const shareLink = `${window.location.origin}/shared/${itemType.toLowerCase()}/${item?.id}`;
      await navigator.clipboard.writeText(shareLink);
      setCopied(true);
      setTimeout(() => setCopied(false), 2000);
    } catch (error) {
      console.error('Failed to copy link:', error);
    }
  };

  const togglePublic = async () => {
    try {
      // Toggle public status - you'll need to implement this in your backend
      await onShare(item, null, !item?.isPublic);
      onClose();
    } catch (error) {
      console.error('Error updating public status:', error);
    }
  };

  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 z-50 overflow-y-auto">
      <div className="flex min-h-screen items-center justify-center p-4">
        {/* Backdrop */}
        <div 
          className="fixed inset-0 bg-black/50 backdrop-blur-sm transition-opacity"
          onClick={onClose}
        />
        
        {/* Modal */}
        <div className="relative w-full max-w-md transform rounded-2xl bg-white shadow-2xl transition-all">
          {/* Header */}
          <div className="flex items-center justify-between border-b border-gray-200 p-6">
            <div className="flex items-center space-x-3">
              <div className="rounded-full bg-blue-100 p-2">
                <Share2 className="h-5 w-5 text-blue-600" />
              </div>
              <div>
                <h2 className="text-lg font-semibold text-gray-900">
                  Share {itemType}
                </h2>
                <p className="text-sm text-gray-500">
                  {item?.title || item?.name}
                </p>
              </div>
            </div>
            <button
              onClick={onClose}
              className="rounded-full p-2 text-gray-400 hover:bg-gray-100 hover:text-gray-600 transition-colors"
            >
              <X className="h-5 w-5" />
            </button>
          </div>

          {/* Content */}
          <div className="p-6">
            {/* Share Mode Tabs */}
            <div className="flex space-x-1 rounded-lg bg-gray-100 p-1 mb-6">
              <button
                onClick={() => setShareMode('email')}
                className={`flex-1 rounded-md px-3 py-2 text-sm font-medium transition-colors ${
                  shareMode === 'email'
                    ? 'bg-white text-gray-900 shadow'
                    : 'text-gray-600 hover:text-gray-900'
                }`}
              >
                <Mail className="inline h-4 w-4 mr-2" />
                By Email
              </button>
              <button
                onClick={() => setShareMode('link')}
                className={`flex-1 rounded-md px-3 py-2 text-sm font-medium transition-colors ${
                  shareMode === 'link'
                    ? 'bg-white text-gray-900 shadow'
                    : 'text-gray-600 hover:text-gray-900'
                }`}
              >
                <Copy className="inline h-4 w-4 mr-2" />
                Copy Link
              </button>
            </div>

            {shareMode === 'email' ? (
              <form onSubmit={handleShare} className="space-y-4">
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    Share with people
                  </label>
                  <div className="relative">
                    <Users className="absolute left-3 top-1/2 transform -translate-y-1/2 h-5 w-5 text-gray-400" />
                    <input
                      type="email"
                      value={shareWith}
                      onChange={(e) => setShareWith(e.target.value)}
                      placeholder="Enter email address"
                      className="w-full pl-10 pr-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent transition-colors"
                      required
                    />
                  </div>
                  <p className="text-xs text-gray-500 mt-2">
                    They will receive an email invitation to access this {itemType.toLowerCase()}
                  </p>
                </div>

                <button
                  type="submit"
                  disabled={loading || !shareWith.trim()}
                  className="w-full flex items-center justify-center px-4 py-3 border border-transparent text-sm font-medium rounded-lg text-white bg-blue-600 hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500 disabled:opacity-50 disabled:cursor-not-allowed transition-colors"
                >
                  {loading ? (
                    <div className="animate-spin rounded-full h-4 w-4 border-2 border-white border-t-transparent mr-2" />
                  ) : (
                    <Share2 className="h-4 w-4 mr-2" />
                  )}
                  {loading ? 'Sharing...' : 'Send Invitation'}
                </button>
              </form>
            ) : (
              <div className="space-y-4">
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    Share link
                  </label>
                  <div className="flex">
                    <input
                      type="text"
                      value={`${window.location.origin}/shared/${itemType.toLowerCase()}/${item?.id}`}
                      readOnly
                      className="flex-1 px-3 py-3 border border-gray-300 rounded-l-lg bg-gray-50 text-gray-600 text-sm"
                    />
                    <button
                      onClick={handleCopyLink}
                      className="px-4 py-3 border border-l-0 border-gray-300 rounded-r-lg bg-white hover:bg-gray-50 transition-colors"
                    >
                      {copied ? (
                        <Check className="h-4 w-4 text-green-600" />
                      ) : (
                        <Copy className="h-4 w-4 text-gray-600" />
                      )}
                    </button>
                  </div>
                  {copied && (
                    <p className="text-xs text-green-600 mt-2">Link copied to clipboard!</p>
                  )}
                </div>
              </div>
            )}

            {/* Public Access Toggle */}
            <div className="mt-6 pt-6 border-t border-gray-200">
              <div className="flex items-center justify-between">
                <div>
                  <h3 className="text-sm font-medium text-gray-900">Public access</h3>
                  <p className="text-xs text-gray-500">
                    Anyone with the link can view this {itemType.toLowerCase()}
                  </p>
                </div>
                <button
                  onClick={togglePublic}
                  className={`relative inline-flex h-6 w-11 flex-shrink-0 cursor-pointer rounded-full border-2 border-transparent transition-colors duration-200 ease-in-out focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-offset-2 ${
                    item?.isPublic ? 'bg-blue-600' : 'bg-gray-200'
                  }`}
                >
                  <span
                    className={`pointer-events-none inline-block h-5 w-5 transform rounded-full bg-white shadow ring-0 transition duration-200 ease-in-out ${
                      item?.isPublic ? 'translate-x-5' : 'translate-x-0'
                    }`}
                  />
                </button>
              </div>
            </div>
          </div>

          {/* Footer */}
          <div className="border-t border-gray-200 px-6 py-4">
            <button
              onClick={onClose}
              className="w-full px-4 py-2 text-sm font-medium text-gray-700 bg-gray-100 hover:bg-gray-200 rounded-lg transition-colors"
            >
              Done
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ShareModal;
