import React, { useState } from 'react';
import Sidebar from './user/Sidebar';
import Dashboard from './user/Dashboard';
import MyDrive from './user/MyDrive';
import Sharedwith from './user/SharedFiles';

const MainLayout = () => {
  const [activeSection, setActiveSection] = useState('dashboard');

  const handleLogout = () => {
    // Implement logout logic here
    console.log('Logging out...');
    // For example:
    // localStorage.removeItem('authToken');
    // navigate('/login');
  };

  const handleCreateSuccess = (type, item) => {
    // Handle successful creation/upload
    console.log(`${type} created/uploaded:`, item);
    // You might want to refresh the current view or show a notification
    
    // If we're on dashboard, we might want to refresh it
    if (activeSection === 'dashboard') {
      // Dashboard component will handle its own refresh via useEffect
      // or you could trigger a refresh here
    }
  };

  const renderActiveSection = () => {
    switch (activeSection) {
      case 'dashboard':
        return <Dashboard />;
      case 'mydrive':
        return <MyDrive />;
      case 'shared':
        return <SharedFiles />;
    //   case 'settings':
    //     return <Settings />;
      default:
        return <Dashboard />;
    }
  };

  return (
    <div className="flex h-screen bg-gray-50 overflow-hidden">
      {/* Fixed Sidebar */}
      <Sidebar 
        activeSection={activeSection}
        setActiveSection={setActiveSection}
        onLogout={handleLogout}
        onCreateSuccess={handleCreateSuccess}
      />
      
      {/* Main Content Area - with left margin to account for fixed sidebar */}
      <div className="flex-1 ml-80">
        {renderActiveSection()}
      </div>
    </div>
  );
};

export default MainLayout;
