// src/components/documents/DocumentGrid.js
import React from 'react';
import './DocumentGrid.css';

const DocumentGrid = ({ documents, view = 'grid' }) => {
  const getFileIcon = (fileType) => {
    if (fileType?.includes('pdf')) return 'üìÑ';
    if (fileType?.includes('image')) return 'üñºÔ∏è';
    if (fileType?.includes('video')) return 'üé•';
    if (fileType?.includes('audio')) return 'üéµ';
    return 'üìã';
  };

  if (documents.length === 0) {
    return (
      <div className="empty-state">
        <p>No documents found</p>
      </div>
    );
  }

  return (
    <div className={`document-grid ${view}`}>
      {documents.map(document => (
        <div key={document.id} className="document-item">
          <div className="document-icon">
            {getFileIcon(document.fileType)}
          </div>
          <div className="document-info">
            <h3 className="document-title">{document.title}</h3>
            <p className="document-filename">{document.fileName}</p>
            <div className="document-meta">
              <span className="document-type">{document.fileType}</span>
              {document.isPublic && <span className="public-badge">Public</span>}
            </div>
          </div>
          <div className="document-actions">
            <button className="action-btn">Download</button>
            <button className="action-btn">Share</button>
            <button className="action-btn delete">Delete</button>
          </div>
        </div>
      ))}
    </div>
  );
};

export default DocumentGrid;
