import React, { useState } from 'react';
import '../../styles/components/Modal.css';

const ShareModal = ({ isOpen, onClose, item, itemType, onShare }) => {
  const [shareWith, setShareWith] = useState('');
  const [loading, setLoading] = useState(false);

  const handleShare = async (e) => {
    e.preventDefault();
    if (!shareWith.trim()) return;

    setLoading(true);
    try {
      await onShare(item, shareWith.trim());
      setShareWith('');
      onClose();
    } catch (error) {
      console.error('Error sharing item:', error);
    } finally {
      setLoading(false);
    }
  };

  if (!isOpen) return null;

  return (
    <div className="modal-overlay" onClick={onClose}>
      <div className="modal-content" onClick={(e) => e.stopPropagation()}>
        <div className="modal-header">
          <h2>Share {itemType}</h2>
          <button className="modal-close" onClick={onClose}>&times;</button>
        </div>
        <div className="share-item-info">
          <h3>{item?.title || item?.name}</h3>
          <p>Share this {itemType.toLowerCase()} with other users</p>
        </div>
        <form onSubmit={handleShare}>
          <div className="form-group">
            <label htmlFor="shareWith">Share with (username or email)</label>
            <input
              type="text"
              id="shareWith"
              value={shareWith}
              onChange={(e) => setShareWith(e.target.value)}
              placeholder="Enter username or email"
              required
            />
          </div>
          <div className="modal-actions">
            <button type="button" onClick={onClose} className="btn-secondary">
              Cancel
            </button>
            <button type="submit" className="btn-primary" disabled={loading}>
              {loading ? 'Sharing...' : 'Share'}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default ShareModal;
