import React, { useState } from 'react';
import { 
  FileText, 
  MoreVertical, 
  Share2, 
  Eye, 
  Download, 
  Edit3, 
  Trash2, 
  Calendar,
  User,
  Globe,
  Lock
} from 'lucide-react';

const FileCard = ({ document, onView, onShare, onDelete, onEdit, showActions = true }) => {
  const [showMenu, setShowMenu] = useState(false);

  const getFileIcon = (fileType) => {
    const type = fileType?.toLowerCase();
    switch (type) {
      case 'pdf':
        return { icon: FileText, color: 'from-red-500 to-red-600' };
      case 'doc':
      case 'docx':
        return { icon: FileText, color: 'from-blue-500 to-blue-600' };
      case 'xls':
      case 'xlsx':
        return { icon: FileText, color: 'from-green-500 to-green-600' };
      case 'ppt':
      case 'pptx':
        return { icon: FileText, color: 'from-orange-500 to-orange-600' };
      case 'txt':
        return { icon: FileText, color: 'from-gray-500 to-gray-600' };
      default:
        return { icon: FileText, color: 'from-indigo-500 to-indigo-600' };
    }
  };

  const handleAction = (action, e) => {
    e.stopPropagation();
    setShowMenu(false);
    
    switch (action) {
      case 'view':
        onView(document);
        break;
      case 'share':
        onShare(document);
        break;
      case 'edit':
        onEdit && onEdit(document);
        break;
      case 'delete':
        onDelete(document.id);
        break;
      case 'download':
        // Implement download functionality
        console.log('Downloading:', document);
        break;
    }
  };

  const { icon: Icon, color } = getFileIcon(document.fileType);

  return (
    <div className="group bg-white rounded-xl border border-gray-200 hover:border-blue-300 hover:shadow-md transition-all duration-200 relative">
      {/* Main Content */}
      <div className="p-6">
        <div className="flex items-start justify-between">
          <div className="flex items-center space-x-4 flex-1">
            {/* File Icon */}
            <div className="flex-shrink-0">
              <div className={`w-12 h-12 bg-gradient-to-br ${color} rounded-lg flex items-center justify-center shadow-sm`}>
                <Icon className="h-6 w-6 text-white" />
              </div>
            </div>
            
            {/* File Info */}
            <div className="flex-1 min-w-0">
              <h3 className="text-lg font-semibold text-gray-900 truncate group-hover:text-blue-700 transition-colors">
                {document.title}
              </h3>
              <p className="text-sm text-gray-600 truncate mt-1">
                {document.fileName}
              </p>
              <div className="flex items-center space-x-4 mt-2 text-xs text-gray-500">
                <div className="flex items-center space-x-1">
                  <Calendar className="h-3 w-3" />
                  <span>
                    {document.createdAt 
                      ? new Date(document.createdAt).toLocaleDateString('en-US', {
                          month: 'short',
                          day: 'numeric'
                        })
                      : 'Unknown'
                    }
                  </span>
                </div>
                {document.fileType && (
                  <span className="px-2 py-1 bg-gray-100 rounded-md font-medium">
                    {document.fileType.toUpperCase()}
                  </span>
                )}
                {document.owner && (
                  <div className="flex items-center space-x-1">
                    <User className="h-3 w-3" />
                    <span>{document.owner.username}</span>
                  </div>
                )}
              </div>
            </div>
          </div>

          {/* Privacy Indicator */}
          <div className="flex items-center space-x-2">
            {document.isPublic ? (
              <div className="flex items-center space-x-1 text-green-600">
                <Globe className="h-4 w-4" />
              </div>
            ) : (
              <div className="flex items-center space-x-1 text-gray-500">
                <Lock className="h-4 w-4" />
              </div>
            )}
            
            {/* Actions Menu */}
            {showActions && (
              <div className="file-actions relative">
                <button
                  onClick={(e) => {
                    e.stopPropagation();
                    setShowMenu(!showMenu);
                  }}
                  className="p-2 rounded-lg opacity-0 group-hover:opacity-100 hover:bg-gray-100 transition-all duration-200"
                >
                  <MoreVertical className="h-5 w-5 text-gray-500" />
                </button>

                {/* Dropdown Menu */}
                {showMenu && (
                  <div className="action-menu absolute right-0 top-full mt-2 w-48 bg-white rounded-lg shadow-lg border border-gray-200 py-1 z-10">
                    <button
                      onClick={(e) => handleAction('view', e)}
                      className="w-full flex items-center px-4 py-2 text-sm text-gray-700 hover:bg-gray-50 transition-colors"
                    >
                      <Eye className="h-4 w-4 mr-3 text-gray-500" />
                      View
                    </button>
                    
                    <button
                      onClick={(e) => handleAction('download', e)}
                      className="w-full flex items-center px-4 py-2 text-sm text-gray-700 hover:bg-gray-50 transition-colors"
                    >
                      <Download className="h-4 w-4 mr-3 text-gray-500" />
                      Download
                    </button>
                    
                    <button
                      onClick={(e) => handleAction('share', e)}
                      className="w-full flex items-center px-4 py-2 text-sm text-gray-700 hover:bg-gray-50 transition-colors"
                    >
                      <Share2 className="h-4 w-4 mr-3 text-gray-500" />
                      Share
                    </button>
                    
                    {onEdit && (
                      <button
                        onClick={(e) => handleAction('edit', e)}
                        className="w-full flex items-center px-4 py-2 text-sm text-gray-700 hover:bg-gray-50 transition-colors"
                      >
                        <Edit3 className="h-4 w-4 mr-3 text-gray-500" />
                        Edit
                      </button>
                    )}
                    
                    <hr className="my-1" />
                    
                    <button
                      onClick={(e) => handleAction('delete', e)}
                      className="w-full flex items-center px-4 py-2 text-sm text-red-600 hover:bg-red-50 transition-colors"
                    >
                      <Trash2 className="h-4 w-4 mr-3" />
                      Delete
                    </button>
                  </div>
                )}
              </div>
            )}
          </div>
        </div>
      </div>

      {/* Hover Effect Overlay */}
      <div className="absolute inset-0 bg-gradient-to-r from-blue-500/5 to-indigo-500/5 opacity-0 group-hover:opacity-100 transition-opacity duration-200 rounded-xl pointer-events-none"></div>
      
      {/* Click outside to close menu */}
      {showMenu && (
        <div 
          className="fixed inset-0 z-5"
          onClick={() => setShowMenu(false)}
        />
      )}
    </div>
  );
};

export default FileCard;
