import React, { useState, useEffect } from 'react';
import { documentService } from '../../services/documentService';
import FileCard from '../common/FileCard';
import { Users, Globe, Search, Filter, Calendar } from 'lucide-react';
import toast from 'react-hot-toast';

const SharedWithMe = () => {
  const [sharedDocuments, setSharedDocuments] = useState([]);
  const [publicDocuments, setPublicDocuments] = useState([]);
  const [loading, setLoading] = useState(true);
  const [activeTab, setActiveTab] = useState('shared');
  const [searchQuery, setSearchQuery] = useState('');
  const [filterBy, setFilterBy] = useState('all');

  useEffect(() => {
    fetchSharedContent();
  }, []);

  const fetchSharedContent = async () => {
    setLoading(true);
    try {
      const [accessible, publicDocs, myDocs] = await Promise.all([
        documentService.getAllDocuments(),
        documentService.getPublicDocuments(),
        documentService.getMyDocuments()
      ]);
      
      // Filter out user's own documents to get only shared ones
      const myDocIds = myDocs.map(doc => doc.id);
      const sharedOnly = accessible.filter(doc => !myDocIds.includes(doc.id) && !doc.isPublic);
      
      setSharedDocuments(sharedOnly);
      setPublicDocuments(publicDocs.filter(doc => !myDocIds.includes(doc.id)));
    } catch (error) {
      console.error('Error fetching shared content:', error);
      toast.error('Failed to load shared content');
    } finally {
      setLoading(false);
    }
  };

  const handleViewDocument = (document) => {
    toast.info('Document viewer will be implemented');
    console.log('Viewing shared document:', document);
  };

  const getDisplayDocuments = () => {
    const documents = activeTab === 'shared' ? sharedDocuments : publicDocuments;
    
    // Apply search filter
    let filtered = documents.filter(doc =>
      doc.title.toLowerCase().includes(searchQuery.toLowerCase()) ||
      doc.fileName.toLowerCase().includes(searchQuery.toLowerCase()) ||
      (doc.owner?.username || '').toLowerCase().includes(searchQuery.toLowerCase())
    );

    // Apply file type filter
    if (filterBy !== 'all') {
      filtered = filtered.filter(doc => 
        doc.fileType?.toLowerCase() === filterBy.toLowerCase()
      );
    }

    return filtered;
  };

  const getFileTypes = () => {
    const documents = activeTab === 'shared' ? sharedDocuments : publicDocuments;
    const types = [...new Set(documents.map(doc => doc.fileType).filter(Boolean))];
    return types.sort();
  };

  const TabButton = ({ id, label, icon: Icon, isActive, onClick, count }) => (
    <button
      onClick={onClick}
      className={`flex items-center space-x-2 px-4 py-3 rounded-lg font-medium transition-all duration-200 ${
        isActive
          ? 'bg-blue-600 text-white shadow-md'
          : 'text-gray-600 hover:text-gray-900 hover:bg-gray-100'
      }`}
    >
      <Icon className="h-5 w-5" />
      <span>{label}</span>
      {count !== undefined && (
        <span className={`px-2 py-1 rounded-full text-xs ${
          isActive ? 'bg-blue-500 text-white' : 'bg-gray-200 text-gray-600'
        }`}>
          {count}
        </span>
      )}
    </button>
  );

  const EmptyState = ({ icon: Icon, title, description }) => (
    <div className="flex flex-col items-center justify-center py-16">
      <div className="w-20 h-20 bg-gray-100 rounded-full flex items-center justify-center mb-4">
        <Icon className="h-10 w-10 text-gray-400" />
      </div>
      <h3 className="text-lg font-medium text-gray-900 mb-2">{title}</h3>
      <p className="text-gray-500 text-center max-w-md">{description}</p>
    </div>
  );

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-96">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-4 border-blue-600 border-t-transparent mx-auto mb-4"></div>
          <p className="text-gray-600">Loading shared content...</p>
        </div>
      </div>
    );
  }

  const displayDocuments = getDisplayDocuments();
  const fileTypes = getFileTypes();

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex flex-col sm:flex-row sm:items-center justify-between space-y-4 sm:space-y-0">
        <div>
          <h1 className="text-3xl font-bold text-gray-900">Shared with Me</h1>
          <p className="text-gray-600 mt-1">
            Documents and files that have been shared with you by other users
          </p>
        </div>
        
        {/* Quick Stats */}
        <div className="flex items-center space-x-4 text-sm text-gray-500">
          <div className="flex items-center space-x-1">
            <Users className="h-4 w-4" />
            <span>{sharedDocuments.length} shared</span>
          </div>
          <div className="flex items-center space-x-1">
            <Globe className="h-4 w-4" />
            <span>{publicDocuments.length} public</span>
          </div>
        </div>
      </div>

      {/* Tabs */}
      <div className="flex space-x-2">
        <TabButton
          id="shared"
          label="Shared Documents"
          icon={Users}
          isActive={activeTab === 'shared'}
          onClick={() => setActiveTab('shared')}
          count={sharedDocuments.length}
        />
        <TabButton
          id="public"
          label="Public Documents"
          icon={Globe}
          isActive={activeTab === 'public'}
          onClick={() => setActiveTab('public')}
          count={publicDocuments.length}
        />
      </div>

      {/* Search and Filters */}
      <div className="flex flex-col sm:flex-row sm:items-center space-y-4 sm:space-y-0 sm:space-x-4">
        {/* Search */}
        <div className="relative flex-1 max-w-md">
          <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 h-5 w-5 text-gray-400" />
          <input
            type="text"
            placeholder="Search documents..."
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
            className="w-full pl-10 pr-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
          />
        </div>

        {/* File Type Filter */}
        {fileTypes.length > 0 && (
          <div className="flex items-center space-x-2">
            <Filter className="h-5 w-5 text-gray-400" />
            <select
              value={filterBy}
              onChange={(e) => setFilterBy(e.target.value)}
              className="px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
            >
              <option value="all">All types</option>
              {fileTypes.map(type => (
                <option key={type} value={type}>
                  {type.toUpperCase()}
                </option>
              ))}
            </select>
          </div>
        )}
      </div>

      {/* Content */}
      <div className="bg-white rounded-xl border border-gray-200 min-h-96">
        {displayDocuments.length === 0 ? (
          <div className="p-6">
            {searchQuery || filterBy !== 'all' ? (
              <EmptyState
                icon={Search}
                title="No results found"
                description={`No documents match your search criteria "${searchQuery}"`}
              />
            ) : activeTab === 'shared' ? (
              <EmptyState
                icon={Users}
                title="No shared documents"
                description="Documents shared with you by other users will appear here. Ask colleagues to share documents with you to get started."
              />
            ) : (
              <EmptyState
                icon={Globe}
                title="No public documents"
                description="Public documents that anyone can access will appear here. Check back later for new public content."
              />
            )}
          </div>
        ) : (
          <div className="p-6">
            {/* Results header */}
            <div className="flex items-center justify-between mb-6">
              <h3 className="text-lg font-medium text-gray-900">
                {displayDocuments.length} document{displayDocuments.length !== 1 ? 's' : ''}
                {searchQuery && ` matching "${searchQuery}"`}
              </h3>
              
              {/* Sort options */}
              <select className="px-3 py-2 border border-gray-300 rounded-lg text-sm focus:ring-2 focus:ring-blue-500 focus:border-transparent">
                <option value="recent">Recently shared</option>
                <option value="name">Name A-Z</option>
                <option value="owner">Owner</option>
                <option value="type">File type</option>
              </select>
            </div>

            {/* Documents grid */}
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-4">
              {displayDocuments.map(doc => (
                <div key={doc.id} className="relative">
                  <FileCard
                    document={doc}
                    onView={handleViewDocument}
                    onShare={() => {}}
                    onDelete={() => {}}
                    showActions={false}
                  />
                  
                  {/* Shared indicator */}
                  <div className="absolute top-2 right-2">
                    {activeTab === 'shared' ? (
                      <div className="bg-blue-100 text-blue-800 px-2 py-1 rounded-full text-xs font-medium flex items-center space-x-1">
                        <Users className="h-3 w-3" />
                        <span>Shared</span>
                      </div>
                    ) : (
                      <div className="bg-green-100 text-green-800 px-2 py-1 rounded-full text-xs font-medium flex items-center space-x-1">
                        <Globe className="h-3 w-3" />
                        <span>Public</span>
                      </div>
                    )}
                  </div>
                </div>
              ))}
            </div>

            {/* Load more button (if needed) */}
            {displayDocuments.length >= 20 && (
              <div className="text-center mt-8">
                <button className="px-6 py-3 bg-gray-100 hover:bg-gray-200 text-gray-700 rounded-lg transition-colors">
                  Load More
                </button>
              </div>
            )}
          </div>
        )}
      </div>

      {/* Info Card */}
      <div className="bg-blue-50 border border-blue-200 rounded-xl p-6">
        <div className="flex items-start space-x-3">
          <div className="flex-shrink-0">
            <div className="w-10 h-10 bg-blue-100 rounded-lg flex items-center justify-center">
              <Users className="h-5 w-5 text-blue-600" />
            </div>
          </div>
          <div>
            <h3 className="font-medium text-blue-900 mb-1">
              How sharing works
            </h3>
            <p className="text-blue-700 text-sm">
              Documents appear here when someone shares them directly with you or makes them public. 
              You can view and download shared documents, but editing permissions depend on what the owner allows.
            </p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default SharedWithMe;
