import React, { useState, useEffect } from 'react';
import { documentService } from '../../services/documentService';
import { folderService } from '../../services/folderService';
import FolderCard from '../common/FolderCard';
import FileCard from '../common/FileCard';
import '../../styles/components/Dashboard.css';

const Dashboard = () => {
  const [recentDocuments, setRecentDocuments] = useState([]);
  const [recentFolders, setRecentFolders] = useState([]);
  const [publicDocuments, setPublicDocuments] = useState([]);
  const [stats, setStats] = useState({
    totalDocuments: 0,
    totalFolders: 0,
    sharedDocuments: 0
  });
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchDashboardData();
  }, []);

  const fetchDashboardData = async () => {
    try {
      const [documents, folders, publicDocs] = await Promise.all([
        documentService.getMyDocuments(),
        folderService.getRootFolders(),
        documentService.getPublicDocuments()
      ]);

      // Get recent items (last 5)
      const sortedDocs = documents.sort((a, b) => new Date(b.createdAt) - new Date(a.createdAt));
      const sortedFolders = folders.sort((a, b) => new Date(b.createdAt) - new Date(a.createdAt));

      setRecentDocuments(sortedDocs.slice(0, 5));
      setRecentFolders(sortedFolders.slice(0, 5));
      setPublicDocuments(publicDocs.slice(0, 5));

      setStats({
        totalDocuments: documents.length,
        totalFolders: folders.length,
        sharedDocuments: documents.filter(doc => doc.isPublic).length
      });
    } catch (error) {
      console.error('Error fetching dashboard data:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleViewDocument = (document) => {
    // Implement view functionality
    console.log('Viewing document:', document);
  };

  const handleShareItem = (item) => {
    // Implement share functionality
    console.log('Sharing item:', item);
  };

  const handleDeleteItem = (id) => {
    // Implement delete functionality
    console.log('Deleting item:', id);
  };

  if (loading) {
    return <div className="loading">Loading dashboard...</div>;
  }

  return (
    <div className="dashboard">
      <div className="dashboard-header">
        <h1>Dashboard</h1>
        <p>Welcome back! Here's an overview of your documents and folders.</p>
      </div>

      <div className="stats-grid">
        <div className="stat-card">
          <div className="stat-icon documents">
            <svg width="24" height="24" viewBox="0 0 24 24" fill="none">
              <path d="M14,2H6A2,2 0 0,0 4,4V20A2,2 0 0,0 6,22H18A2,2 0 0,0 20,20V8L14,2Z" fill="currentColor"/>
            </svg>
          </div>
          <div className="stat-info">
            <h3>{stats.totalDocuments}</h3>
            <p>Total Documents</p>
          </div>
        </div>
        <div className="stat-card">
          <div className="stat-icon folders">
            <svg width="24" height="24" viewBox="0 0 24 24" fill="none">
              <path d="M10 4H4c-1.11 0-2 .89-2 2v12c0 1.11.89 2 2 2h16c1.11 0 2-.89 2-2V8c0-1.11-.89-2-2-2h-8l-2-2z" fill="currentColor"/>
            </svg>
          </div>
          <div className="stat-info">
            <h3>{stats.totalFolders}</h3>
            <p>Total Folders</p>
          </div>
        </div>
        <div className="stat-card">
          <div className="stat-icon shared">
            <svg width="24" height="24" viewBox="0 0 24 24" fill="none">
              <path d="M18 16.08c-.76 0-1.44.3-1.96.77L8.91 12.7c.05-.23.09-.46.09-.7s-.04-.47-.09-.7l7.05-4.11c.54.5 1.25.81 2.04.81 1.66 0 3-1.34 3-3s-1.34-3-3-3-3 1.34-3 3c0 .24.04.47.09.7L8.04 9.81C7.5 9.31 6.79 9 6 9c-1.66 0-3 1.34-3 3s1.34 3 3 3c.79 0 1.5-.31 2.04-.81l7.12 4.16c-.05.21-.08.43-.08.65 0 1.61 1.31 2.92 2.92 2.92 1.61 0 2.92-1.31 2.92-2.92s-1.31-2.92-2.92-2.92z" fill="currentColor"/>
            </svg>
          </div>
          <div className="stat-info">
            <h3>{stats.sharedDocuments}</h3>
            <p>Shared Documents</p>
          </div>
        </div>
      </div>

      <div className="dashboard-sections">
        <div className="dashboard-section">
          <div className="section-header">
            <h2>Recent Documents</h2>
            <a href="/my-drive" className="view-all-link">View All</a>
          </div>
          <div className="items-grid">
            {recentDocuments.length === 0 ? (
              <p className="empty-message">No documents yet</p>
            ) : (
              recentDocuments.map(doc => (
                <FileCard
                  key={doc.id}
                  document={doc}
                  onView={handleViewDocument}
                  onShare={handleShareItem}
                  onDelete={handleDeleteItem}
                />
              ))
            )}
          </div>
        </div>

        <div className="dashboard-section">
          <div className="section-header">
            <h2>Recent Folders</h2>
            <a href="/my-drive" className="view-all-link">View All</a>
          </div>
          <div className="items-grid">
            {recentFolders.length === 0 ? (
              <p className="empty-message">No folders yet</p>
            ) : (
              recentFolders.map(folder => (
                <FolderCard
                  key={folder.id}
                  folder={folder}
                  onOpen={() => {}}
                  onShare={handleShareItem}
                  onDelete={handleDeleteItem}
                />
              ))
            )}
          </div>
        </div>

        <div className="dashboard-section">
          <div className="section-header">
            <h2>Public Documents</h2>
            <a href="/shared-with-me" className="view-all-link">View All</a>
          </div>
          <div className="items-grid">
            {publicDocuments.length === 0 ? (
              <p className="empty-message">No public documents available</p>
            ) : (
              publicDocuments.map(doc => (
                <FileCard
                  key={doc.id}
                  document={doc}
                  onView={handleViewDocument}
                  onShare={handleShareItem}
                  onDelete={handleDeleteItem}
                  showActions={false}
                />
              ))
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
