import React, { useState, useEffect } from "react";
import Header from "./components/user/Header";
import Footer from "./components/user/Footer";
import Sidebar from "./components/user/Sidebar";
import AdminSidebar from "./components/admin/AdminSidebar";
import Dashboard from "./components/user/Dashboard";
import MyDrive from "./components/user/MyDrive";
import SharedWithMe from "./components/user/SharedWithMe";
import AdminDashboard from "./components/admin/AdminDashboard";
import "./App.css";

// Simple Settings component
const Settings = () => (
  <div className="p-6">
    <h1 className="text-3xl font-bold text-gray-800 mb-6">Settings</h1>
    <div className="bg-white rounded-xl shadow-lg p-6">
      <h2 className="text-xl font-semibold text-gray-800 mb-4">Account Settings</h2>
      <p className="text-gray-600 mb-4">Manage your account preferences and settings.</p>
    </div>
  </div>
);

// Simple login form
const LoginForm = ({ handleLogin }) => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");

  // Mock users for demo
  const USERS = [
    { email: "admin@example.com", password: "admin123", role: "ADMIN" },
    { email: "user@example.com", password: "user123", role: "USER" },
  ];

  const submitLogin = (e) => {
    e.preventDefault();
    const user = USERS.find(u => u.email === email && u.password === password);
    if (user) {
      handleLogin(user.role);
    } else {
      setError("Invalid email or password");
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-blue-50 to-indigo-100">
      <div className="bg-white p-8 rounded-xl shadow-2xl w-full max-w-md">
        <div className="text-center mb-8">
          <div
            className="w-16 h-16 rounded-xl flex items-center justify-center text-white font-bold text-2xl mx-auto mb-4"
            style={{ backgroundColor: "#1A2A80" }}
          >
            DS
          </div>
          <h1 className="text-2xl font-bold text-gray-800">Document Sharing</h1>
          <p className="text-gray-600">Sign in to your account</p>
        </div>
        <form onSubmit={submitLogin}>
          <div className="space-y-4">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">Email</label>
              <input
                type="email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                required
                className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:border-transparent"
                style={{ "--tw-ring-color": "#7A85C1" }}
                placeholder="Enter your email"
              />
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">Password</label>
              <input
                type="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required
                className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:border-transparent"
                style={{ "--tw-ring-color": "#7A85C1" }}
                placeholder="Enter your password"
              />
            </div>
          </div>
          {error && <p className="text-red-500 mt-2 text-sm">{error}</p>}
          <div className="flex space-x-2 mt-6">
            <button
              type="submit"
              className="w-full px-4 py-2 text-white rounded-lg font-medium"
              style={{ backgroundColor: "#3B38A0" }}
            >
              Sign In
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

// Layout wrapper for logged-in users
const LoggedInLayout = ({ userRole, activeSection, setActiveSection, handleLogout, children }) => (
  <div className="min-h-screen flex flex-col bg-gray-50">
    <Header />
    <div className="flex flex-1">
      {userRole === "ADMIN" ? (
        <AdminSidebar activeSection={activeSection} setActiveSection={setActiveSection} onLogout={handleLogout} />
      ) : (
        <Sidebar activeSection={activeSection} setActiveSection={setActiveSection} onLogout={handleLogout} />
      )}
      <main className="flex-1 overflow-y-auto">{children}</main>
    </div>
    <Footer />
  </div>
);

const App = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [userRole, setUserRole] = useState(null); // "USER" or "ADMIN"
  const [activeSection, setActiveSection] = useState("dashboard");

  useEffect(() => {
    const role = localStorage.getItem("role");
    if (role) {
      setIsLoggedIn(true);
      setUserRole(role);
      setActiveSection(role === "ADMIN" ? "admin-overview" : "dashboard");
    }
  }, []);

  const handleLogin = (role) => {
    setIsLoggedIn(true);
    setUserRole(role);
    localStorage.setItem("role", role);
    setActiveSection(role === "ADMIN" ? "admin-overview" : "dashboard");
  };

  const handleLogout = () => {
    setIsLoggedIn(false);
    setUserRole(null);
    localStorage.removeItem("role");
  };

  const renderMainContent = () => {
    if (userRole === "ADMIN") {
      return <AdminDashboard activeTab={activeSection.replace("admin-", "") || "overview"} />;
    } else {
      switch (activeSection) {
        case "dashboard":
          return <Dashboard />;
        case "mydrive":
          return <MyDrive />;
        case "shared":
          return <SharedWithMe />;
        case "settings":
          return <Settings />;
        default:
          return <Dashboard />;
      }
    }
  };

  if (!isLoggedIn) return <LoginForm handleLogin={handleLogin} />;

  return (
    <LoggedInLayout
      userRole={userRole}
      activeSection={activeSection}
      setActiveSection={setActiveSection}
      handleLogout={handleLogout}
    >
      {renderMainContent()}
    </LoggedInLayout>
  );
};

export default App;
