import React, { useState } from "react";
import Header from "./components/user/Header";
import Footer from "./components/user/Footer";
import AdminSidebar from "./components/admin/AdminSidebar";
import AdminDashboard from "./components/admin/AdminDashboard";
import MainLayout from "./MainLayout"; // Import your MainLayout
import LoginForm from "./components/user/LoginForm"; // Move LoginForm to a separate file if you want

const App = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(true);
  const [userRole, setUserRole] = useState("USER"); // 'USER' or 'ADMIN'
  const [activeSection, setActiveSection] = useState("dashboard");

  const handleLogout = () => {
    setIsLoggedIn(false);
    setUserRole("USER");
    setActiveSection("dashboard");
    console.log("User logged out");
  };

  const handleLogin = (role = "USER") => {
    setIsLoggedIn(true);
    setUserRole(role);
    setActiveSection(role === "ADMIN" ? "admin-overview" : "dashboard");
  };

  const handleCreateSuccess = (type, item) => {
    console.log(`${type} created/uploaded:`, item);
  };

  const renderAdminContent = () => {
    switch (activeSection) {
      case "admin-overview":
      case "admin-users":
      case "admin-documents":
      case "admin-logs":
      case "admin-settings":
        return <AdminDashboard activeTab={activeSection.replace("admin-", "")} />;
      default:
        return <AdminDashboard activeTab="overview" />;
    }
  };

  if (!isLoggedIn) {
    return <LoginForm handleLogin={handleLogin} />;
  }

  return (
    <div className="min-h-screen bg-gray-50 flex flex-col overflow-hidden">
      <Header />
      <div className="flex flex-1 relative overflow-hidden">
        {userRole === "ADMIN" ? (
          <>
            <AdminSidebar
              activeSection={activeSection}
              setActiveSection={setActiveSection}
              onLogout={handleLogout}
            />
            <main className="flex-1">{renderAdminContent()}</main>
          </>
        ) : (
          <MainLayout /> // Render MainLayout for normal users
        )}
      </div>
      <Footer />
    </div>
  );
};

export default App;
