// src/components/common/FileCard.js
import React, { useState } from 'react';

const FileCard = ({ document, onView, onShare, onDelete, showActions = true }) => {
  const [isHovered, setIsHovered] = useState(false);

  const getFileIcon = (fileType) => {
    const type = fileType?.toLowerCase();
    switch (type) {
      case 'pdf':
        return {
          icon: (
            <svg className="w-8 h-8" fill="currentColor" viewBox="0 0 24 24">
              <path d="M14,2H6A2,2 0 0,0 4,4V20A2,2 0 0,0 6,22H18A2,2 0 0,0 20,20V8L14,2M18,20H6V4H13V9H18V20Z" />
            </svg>
          ),
          gradient: 'from-red-400 to-red-600',
          bgColor: 'bg-red-100'
        };
      case 'doc':
      case 'docx':
        return {
          icon: (
            <svg className="w-8 h-8" fill="currentColor" viewBox="0 0 24 24">
              <path d="M14,2H6A2,2 0 0,0 4,4V20A2,2 0 0,0 6,22H18A2,2 0 0,0 20,20V8L14,2M18,20H6V4H13V9H18V20Z" />
            </svg>
          ),
          gradient: 'from-blue-400 to-blue-600',
          bgColor: 'bg-blue-100'
        };
      case 'xls':
      case 'xlsx':
        return {
          icon: (
            <svg className="w-8 h-8" fill="currentColor" viewBox="0 0 24 24">
              <path d="M14,2H6A2,2 0 0,0 4,4V20A2,2 0 0,0 6,22H18A2,2 0 0,0 20,20V8L14,2M18,20H6V4H13V9H18V20Z" />
            </svg>
          ),
          gradient: 'from-green-400 to-green-600',
          bgColor: 'bg-green-100'
        };
      case 'ppt':
      case 'pptx':
        return {
          icon: (
            <svg className="w-8 h-8" fill="currentColor" viewBox="0 0 24 24">
              <path d="M14,2H6A2,2 0 0,0 4,4V20A2,2 0 0,0 6,22H18A2,2 0 0,0 20,20V8L14,2M18,20H6V4H13V9H18V20Z" />
            </svg>
          ),
          gradient: 'from-orange-400 to-orange-600',
          bgColor: 'bg-orange-100'
        };
      default:
        return {
          icon: (
            <svg className="w-8 h-8" fill="currentColor" viewBox="0 0 24 24">
              <path d="M14,2H6A2,2 0 0,0 4,4V20A2,2 0 0,0 6,22H18A2,2 0 0,0 20,20V8L14,2M18,20H6V4H13V9H18V20Z" />
            </svg>
          ),
          gradient: 'from-gray-400 to-gray-600',
          bgColor: 'bg-gray-100'
        };
    }
  };

  const fileConfig = getFileIcon(document.fileType);

  const handleView = (e) => {
    e.stopPropagation();
    onView(document);
  };

  const handleShare = (e) => {
    e.stopPropagation();
    onShare(document);
  };

  const handleDelete = (e) => {
    e.stopPropagation();
    onDelete(document.id);
  };

  return (
    <div 
      className="card p-6 cursor-pointer transform hover:scale-105 hover:-translate-y-1 relative group"
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
      onClick={handleView}
    >
      {/* File Icon */}
      <div className="flex flex-col items-center space-y-4">
        <div className="relative">
          <div className={`p-4 bg-gradient-to-br ${fileConfig.gradient} rounded-2xl shadow-lg text-white`}>
            {fileConfig.icon}
          </div>
          {/* File type badge */}
          <div className={`absolute -top-2 -right-2 px-2 py-1 ${fileConfig.bgColor} text-xs font-semibold rounded-full shadow-md`}>
            <span className={`bg-gradient-to-r ${fileConfig.gradient} bg-clip-text text-transparent`}>
              {document.fileType?.toUpperCase()}
            </span>
          </div>
        </div>

        {/* File Info */}
        <div className="text-center w-full space-y-2">
          <h3 className="font-semibold text-gray-900 text-lg mb-1 truncate" title={document.title}>
            {document.title}
          </h3>
          <p className="text-sm text-gray-600 truncate" title={document.fileName}>
            {document.fileName}
          </p>
          <div className="space-y-1">
            <p className="text-xs text-gray-500">
              Uploaded {new Date(document.createdAt).toLocaleDateString('en-US', {
                month: 'short',
                day: 'numeric',
                year: 'numeric'
              })}
            </p>
            {document.owner && (
              <p className="text-xs text-gray-500">
                By: <span className="font-medium">{document.owner.username}</span>
              </p>
            )}
          </div>
        </div>

        {/* Action Buttons */}
        {showActions && (
          <div className={`flex space-x-2 transition-all duration-300 ${isHovered ? 'opacity-100 translate-y-0' : 'opacity-0 translate-y-2'}`}>
            <button
              onClick={handleView}
              className="p-2 bg-green-100 hover:bg-green-200 text-green-600 rounded-lg transition-colors duration-200 group/btn"
              title="View document"
            >
              <svg className="w-4 h-4 transform group-hover/btn:scale-110 transition-transform" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 12a3 3 0 11-6 0 3 3 0 016 0z" />
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M2.458 12C3.732 7.943 7.523 5 12 5c4.478 0 8.268 2.943 9.542 7-1.274 4.057-5.064 7-9.542 7-4.477 0-8.268-2.943-9.542-7z" />
              </svg>
            </button>
            <button
              onClick={handleShare}
              className="p-2 bg-blue-100 hover:bg-blue-200 text-blue-600 rounded-lg transition-colors duration-200 group/btn"
              title="Share document"
            >
              <svg className="w-4 h-4 transform group-hover/btn:scale-110 transition-transform" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M8.684 13.342C8.886 12.938 9 12.482 9 12c0-.482-.114-.938-.316-1.342m0 2.684a3 3 0 110-2.684m0 2.684l6.632 3.316m-6.632-6l6.632-3.316m0 0a3 3 0 105.367-2.684 3 3 0 00-5.367 2.684zm0 9.316a3 3 0 105.367 2.684 3 3 0 00-5.367-2.684z" />
              </svg>
            </button>
            <button
              onClick={handleDelete}
              className="p-2 bg-red-100 hover:bg-red-200 text-red-600 rounded-lg transition-colors duration-200 group/btn"
              title="Delete document"
            >
              <svg className="w-4 h-4 transform group-hover/btn:scale-110 transition-transform" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 7l-.867 12.142A2 2 0 0116.138 21H7.862a2 2 0 01-1.995-1.858L5 7m5 4v6m4-6v6m1-10V4a1 1 0 00-1-1h-4a1 1 0 00-1 1v3M4 7h16" />
              </svg>
            </button>
          </div>
        )}
      </div>

      {/* Hover overlay */}
      <div className={`absolute inset-0 bg-gradient-to-br from-blue-50 to-indigo-50 rounded-xl opacity-0 transition-opacity duration-300 ${isHovered ? 'opacity-50' : ''}`}></div>

      {/* Public indicator */}
      {document.isPublic && (
        <div className="absolute top-3 right-3 p-1 bg-green-100 text-green-600 rounded-full">
          <svg className="w-3 h-3" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M3.055 11H5a2 2 0 012 2v1a2 2 0 002 2 2 2 0 012 2v2.945M8 3.935V5.5A2.5 2.5 0 0010.5 8h.5a2 2 0 012 2 2 2 0 104 0 2 2 0 012-2h1.064M15 20.488V18a2 2 0 012-2h3.064M21 12a9 9 0 11-18 0 9 9 0 0118 0z" />
          </svg>
        </div>
      )}
    </div>
  );
};

export default FileCard;
