import { useState, useEffect } from "react"
import { useNavigate } from "react-router-dom"
import { authAPI, folderAPI, documentAPI, fileOperations } from "../../lib/api"
import "../styles/CreateFolder.css"

export default function UserDashboard() {
  const navigate = useNavigate()
  const [user, setUser] = useState(null)
  const [folders, setFolders] = useState([])
  const [documents, setDocuments] = useState([])
  const [sharedFolders, setSharedFolders] = useState([])
  const [loading, setLoading] = useState(true)
  const [showNewDialog, setShowNewDialog] = useState(false)

  useEffect(() => {
    if (!authAPI.isAuthenticated()) {
      navigate("/login")
      return
    }

    loadUserData()
  }, [navigate])

  const loadUserData = async () => {
    try {
      const currentUser = authAPI.getCurrentUser()
      setUser(currentUser)

      const [userFolders, userDocs, shared] = await Promise.all([
        folderAPI.getUserFolders(),
        documentAPI.getUserDocuments(),
        folderAPI.getSharedFolders(),
      ])

      setFolders(userFolders)
      setDocuments(userDocs)
      setSharedFolders(shared)
    } catch (error) {
      console.error("Failed to load user data:", error)
    } finally {
      setLoading(false)
    }
  }

  const handleCreateFolder = async (name) => {
    try {
      await fileOperations.createNewFolder(name)
      setShowNewDialog(false)
      loadUserData()
    } catch (error) {
      console.error("Failed to create folder:", error)
    }
  }

  const handleFileUpload = async (file) => {
    try {
      await fileOperations.uploadFileToFolder(file)
      loadUserData()
    } catch (error) {
      console.error("Failed to upload file:", error)
    }
  }

  const handleLogout = () => {
    authAPI.logout()
    navigate("/login")
  }

  if (loading) {
    return <div className="loading">Loading...</div>
  }

  return (
    <div className="dashboard">
      <header className="dashboard-header">
        <div className="header-left">
          <h1>My Drive</h1>
          <button className="new-button" onClick={() => setShowNewDialog(true)}>
            + New
          </button>
        </div>
        <div className="header-right">
          <span>Welcome, {user?.username}</span>
          <button onClick={handleLogout} className="logout-button">
            Logout
          </button>
        </div>
      </header>

      <main className="dashboard-content">
        <section className="folders-section">
          <h2>My Folders</h2>
          <div className="items-grid">
            {folders.map((folder) => (
              <div key={folder.id} className="item-card folder-card">
                <div className="folder-icon">📁</div>
                <span className="item-name">{folder.name}</span>
              </div>
            ))}
          </div>
        </section>

        <section className="documents-section">
          <h2>My Documents</h2>
          <div className="items-grid">
            {documents.map((doc) => (
              <div key={doc.id} className="item-card document-card">
                <div className="document-icon">📄</div>
                <span className="item-name">{doc.name}</span>
              </div>
            ))}
          </div>
        </section>

        <section className="shared-section">
          <h2>Shared with Me</h2>
          <div className="items-grid">
            {sharedFolders.map((folder) => (
              <div key={folder.id} className="item-card shared-card">
                <div className="shared-icon">👥</div>
                <span className="item-name">{folder.name}</span>
                <span className="owner-name">by {folder.owner.username}</span>
              </div>
            ))}
          </div>
        </section>
      </main>

      {showNewDialog && (
        <NewItemDialog
          onCreateFolder={handleCreateFolder}
          onUploadFile={handleFileUpload}
          onClose={() => setShowNewDialog(false)}
        />
      )}
    </div>
  )
}

function NewItemDialog({ onCreateFolder, onUploadFile, onClose }) {
  const [folderName, setFolderName] = useState("")

  const handleFolderSubmit = (e) => {
    e.preventDefault()
    if (folderName.trim()) {
      onCreateFolder(folderName.trim())
      setFolderName("")
    }
  }

  const handleFileChange = (e) => {
    const file = e.target.files[0]
    if (file) {
      onUploadFile(file)
    }
  }

  return (
    <div className="dialog-overlay" onClick={onClose}>
      <div className="dialog" onClick={(e) => e.stopPropagation()}>
        <h3>Create New</h3>

        <form onSubmit={handleFolderSubmit} className="dialog-form">
          <input
            type="text"
            placeholder="Folder name"
            value={folderName}
            onChange={(e) => setFolderName(e.target.value)}
            className="dialog-input"
          />
          <button type="submit" className="dialog-button">
            Create Folder
          </button>
        </form>

        <div className="dialog-divider">or</div>

        <label className="file-upload-label">
          <input type="file" onChange={handleFileChange} className="file-input" />
          Upload File
        </label>

        <button onClick={onClose} className="dialog-close">
          Cancel
        </button>
      </div>
    </div>
  )
}
