// Check if user is authenticated
const isAuthenticated = () => {
  const token = localStorage.getItem('token');
  return !!token;
};

// Get user role from localStorage
const getUserRole = () => {
  const user = localStorage.getItem('user');
  if (user) {
    try {
      const userData = JSON.parse(user);
      return userData.role;
    } catch (error) {
      console.error('Error parsing user data:', error);
      return null;
    }
  }
  return null;
};

// Main Dashboard Layout Component
const DashboardLayout = ({ children, userRole }) => {
  const [activeSection, setActiveSection] = useState(() => {
    return userRole === "ADMIN" ? "admin-overview" : "dashboard";
  });
  const location = useLocation();

  // Update active section based on current route
  useEffect(() => {
    const path = location.pathname;
    if (userRole === "ADMIN") {
      if (path.includes('users')) setActiveSection('admin-users');
      else if (path.includes('documents')) setActiveSection('admin-documents');
      else if (path.includes('logs')) setActiveSection('admin-logs');
      else if (path.includes('settings')) setActiveSection('admin-settings');
      else setActiveSection('admin-overview');
    } else {
      if (path.includes('mydrive')) setActiveSection('mydrive');
      else if (path.includes('shared')) setActiveSection('shared');
      else if (path.includes('settings')) setActiveSection('settings');
      else setActiveSection('dashboard');
    }
  }, [location.pathname, userRole]);

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    window.location.href = '/login';
  };

  const handleSectionChange = (section) => {
    setActiveSection(section);
    // Navigate to appropriate route based on section
    if (userRole === "ADMIN") {
      const routeMap = {
        'admin-overview': '/admin-dashboard',
        'admin-users': '/admin-dashboard/users',
        'admin-documents': '/admin-dashboard/documents',
        'admin-logs': '/admin-dashboard/logs',
        'admin-settings': '/admin-dashboard/settings'
      };
      window.history.pushState(null, '', routeMap[section] || '/admin-dashboard');
    } else {
      const routeMap = {
        'dashboard': '/user-dashboard',
        'mydrive': '/user-dashboard/mydrive',
        'shared': '/user-dashboard/shared',
        'settings': '/user-dashboard/settings'
      };
      window.history.pushState(null, '', routeMap[section] || '/user-dashboard');
    }
  };

  return (
    <div className="min-h-screen bg-gray-50 flex flex-col">
      <Header />
      <div className="flex flex-1">
        {userRole === "ADMIN" ? (
          <AdminSidebar
            activeSection={activeSection}
            setActiveSection={handleSectionChange}
            onLogout={handleLogout}
          />
        ) : (
          <Sidebar
            activeSection={activeSection}
            setActiveSection={handleSectionChange}
            onLogout={handleLogout}
          />
        )}
        <main className="flex-1 overflow-y-auto">
          {children}
        </main>
      </div>
      <Footer />
    </div>
  );
};

// User Dashboard Routes Component
const UserDashboardRoutes = () => {
  const userRole = getUserRole();
  
  return (
    <DashboardLayout userRole={userRole}>
      <Routes>
        <Route path="/" element={<Dashboard />} />
        <Route path="/mydrive" element={<MyDrive />} />
        <Route path="/shared" element={<SharedWithMe />} />
        <Route path="/settings" element={<Settings />} />
        <Route path="*" element={<Navigate to="/user-dashboard" replace />} />
      </Routes>
    </DashboardLayout>
  );
};

// Admin Dashboard Routes Component
const AdminDashboardRoutes = () => {
  const userRole = getUserRole();
  
  return (
    <DashboardLayout userRole={userRole}>
      <Routes>
        <Route path="/" element={<AdminDashboard activeTab="overview" />} />
        <Route path="/users" element={<AdminDashboard activeTab="users" />} />
        <Route path="/documents" element={<AdminDashboard activeTab="documents" />} />
        <Route path="/logs" element={<AdminDashboard activeTab="logs" />} />
        <Route path="/settings" element={<AdminDashboard activeTab="settings" />} />
        <Route path="*" element={<Navigate to="/admin-dashboard" replace />} />
      </Routes>
    </DashboardLayout>
  );
};

function App() {
  return (
    <Router>
      <div className="App">
        <Routes>
          {/* Public Routes */}
          <Route 
            path="/login" 
            element={
              !isAuthenticated() ? (
                <Login />
              ) : (
                <Navigate to={getUserRole() === 'ADMIN' ? '/admin-dashboard' : '/user-dashboard'} replace />
              )
            } 
          />
          
          <Route 
            path="/register" 
            element={
              !isAuthenticated() ? (
                <Register />
              ) : (
                <Navigate to={getUserRole() === 'ADMIN' ? '/admin-dashboard' : '/user-dashboard'} replace />
              )
            } 
          />

          {/* Protected Admin Routes */}
          <Route 
            path="/admin-dashboard/*" 
            element={
              <ProtectedRoute requiredRole="ADMIN">
                <AdminDashboardRoutes />
              </ProtectedRoute>
            } 
          />

          {/* Protected User Routes */}
          <Route 
            path="/user-dashboard/*" 
            element={
              <ProtectedRoute requiredRole="USER">
                <UserDashboardRoutes />
              </ProtectedRoute>
            } 
          />

          {/* Default Route */}
          <Route 
            path="/" 
            element={
              isAuthenticated() ? (
                <Navigate to={getUserRole() === 'ADMIN' ? '/admin-dashboard' : '/user-dashboard'} replace />
              ) : (
                <Navigate to="/login" replace />
              )
            } 
          />

          {/* Catch all - redirect to login */}
          <Route path="*" element={<Navigate to="/login" replace />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
