import React from 'react';
import { NavLink } from 'react-router-dom';
import { LayoutDashboard, FolderOpen, Users, Star, Trash2, Cloud } from 'lucide-react';

const Sidebar = () => {
  const menuItems = [
    {
      path: '/dashboard',
      icon: LayoutDashboard,
      label: 'Dashboard',
      color: 'text-blue-600'
    },
    {
      path: '/my-drive',
      icon: FolderOpen,
      label: 'My Drive',
      color: 'text-green-600'
    },
    {
      path: '/shared-with-me',
      icon: Users,
      label: 'Shared with Me',
      color: 'text-purple-600'
    }
  ];

  const secondaryItems = [
    {
      path: '/starred',
      icon: Star,
      label: 'Starred',
      color: 'text-yellow-500'
    },
    {
      path: '/recent',
      icon: Cloud,
      label: 'Recent',
      color: 'text-indigo-600'
    },
    {
      path: '/trash',
      icon: Trash2,
      label: 'Trash',
      color: 'text-red-500'
    }
  ];

  return (
    <aside className="fixed left-0 top-16 h-[calc(100vh-4rem)] w-64 bg-white border-r border-gray-200 overflow-y-auto">
      <div className="p-4">
        {/* Primary Navigation */}
        <nav className="space-y-2">
          {menuItems.map((item) => {
            const Icon = item.icon;
            return (
              <NavLink
                key={item.path}
                to={item.path}
                className={({ isActive }) =>
                  `flex items-center space-x-3 px-3 py-2.5 rounded-lg text-sm font-medium transition-all duration-200 ${
                    isActive
                      ? 'bg-blue-50 text-blue-700 border-r-2 border-blue-600'
                      : 'text-gray-600 hover:bg-gray-50 hover:text-gray-900'
                  }`
                }
              >
                {({ isActive }) => (
                  <>
                    <Icon 
                      className={`h-5 w-5 ${
                        isActive ? 'text-blue-600' : 'text-gray-400'
                      }`} 
                    />
                    <span>{item.label}</span>
                  </>
                )}
              </NavLink>
            );
          })}
        </nav>

        {/* Divider */}
        <div className="my-6 border-t border-gray-200"></div>

        {/* Secondary Navigation */}
        <div>
          <h3 className="px-3 text-xs font-semibold text-gray-500 uppercase tracking-wider mb-3">
            Quick Access
          </h3>
          <nav className="space-y-1">
            {secondaryItems.map((item) => {
              const Icon = item.icon;
              return (
                <NavLink
                  key={item.path}
                  to={item.path}
                  className={({ isActive }) =>
                    `flex items-center space-x-3 px-3 py-2 rounded-lg text-sm transition-all duration-200 ${
                      isActive
                        ? 'bg-gray-100 text-gray-900'
                        : 'text-gray-600 hover:bg-gray-50 hover:text-gray-900'
                    }`
                  }
                >
                  <Icon className={`h-4 w-4 ${item.color}`} />
                  <span>{item.label}</span>
                </NavLink>
              );
            })}
          </nav>
        </div>

        {/* Storage Info */}
        <div className="mt-8 p-4 bg-gradient-to-br from-blue-50 to-indigo-50 rounded-lg border border-blue-100">
          <div className="flex items-center space-x-2 mb-2">
            <Cloud className="h-4 w-4 text-blue-600" />
            <span className="text-sm font-medium text-blue-900">Storage</span>
          </div>
          <div className="space-y-2">
            <div className="flex justify-between text-xs text-blue-800">
              <span>Used</span>
              <span>2.4 GB of 15 GB</span>
            </div>
            <div className="w-full bg-blue-200 rounded-full h-2">
              <div className="bg-blue-600 h-2 rounded-full" style={{ width: '16%' }}></div>
            </div>
            <button className="w-full text-xs text-blue-700 hover:text-blue-800 font-medium">
              Upgrade storage
            </button>
          </div>
        </div>
      </div>
    </aside>
  );
};

export default Sidebar;
