import React, { useState } from 'react';
import { Search, Bell, Settings, LogOut, User, ChevronDown, Zap, Crown, Sparkles } from 'lucide-react';

const Header = ({ currentUser, logout }) => {
  const [showDropdown, setShowDropdown] = useState(false);
  const [searchQuery, setSearchQuery] = useState('');

  const handleLogout = () => {
    logout();
    setShowDropdown(false);
  };

  return (
    <header className="h-20 sticky top-0 z-50" style={{
      background: 'rgba(255, 255, 255, 0.95)',
      backdropFilter: 'blur(20px)',
      borderBottom: '1px solid #F3F4F6'
    }}>
      <div className="flex items-center justify-between h-full px-8 max-w-[1400px] mx-auto">
        {/* Logo Section */}
        <div className="flex items-center space-x-4 min-w-0">
          <div className="flex items-center space-x-4">
            <div 
              className="w-12 h-12 rounded-2xl flex items-center justify-center shadow-lg transition-all duration-300 hover:shadow-xl hover:scale-105"
              style={{
                background: 'linear-gradient(135deg, #1A2A80 0%, #3B38A0 100%)'
              }}
            >
              <svg className="w-7 h-7 text-white" fill="currentColor" viewBox="0 0 24 24">
                <path d="M10 4H4c-1.11 0-2 .89-2 2v12c0 1.11.89 2 2 2h16c1.11 0 2-.89 2-2V8c0-1.11-.89-2-2-2h-8l-2-2z"/>
              </svg>
            </div>
            <div className="hidden sm:block">
              <h1 className="text-2xl font-bold" style={{
                background: 'linear-gradient(135deg, #1A2A80 0%, #3B38A0 100%)',
                WebkitBackgroundClip: 'text',
                WebkitTextFillColor: 'transparent',
                backgroundClip: 'text'
              }}>
                DocShare
              </h1>
              <p className="text-xs font-medium" style={{ color: '#7A85C1' }}>
                Document Management
              </p>
            </div>
          </div>
        </div>

        {/* Search Bar */}
        <div className="flex-1 max-w-2xl mx-8 hidden md:block">
          <div className="relative group">
            <Search 
              className="absolute left-4 top-1/2 transform -translate-y-1/2 h-5 w-5 transition-colors group-hover:text-[#3B38A0]" 
              style={{ color: '#7A85C1' }}
            />
            <input
              type="text"
              placeholder="Search documents, folders, and more..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              className="w-full pl-12 pr-6 py-4 rounded-2xl border border-gray-200 focus:ring-2 focus:border-transparent transition-all duration-300 shadow-lg hover:shadow-xl text-gray-700 placeholder-gray-400"
              style={{
                backgroundColor: 'rgba(255, 255, 255, 0.8)',
                backdropFilter: 'blur(20px)',
                focusRingColor: 'rgba(122, 133, 193, 0.3)'
              }}
              onFocus={(e) => {
                e.target.style.backgroundColor = 'white';
                e.target.style.borderColor = '#7A85C1';
                e.target.style.boxShadow = '0 0 0 3px rgba(122, 133, 193, 0.1)';
              }}
              onBlur={(e) => {
                e.target.style.backgroundColor = 'rgba(255, 255, 255, 0.8)';
                e.target.style.borderColor = '#E5E7EB';
                e.target.style.boxShadow = '0 10px 15px -3px rgb(0 0 0 / 0.1), 0 4px 6px -4px rgb(0 0 0 / 0.1)';
              }}
            />
            <div className="absolute right-4 top-1/2 transform -translate-y-1/2">
              <kbd className="hidden lg:inline-block px-2 py-1 text-xs font-semibold rounded-lg border" style={{
                color: '#7A85C1',
                backgroundColor: 'rgba(178, 176, 232, 0.2)',
                borderColor: 'rgba(178, 176, 232, 0.3)'
              }}>
                âŒ˜K
              </kbd>
            </div>
          </div>
        </div>

        {/* Right Side Actions */}
        <div className="flex items-center space-x-3">
          {/* Mobile Search */}
          <button className="md:hidden p-3 rounded-xl transition-all duration-300 hover:scale-105" style={{
            color: '#7A85C1'
          }} onMouseEnter={(e) => {
            e.target.style.color = '#3B38A0';
            e.target.style.backgroundColor = 'rgba(178, 176, 232, 0.2)';
          }} onMouseLeave={(e) => {
            e.target.style.color = '#7A85C1';
            e.target.style.backgroundColor = 'transparent';
          }}>
            <Search className="h-5 w-5" />
          </button>

          {/* Notifications */}
          <div className="relative">
            <button className="p-3 rounded-xl transition-all duration-300 hover:scale-105 relative group" style={{
              color: '#7A85C1'
            }} onMouseEnter={(e) => {
              e.target.style.color = '#3B38A0';
              e.target.style.backgroundColor = 'rgba(178, 176, 232, 0.2)';
            }} onMouseLeave={(e) => {
              e.target.style.color = '#7A85C1';
              e.target.style.backgroundColor = 'transparent';
            }}>
              <Bell className="h-5 w-5" />
              <span className="absolute -top-1 -right-1 w-5 h-5 text-white text-xs rounded-full flex items-center justify-center font-bold shadow-lg animate-pulse" style={{
                background: 'linear-gradient(135deg, #EF4444 0%, #DC2626 100%)'
              }}>
                3
              </span>
            </button>
          </div>

          {/* Settings */}
          <button className="p-3 rounded-xl transition-all duration-300 hover:scale-105" style={{
            color: '#7A85C1'
          }} onMouseEnter={(e) => {
            e.target.style.color = '#3B38A0';
            e.target.style.backgroundColor = 'rgba(178, 176, 232, 0.2)';
          }} onMouseLeave={(e) => {
            e.target.style.color = '#7A85C1';
            e.target.style.backgroundColor = 'transparent';
          }}>
            <Settings className="h-5 w-5" />
          </button>

          {/* Profile Dropdown */}
          <div className="relative">
            <button
              onClick={() => setShowDropdown(!showDropdown)}
              className="flex items-center space-x-3 p-2 rounded-2xl transition-all duration-300 hover:scale-105 group"
              style={{
                backgroundColor: showDropdown ? 'rgba(178, 176, 232, 0.2)' : 'transparent'
              }}
              onMouseEnter={(e) => {
                if (!showDropdown) {
                  e.target.style.backgroundColor = 'rgba(178, 176, 232, 0.2)';
                }
              }}
              onMouseLeave={(e) => {
                if (!showDropdown) {
                  e.target.style.backgroundColor = 'transparent';
                }
              }}
            >
              <div className="relative">
                <div 
                  className="w-11 h-11 rounded-2xl flex items-center justify-center shadow-lg group-hover:shadow-xl transition-all duration-300"
                  style={{
                    background: 'linear-gradient(135deg, #3B38A0 0%, #7A85C1 100%)'
                  }}
                >
                  <span className="text-white font-bold text-lg">
                    {currentUser?.username?.[0]?.toUpperCase() || 'U'}
                  </span>
                </div>
                <div className="absolute -bottom-1 -right-1 w-4 h-4 bg-green-500 border-2 border-white rounded-full animate-pulse"></div>
              </div>
              <div className="hidden lg:block text-left">
                <div className="flex items-center space-x-2">
                  <p className="text-sm font-semibold" style={{ color: '#1A2A80' }}>
                    {currentUser?.username || 'User'}
                  </p>
                  <Crown className="h-4 w-4" style={{ color: '#7A85C1' }} />
                </div>
                <p className="text-xs font-medium flex items-center space-x-1" style={{ color: '#7A85C1' }}>
                  <Sparkles className="h-3 w-3" />
                  <span>Premium User</span>
                </p>
              </div>
              <ChevronDown 
                className={`h-4 w-4 transition-all duration-300 hidden lg:block ${showDropdown ? 'rotate-180' : ''}`}
                style={{ color: '#7A85C1' }}
              />
            </button>

            {/* Dropdown Menu */}
            {showDropdown && (
              <>
                <div 
                  className="fixed inset-0 z-40"
                  onClick={() => setShowDropdown(false)}
                />
                <div className="absolute right-0 mt-3 w-80 rounded-2xl shadow-2xl border py-2 z-50 animate-in fade-in slide-in-from-top-5 duration-200" style={{
                  background: 'rgba(255, 255, 255, 0.95)',
                  backdropFilter: 'blur(20px)',
                  borderColor: 'rgba(255, 255, 255, 0.2)'
                }}>
                  {/* User Info Section */}
                  <div className="px-6 py-4 border-b" style={{ borderColor: 'rgba(178, 176, 232, 0.2)' }}>
                    <div className="flex items-center space-x-4">
                      <div 
                        className="w-12 h-12 rounded-2xl flex items-center justify-center shadow-lg"
                        style={{
                          background: 'linear-gradient(135deg, #3B38A0 0%, #7A85C1 100%)'
                        }}
                      >
                        <span className="text-white font-bold text-lg">
                          {currentUser?.username?.[0]?.toUpperCase() || 'U'}
                        </span>
                      </div>
                      <div>
                        <p className="text-sm font-semibold" style={{ color: '#1A2A80' }}>
                          {currentUser?.username || 'User'}
                        </p>
                        <p className="text-xs" style={{ color: '#7A85C1' }}>
                          ID: {currentUser?.id || 'N/A'}
                        </p>
                        <span className="inline-flex items-center px-2 py-1 rounded-full text-xs font-medium mt-1" style={{
                          background: 'linear-gradient(135deg, rgba(16, 185, 129, 0.1) 0%, rgba(5, 150, 105, 0.2) 100%)',
                          color: '#059669'
                        }}>
                          <div className="w-2 h-2 bg-green-500 rounded-full mr-2 animate-pulse"></div>
                          Online
                        </span>
                      </div>
                    </div>
                  </div>
                  
                  {/* Menu Items */}
                  <div className="py-2">
                    <button className="w-full flex items-center px-6 py-3 text-sm text-gray-700 hover:text-[#3B38A0] transition-all duration-200 group" onMouseEnter={(e) => {
                      e.target.style.backgroundColor = 'rgba(178, 176, 232, 0.1)';
                    }} onMouseLeave={(e) => {
                      e.target.style.backgroundColor = 'transparent';
                    }}>
                      <div className="w-10 h-10 rounded-xl flex items-center justify-center mr-3 group-hover:scale-110 transition-all duration-200" style={{
                        backgroundColor: 'rgba(178, 176, 232, 0.2)'
                      }}>
                        <User className="h-5 w-5" style={{ color: '#7A85C1' }} />
                      </div>
                      <div className="text-left">
                        <div className="font-medium">Profile Settings</div>
                        <div className="text-xs text-gray-500">Manage your account</div>
                      </div>
                    </button>
                    
                    <button className="w-full flex items-center px-6 py-3 text-sm text-gray-700 hover:text-[#3B38A0] transition-all duration-200 group" onMouseEnter={(e) => {
                      e.target.style.backgroundColor = 'rgba(178, 176, 232, 0.1)';
                    }} onMouseLeave={(e) => {
                      e.target.style.backgroundColor = 'transparent';
                    }}>
                      <div className="w-10 h-10 rounded-xl flex items-center justify-center mr-3 group-hover:scale-110 transition-all duration-200" style={{
                        backgroundColor: 'rgba(178, 176, 232, 0.2)'
                      }}>
                        <Settings className="h-5 w-5" style={{ color: '#7A85C1' }} />
                      </div>
                      <div className="text-left">
                        <div className="font-medium">Preferences</div>
                        <div className="text-xs text-gray-500">Customize experience</div>
                      </div>
                    </button>

                    <button className="w-full flex items-center px-6 py-3 text-sm text-gray-700 hover:text-[#3B38A0] transition-all duration-200 group" onMouseEnter={(e) => {
                      e.target.style.backgroundColor = 'rgba(178, 176, 232, 0.1)';
                    }} onMouseLeave={(e) => {
                      e.target.style.backgroundColor = 'transparent';
                    }}>
                      <div className="w-10 h-10 rounded-xl flex items-center justify-center mr-3 group-hover:scale-110 transition-all duration-200" style={{
                        backgroundColor: 'rgba(178, 176, 232, 0.2)'
                      }}>
                        <Zap className="h-5 w-5" style={{ color: '#7A85C1' }} />
                      </div>
                      <div className="text-left">
                        <div className="font-medium">Upgrade to Pro</div>
                        <div className="text-xs text-gray-500">Unlock premium features</div>
                      </div>
                    </button>
                  </div>
                  
                  <div className="border-t pt-2" style={{ borderColor: 'rgba(178, 176, 232, 0.2)' }}>
                    <button
                      onClick={handleLogout}
                      className="w-full flex items-center px-6 py-3 text-sm text-red-600 hover:text-red-700 hover:bg-red-50 transition-all duration-200 group"
                    >
                      <div className="w-10 h-10 bg-red-100 rounded-xl flex items-center justify-center mr-3 group-hover:bg-red-200 group-hover:scale-110 transition-all duration-200">
                        <LogOut className="h-5 w-5 text-red-600" />
                      </div>
                      <div className="text-left">
                        <div className="font-medium">Sign Out</div>
                        <div className="text-xs text-red-400">End your session</div>
                      </div>
                    </button>
                  </div>
                </div>
              </>
            )}
          </div>
        </div>
      </div>
    </header>
  );
};

// Mock props for demo
const mockProps = {
  currentUser: {
    username: 'johndoe',
    id: '12345'
  },
  logout: () => console.log('Logout clicked')
};

export default () => <Header {...mockProps} />;
