import React, { useState } from 'react';
import { 
  Upload, 
  File, 
  Share2, 
  Folder, 
  TrendingUp, 
  Users, 
  Activity,
  Plus,
  FolderPlus,
  Eye,
  Download,
  MoreVertical
} from 'lucide-react';

const Dashboard = () => {
  const [uploadProgress, setUploadProgress] = useState(0);
  const [isUploading, setIsUploading] = useState(false);

  // Mock data
  const stats = [
    { title: 'Total Documents', value: '24', icon: File, color: 'from-[#1A2A80] to-[#3B38A0]', change: '+12%' },
    { title: 'Shared Files', value: '8', icon: Share2, color: 'from-[#3B38A0] to-[#7A85C1]', change: '+5%' },
    { title: 'Folders', value: '6', icon: Folder, color: 'from-[#7A85C1] to-[#B2B0E8]', change: '+2%' },
    { title: 'Storage Used', value: '2.4 GB', icon: TrendingUp, color: 'from-[#B2B0E8] to-[#1A2A80]', change: '+8%' }
  ];

  const recentDocuments = [
    { id: 1, name: 'Project Proposal.pdf', type: 'pdf', size: '2.4 MB', lastModified: '2 hours ago', shared: false },
    { id: 2, name: 'Design Mockups.jpg', type: 'image', size: '5.1 MB', lastModified: '1 day ago', shared: true },
    { id: 3, name: 'Meeting Notes.docx', type: 'document', size: '1.2 MB', lastModified: '2 days ago', shared: true },
    { id: 4, name: 'Budget Sheet.xlsx', type: 'spreadsheet', size: '800 KB', lastModified: '3 days ago', shared: false }
  ];

  const recentActivity = [
    { action: 'uploaded', file: 'Project Proposal.pdf', time: '2 hours ago' },
    { action: 'shared', file: 'Design Mockups.jpg', time: '1 day ago' },
    { action: 'created folder', file: 'Marketing Materials', time: '2 days ago' },
    { action: 'downloaded', file: 'Meeting Notes.docx', time: '3 days ago' }
  ];

  const handleFileUpload = (event) => {
    const files = event.target.files;
    if (files && files.length > 0) {
      setIsUploading(true);
      setUploadProgress(0);
      
      const interval = setInterval(() => {
        setUploadProgress(prev => {
          if (prev >= 100) {
            clearInterval(interval);
            setIsUploading(false);
            return 0;
          }
          return prev + 10;
        });
      }, 200);
    }
  };

  const getFileIcon = (type) => {
    switch (type) {
      case 'pdf': return 'üìÑ';
      case 'image': return 'üñºÔ∏è';
      case 'document': return 'üìù';
      case 'spreadsheet': return 'üìä';
      default: return 'üìÅ';
    }
  };

  return (
    <div className="space-y-8">
      {/* Welcome Section */}
      <div className="bg-gradient-to-r from-[#1A2A80] to-[#7A85C1] rounded-3xl p-8 text-white shadow-2xl">
        <h1 className="text-3xl font-bold mb-2">Welcome back!</h1>
        <p className="text-white/80 mb-6">Manage your documents efficiently and securely</p>
        
        <div className="flex flex-wrap gap-4">
          <label className="bg-white/20 hover:bg-white/30 backdrop-blur-sm border border-white/20 px-6 py-3 rounded-xl font-semibold transition-all duration-200 cursor-pointer flex items-center space-x-2 hover:scale-105">
            <Upload className="w-5 h-5" />
            <span>Upload Document</span>
            <input
              type="file"
              multiple
              onChange={handleFileUpload}
              className="hidden"
              accept="*/*"
            />
          </label>
          
          <button className="bg-white/20 hover:bg-white/30 backdrop-blur-sm border border-white/20 px-6 py-3 rounded-xl font-semibold transition-all duration-200 flex items-center space-x-2 hover:scale-105">
            <FolderPlus className="w-5 h-5" />
            <span>New Folder</span>
          </button>
        </div>

        {/* Upload Progress */}
        {isUploading && (
          <div className="mt-6 bg-white/10 backdrop-blur-sm rounded-xl p-4 border border-white/20">
            <div className="flex items-center justify-between mb-2">
              <span className="text-sm font-medium">Uploading...</span>
              <span className="text-sm">{uploadProgress}%</span>
            </div>
            <div className="w-full bg-white/20 rounded-full h-2">
              <div 
                className="bg-white h-2 rounded-full transition-all duration-300"
                style={{ width: `${uploadProgress}%` }}
              ></div>
            </div>
          </div>
        )}
      </div>

      {/* Statistics */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
        {stats.map((stat, index) => {
          const Icon = stat.icon;
          return (
            <div 
              key={index}
              className="bg-white/80 backdrop-blur-lg rounded-2xl p-6 shadow-lg border border-white/20 hover:shadow-xl transition-all duration-300 hover:-translate-y-1"
            >
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-gray-600 text-sm font-medium">{stat.title}</p>
                  <p className="text-2xl font-bold text-gray-800 mt-1">{stat.value}</p>
                  <p className="text-green-600 text-sm mt-1">{stat.change}</p>
                </div>
                <div className={`w-12 h-12 bg-gradient-to-br ${stat.color} rounded-xl flex items-center justify-center shadow-lg`}>
                  <Icon className="w-6 h-6 text-white" />
                </div>
              </div>
            </div>
          );
        })}
      </div>

      {/* Content Grid */}
      <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
        {/* Recent Documents */}
        <div className="lg:col-span-2 bg-white/80 backdrop-blur-lg rounded-2xl shadow-lg border border-white/20">
          <div className="p-6 border-b border-gray-200/50">
            <div className="flex items-center justify-between">
              <h2 className="text-xl font-bold text-gray-800">Recent Documents</h2>
              <button className="text-[#3B38A0] hover:text-[#1A2A80] font-semibold text-sm transition-colors">
                View All
              </button>
            </div>
          </div>
          
          <div className="p-6">
            <div className="space-y-4">
              {recentDocuments.map((doc) => (
                <div key={doc.id} className="flex items-center space-x-4 p-4 rounded-xl hover:bg-[#B2B0E8]/10 transition-colors group">
                  <div className="text-2xl">{getFileIcon(doc.type)}</div>
                  <div className="flex-1 min-w-0">
                    <p className="font-semibold text-gray-800 truncate">{doc.name}</p>
                    <div className="flex items-center space-x-3 text-sm text-gray-500 mt-1">
                      <span>{doc.size}</span>
                      <span>‚Ä¢</span>
                      <span>{doc.lastModified}</span>
                      {doc.shared && (
                        <>
                          <span>‚Ä¢</span>
                          <span className="text-[#3B38A0] font-medium">Shared</span>
                        </>
                      )}
                    </div>
                  </div>
                  <div className="flex items-center space-x-2 opacity-0 group-hover:opacity-100 transition-opacity">
                    <button className="p-2 text-gray-400 hover:text-[#3B38A0] hover:bg-[#B2B0E8]/20 rounded-lg transition-all">
                      <Eye className="w-4 h-4" />
                    </button>
                    <button className="p-2 text-gray-400 hover:text-[#3B38A0] hover:bg-[#B2B0E8]/20 rounded-lg transition-all">
                      <Download className="w-4 h-4" />
                    </button>
                    <button className="p-2 text-gray-400 hover:text-[#3B38A0] hover:bg-[#B2B0E8]/20 rounded-lg transition-all">
                      <MoreVertical className="w-4 h-4" />
                    </button>
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>

        {/* Recent Activity */}
        <div className="bg-white/80 backdrop-blur-lg rounded-2xl shadow-lg border border-white/20">
          <div className="p-6 border-b border-gray-200/50">
            <h2 className="text-xl font-bold text-gray-800">Recent Activity</h2>
          </div>
          
          <div className="p-6">
            <div className="space-y-4">
              {recentActivity.map((activity, index) => (
                <div key={index} className="flex items-start space-x-3">
                  <div className="w-2 h-2 bg-[#3B38A0] rounded-full mt-2 flex-shrink-0"></div>
                  <div className="flex-1">
                    <p className="text-sm text-gray-800">
                      <span className="font-medium">{activity.action}</span>{' '}
                      <span className="text-[#3B38A0]">{activity.file}</span>
                    </p>
                    <p className="text-xs text-gray-500 mt-1">{activity.time}</p>
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>
      </div>

      {/* Quick Actions */}
      <div className="bg-white/80 backdrop-blur-lg rounded-2xl shadow-lg border border-white/20 p-6">
        <h2 className="text-xl font-bold text-gray-800 mb-6">Quick Actions</h2>
        
        <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
          <button className="flex items-center space-x-3 p-4 bg-gradient-to-r from-[#1A2A80]/10 to-[#3B38A0]/10 hover:from-[#1A2A80]/20 hover:to-[#3B38A0]/20 rounded-xl transition-all duration-200 hover:scale-105">
            <Upload className="w-6 h-6 text-[#1A2A80]" />
            <div className="text-left">
              <p className="font-semibold text-gray-800">Upload Files</p>
              <p className="text-sm text-gray-600">Add new documents</p>
            </div>
          </button>
          
          <button className="flex items-center space-x-3 p-4 bg-gradient-to-r from-[#3B38A0]/10 to-[#7A85C1]/10 hover:from-[#3B38A0]/20 hover:to-[#7A85C1]/20 rounded-xl transition-all duration-200 hover:scale-105">
            <Share2 className="w-6 h-6 text-[#3B38A0]" />
            <div className="text-left">
              <p className="font-semibold text-gray-800">Share Document</p>
              <p className="text-sm text-gray-600">Share with team</p>
            </div>
          </button>
          
          <button className="flex items-center space-x-3 p-4 bg-gradient-to-r from-[#7A85C1]/10 to-[#B2B0E8]/10 hover:from-[#7A85C1]/20 hover:to-[#B2B0E8]/20 rounded-xl transition-all duration-200 hover:scale-105">
            <FolderPlus className="w-6 h-6 text-[#7A85C1]" />
            <div className="text-left">
              <p className="font-semibold text-gray-800">Create Folder</p>
              <p className="text-sm text-gray-600">Organize files</p>
            </div>
          </button>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
