import { useState } from "react";
import { useNavigate } from "react-router-dom";

import {
  Search,
  Plus,
  Home,
  HardDrive,
  Users,
  Trash2,
  FileText,
  Folder,
  MoreVertical,
  Grid3X3,
  List,
  User,
  Settings,
  LogOut,
} from "lucide-react"
import "../styles/Home.css"
import "../styles/Navbar.css";

export function Navbar() {
  const [viewMode, setViewMode] = useState("grid")
  const [isLoggedIn, setIsLoggedIn] = useState(true)
  const [showNewDialog, setShowNewDialog] = useState(false)
  const [showUserDropdown, setShowUserDropdown] = useState(false)

  const navigate = useNavigate();
  const getFileIcon = (type) => {
    switch (type) {
      case "folder":
        return <Folder className="file-icon file-icon-primary" />
      default:
        return <FileText className="file-icon file-icon-muted" />
    }
  }

  return (
    <div className="navbar">
      <div className="navbar-content">
        <nav className="nav-links">
          <button className="nav-btn nav-btn-active">
            <Home className="nav-icon" />
            Home
          </button>
          <button className="nav-btn">
            <HardDrive className="nav-icon" />
            My Drive
          </button>
          <button className="nav-btn">
            <Users className="nav-icon" />
            Shared with me
          </button>
          <button className="nav-btn">
            <Trash2 className="nav-icon" />
            Trash
          </button>
        </nav>

        <div className="navbar-actions">
          <button
            className="btn btn-primary new-btn"
            onClick={() => setShowNewDialog(!showNewDialog)}
          >
            <Plus className="btn-icon" />
            New
          </button>

          {showNewDialog && (
            <div className="new-dialog">
              <h3 className="dialog-title">Create New</h3>
              <div className="dialog-content">
                <button className="dialog-option" onClick={() => setShowNewDialog(false)}>
                  <Folder className="option-icon" />
                  New Folder
                </button>
                <button
                  className="dialog-option"
                  onClick={() => {
                    setShowNewDialog(false);
                    navigate("/upload");
                  }}
                >
                  <FileText className="option-icon" />
                  Upload File
                </button>
              </div>
            </div>
          )}

          <div className="view-toggle">
            <button
              className={`view-btn ${viewMode === "grid" ? "view-btn-active" : ""}`}
              onClick={() => setViewMode("grid")}
            >
              <Grid3X3 className="view-icon" />
            </button>
            <button
              className={`view-btn ${viewMode === "list" ? "view-btn-active" : ""}`}
              onClick={() => setViewMode("list")}
            >
              <List className="view-icon" />
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}
