import React, { useState, useEffect } from 'react';
import { 
  FileText, 
  Folder, 
  Upload, 
  Download, 
  Search, 
  Plus, 
  Settings, 
  User, 
  Bell, 
  Menu,
  X,
  Eye,
  Edit,
  Trash2,
  Share2,
  Filter,
  Grid,
  List,
  Calendar,
  Activity,
  MoreVertical
} from 'lucide-react';
import { 
  getMyDocuments, 
  getMyFolders, 
  uploadFile, 
  deleteDocument, 
  downloadFile,
  getDashboardStats 
} from '../api/api';
import Sidebar from './Sidebar';
import Header from './Header';
import DocumentCard from './DocumentCard';
import FolderCard from './FolderCard';
import UploadModal from './UploadModal';
import ShareModal from './ShareModal';
import DeleteConfirmModal from './DeleteConfirmModal';
import LoadingSpinner from './LoadingSpinner';
import Toast from './Toast';

const Dashboard = ({ user, onLogout }) => {
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const [documents, setDocuments] = useState([]);
  const [folders, setFolders] = useState([]);
  const [filteredDocuments, setFilteredDocuments] = useState([]);
  const [searchQuery, setSearchQuery] = useState('');
  const [viewMode, setViewMode] = useState('grid');
  const [loading, setLoading] = useState(true);
  const [uploadModalOpen, setUploadModalOpen] = useState(false);
  const [shareModalOpen, setShareModalOpen] = useState(false);
  const [deleteModalOpen, setDeleteModalOpen] = useState(false);
  const [selectedDocument, setSelectedDocument] = useState(null);
  const [stats, setStats] = useState({ totalDocuments: 0, totalFolders: 0, totalStorage: '0 MB' });
  const [toast, setToast] = useState({ show: false, message: '', type: 'success' });

  useEffect(() => {
    loadData();
  }, []);

  useEffect(() => {
    filterDocuments();
  }, [searchQuery, documents]);

  const loadData = async () => {
    try {
      setLoading(true);
      const [docsResponse, foldersResponse, statsResponse] = await Promise.all([
        getMyDocuments(),
        getMyFolders(),
        getDashboardStats().catch(() => ({ data: { totalDocuments: 0, totalFolders: 0, totalStorage: '0 MB' } }))
      ]);

      setDocuments(docsResponse.data || []);
      setFolders(foldersResponse.data || []);
      setStats(statsResponse.data);
    } catch (error) {
      console.error('Error loading data:', error);
      showToast('Error loading data', 'error');
    } finally {
      setLoading(false);
    }
  };

  const filterDocuments = () => {
    if (!searchQuery) {
      setFilteredDocuments(documents);
      return;
    }

    const filtered = documents.filter(doc =>
      doc.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
      doc.folder?.toLowerCase().includes(searchQuery.toLowerCase())
    );
    setFilteredDocuments(filtered);
  };

  const handleUpload = async (files) => {
    try {
      const uploadPromises = files.map(file => uploadFile(file));
      await Promise.all(uploadPromises);
      showToast(`Successfully uploaded ${files.length} file(s)`, 'success');
      loadData();
      setUploadModalOpen(false);
    } catch (error) {
      console.error('Upload error:', error);
      showToast('Error uploading files', 'error');
    }
  };

  const handleView = (document) => {
    // Open document in new tab or modal
    window.open(`/documents/${document.id}`, '_blank');
  };

  const handleEdit = (document) => {
    setSelectedDocument(document);
    // Open edit modal or navigate to edit page
    console.log('Edit document:', document);
  };

  const handleShare = (document) => {
    setSelectedDocument(document);
    setShareModalOpen(true);
  };

  const handleDelete = (document) => {
    setSelectedDocument(document);
    setDeleteModalOpen(true);
  };

  const confirmDelete = async () => {
    try {
      await deleteDocument(selectedDocument.id);
      showToast('Document deleted successfully', 'success');
      loadData();
      setDeleteModalOpen(false);
      setSelectedDocument(null);
    } catch (error) {
      console.error('Delete error:', error);
      showToast('Error deleting document', 'error');
    }
  };

  const handleDownload = async (document) => {
    try {
      const response = await downloadFile(document.id);
      const url = window.URL.createObjectURL(new Blob([response.data]));
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', document.name);
      document.body.appendChild(link);
      link.click();
      link.remove();
      window.URL.revokeObjectURL(url);
      showToast('Download started', 'success');
    } catch (error) {
      console.error('Download error:', error);
      showToast('Error downloading file', 'error');
    }
  };

  const showToast = (message, type = 'success') => {
    setToast({ show: true, message, type });
    setTimeout(() => setToast({ show: false, message: '', type: 'success' }), 3000);
  };

  if (loading) {
    return <LoadingSpinner />;
  }

  return (
    <div className="flex h-screen bg-gray-50">
      <Sidebar 
        isOpen={sidebarOpen} 
        onClose={() => setSidebarOpen(false)}
        activeItem="documents"
      />

      <div className="flex-1 flex flex-col overflow-hidden">
        <Header 
          user={user}
          onMenuClick={() => setSidebarOpen(true)}
          onLogout={onLogout}
        />

        <main className="flex-1 overflow-y-auto p-4 lg:p-6">
          {/* Stats Cards */}
          <div className="grid grid-cols-1 md:grid-cols-3 gap-4 lg:gap-6 mb-6">
            <div className="bg-white rounded-xl p-6 shadow-sm border border-gray-200">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-sm font-medium text-gray-600">Total Documents</p>
                  <p className="text-2xl font-bold text-[#1A2A80]">{stats.totalDocuments || documents.length}</p>
                </div>
                <div className="p-3 bg-[#1A2A80]/10 rounded-lg">
                  <FileText className="w-6 h-6 text-[#1A2A80]" />
                </div>
              </div>
            </div>

            <div className="bg-white rounded-xl p-6 shadow-sm border border-gray-200">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-sm font-medium text-gray-600">Total Folders</p>
                  <p className="text-2xl font-bold text-[#3B38A0]">{stats.totalFolders || folders.length}</p>
                </div>
                <div className="p-3 bg-[#3B38A0]/10 rounded-lg">
                  <Folder className="w-6 h-6 text-[#3B38A0]" />
                </div>
              </div>
            </div>

            <div className="bg-white rounded-xl p-6 shadow-sm border border-gray-200">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-sm font-medium text-gray-600">Storage Used</p>
                  <p className="text-2xl font-bold text-[#7A85C1]">{stats.totalStorage}</p>
                </div>
                <div className="p-3 bg-[#7A85C1]/10 rounded-lg">
                  <Activity className="w-6 h-6 text-[#7A85C1]" />
                </div>
              </div>
            </div>
          </div>

          {/* Search and Actions */}
          <div className="flex flex-col lg:flex-row lg:items-center lg:justify-between gap-4 mb-6">
            <div className="relative flex-1 max-w-md">
              <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 w-5 h-5" />
              <input
                type="text"
                placeholder="Search documents..."
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
                className="w-full pl-10 pr-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-[#7A85C1] focus:border-transparent"
              />
            </div>

            <div className="flex items-center space-x-3">
              <div className="flex items-center bg-white rounded-lg border border-gray-200 p-1">
                <button
                  onClick={() => setViewMode('grid')}
                  className={`p-2 rounded-md ${viewMode === 'grid' ? 'bg-[#7A85C1] text-white' : 'text-gray-400 hover:text-gray-600'}`}
                >
                  <Grid className="w-4 h-4" />
                </button>
                <button
                  onClick={() => setViewMode('list')}
                  className={`p-2 rounded-md ${viewMode === 'list' ? 'bg-[#7A85C1] text-white' : 'text-gray-400 hover:text-gray-600'}`}
                >
                  <List className="w-4 h-4" />
                </button>
              </div>

              <button
                onClick={() => setUploadModalOpen(true)}
                className="flex items-center space-x-2 bg-gradient-to-r from-[#1A2A80] to-[#3B38A0] text-white px-4 py-2 rounded-lg hover:shadow-lg transition-all duration-200"
              >
                <Upload className="w-4 h-4" />
                <span>Upload</span>
              </button>
            </div>
          </div>

          {/* Quick Access Folders */}
          {folders.length > 0 && (
            <div className="mb-8">
              <h2 className="text-lg font-semibold text-gray-900 mb-4">Quick Access</h2>
              <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-4">
                {folders.slice(0, 4).map((folder) => (
                  <FolderCard 
                    key={folder.id} 
                    folder={folder} 
                    onClick={() => console.log('Navigate to folder:', folder.id)}
                  />
                ))}
              </div>
            </div>
          )}

          {/* Recent Documents */}
          <div>
            <div className="flex items-center justify-between mb-4">
              <h2 className="text-lg font-semibold text-gray-900">
                Recent Documents ({filteredDocuments.length})
              </h2>
              {documents.length !== filteredDocuments.length && (
                <button
                  onClick={() => setSearchQuery('')}
                  className="text-sm text-[#3B38A0] hover:text-[#1A2A80]"
                >
                  Clear filter
                </button>
              )}
            </div>

            {filteredDocuments.length === 0 ? (
              <div className="text-center py-12">
                <FileText className="w-16 h-16 text-gray-300 mx-auto mb-4" />
                <h3 className="text-lg font-medium text-gray-900 mb-2">
                  {searchQuery ? 'No documents found' : 'No documents yet'}
                </h3>
                <p className="text-gray-500 mb-6">
                  {searchQuery 
                    ? 'Try adjusting your search terms'
                    : 'Upload your first document to get started'
                  }
                </p>
                {!searchQuery && (
                  <button
                    onClick={() => setUploadModalOpen(true)}
                    className="bg-gradient-to-r from-[#1A2A80] to-[#3B38A0] text-white px-6 py-3 rounded-lg hover:shadow-lg transition-all duration-200"
                  >
                    Upload Document
                  </button>
                )}
              </div>
            ) : (
              <div className={`grid gap-4 ${viewMode === 'grid' 
                ? 'grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4' 
                : 'grid-cols-1'
              }`}>
                {filteredDocuments.map((doc) => (
                  <DocumentCard
                    key={doc.id}
                    document={doc}
                    viewMode={viewMode}
                    onView={handleView}
                    onEdit={handleEdit}
                    onShare={handleShare}
                    onDelete={handleDelete}
                    onDownload={handleDownload}
                  />
                ))}
              </div>
            )}
          </div>
        </main>
      </div>

      {/* Modals */}
      <UploadModal
        isOpen={uploadModalOpen}
        onClose={() => setUploadModalOpen(false)}
        onUpload={handleUpload}
      />

      <ShareModal
        isOpen={shareModalOpen}
        onClose={() => setShareModalOpen(false)}
        document={selectedDocument}
        onShare={(permissions) => {
          console.log('Share document:', selectedDocument, permissions);
          setShareModalOpen(false);
          showToast('Document shared successfully', 'success');
        }}
      />

      <DeleteConfirmModal
        isOpen={deleteModalOpen}
        onClose={() => setDeleteModalOpen(false)}
        onConfirm={confirmDelete}
        documentName={selectedDocument?.name}
      />

      <Toast
        show={toast.show}
        message={toast.message}
        type={toast.type}
        onClose={() => setToast({ show: false, message: '', type: 'success' })}
      />
    </div>
  );
};

export default Dashboard;
