import React, { useState, useEffect } from 'react';
import { useAuth } from '../context/AuthContext';
import { useApp } from '../context/AppContext';
import LoadingSpinner from '../components/common/LoadingSpinner';

const FolderCard = ({ folder, onOpen, onEdit, onDelete, onShare }) => {
  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString();
  };

  return (
    <div className="card hover:shadow-lg transition-all duration-200 group">
      <div className="flex items-start justify-between">
        <div 
          className="flex items-center space-x-3 flex-1 cursor-pointer"
          onClick={() => onOpen(folder)}
        >
          <div className="text-4xl text-primary-500 group-hover:text-primary-600 transition-colors">
            üìÅ
          </div>
          <div className="flex-1 min-w-0">
            <h3 className="text-sm font-medium text-gray-900 truncate group-hover:text-primary-600">
              {folder.name}
            </h3>
            <div className="flex items-center mt-2 text-xs text-gray-500 space-x-4">
              <span>{folder.documentCount || 0} files</span>
              <span>{folder.subFolderCount || 0} folders</span>
              <span>Created {formatDate(folder.createdAt || new Date())}</span>
            </div>
            {folder.description && (
              <p className="text-xs text-gray-400 mt-1 truncate">
                {folder.description}
              </p>
            )}
          </div>
        </div>
        
        <div className="flex items-center space-x-1 opacity-0 group-hover:opacity-100 transition-opacity">
          <button
            onClick={(e) => {
              e.stopPropagation();
              onShare(folder);
            }}
            className="p-1 text-gray-400 hover:text-blue-600 transition-colors"
            title="Share folder"
          >
            <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M8.684 13.342C8.886 12.938 9 12.482 9 12c0-.482-.114-.938-.316-1.342m0 2.684a3 3 0 110-2.684m0 2.684l6.632 3.316m-6.632-6l6.632-3.316m0 0a3 3 0 105.367-2.684 3 3 0 00-5.367 2.684zm0 9.316a3 3 0 105.367 2.684 3 3 0 00-5.367-2.684z" />
            </svg>
          </button>
          
          <button
            onClick={(e) => {
              e.stopPropagation();
              onEdit(folder);
            }}
            className="p-1 text-gray-400 hover:text-green-600 transition-colors"
            title="Edit folder"
          >
            <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M11 5H6a2 2 0 00-2 2v11a2 2 0 002 2h11a2 2 0 002-2v-5m-1.414-9.414a2 2 0 112.828 2.828L11.828 15H9v-2.828l8.586-8.586z" />
            </svg>
          </button>
          
          <button
            onClick={(e) => {
              e.stopPropagation();
              onDelete(folder);
            }}
            className="p-1 text-gray-400 hover:text-red-600 transition-colors"
            title="Delete folder"
          >
            <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 7l-.867 12.142A2 2 0 0116.138 21H7.862a2 2 0 01-1.995-1.858L5 7m5 4v6m4-6v6m1-10V4a1 1 0 00-1-1h-4a1 1 0 00-1 1v3M4 7h16" />
            </svg>
          </button>
        </div>
      </div>
    </div>
  );
};

const FolderModal = ({ isOpen, onClose, folder, onSave }) => {
  const [formData, setFormData] = useState({
    name: '',
    description: ''
  });
  const [saving, setSaving] = useState(false);
  const { showSuccess, showError } = useApp();

  useEffect(() => {
    if (folder) {
      setFormData({
        name: folder.name || '',
        description: folder.description || ''
      });
    } else {
      setFormData({
        name: '',
        description: ''
      });
    }
  }, [folder]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!formData.name.trim()) {
      showError('Folder name is required');
      return;
    }

    setSaving(true);
    try {
      await onSave(formData);
      showSuccess(folder ? 'Folder updated successfully' : 'Folder created successfully');
      onClose();
    } catch (error) {
      showError('Failed to save folder');
    } finally {
      setSaving(false);
    }
  };

  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50">
      <div className="bg-white rounded-lg max-w-md w-full p-6">
        <div className="flex justify-between items-center mb-4">
          <h3 className="text-lg font-medium">
            {folder ? 'Edit Folder' : 'Create New Folder'}
          </h3>
          <button
            onClick={onClose}
            className="text-gray-400 hover:text-gray-600"
          >
            <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
            </svg>
          </button>
        </div>

        <div className="space-y-4">
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              Folder Name
            </label>
            <input
              type="text"
              value={formData.name}
              onChange={(e) => setFormData(prev => ({ ...prev, name: e.target.value }))}
              className="input-field"
              placeholder="Enter folder name"
              disabled={saving}
            />
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              Description (Optional)
            </label>
            <textarea
              value={formData.description}
              onChange={(e) => setFormData(prev => ({ ...prev, description: e.target.value }))}
              className="input-field"
              rows="3"
              placeholder="Enter folder description"
              disabled={saving}
            />
          </div>
        </div>

        <div className="flex justify-end space-x-3 mt-6">
          <button
            onClick={onClose}
            className="btn-outline"
            disabled={saving}
          >
            Cancel
          </button>
          <button
            onClick={handleSubmit}
            className="btn-primary"
            disabled={saving}
          >
            {saving ? (
              <>
                <LoadingSpinner size="sm" color="white" />
                <span className="ml-2">Saving...</span>
              </>
            ) : (
              folder ? 'Update' : 'Create'
            )}
          </button>
        </div>
      </div>
    </div>
  );
};

const Breadcrumb = ({ path, onNavigate }) => {
  return (
    <nav className="flex items-center space-x-2 text-sm text-gray-500">
      <button
        onClick={() => onNavigate(null)}
        className="hover:text-primary-600 transition-colors"
      >
        <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M3 7v10a2 2 0 002 2h14a2 2 0 002-2V9a2 2 0 00-2-2H5a2 2 0 00-2-2z" />
        </svg>
      </button>
      
      {path.map((folder, index) => (
        <React.Fragment key={folder.id}>
          <svg className="w-4 h-4 text-gray-300" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5l7 7-7 7" />
          </svg>
          <button
            onClick={() => onNavigate(folder)}
            className="hover:text-primary-600 transition-colors"
          >
            {folder.name}
          </button>
        </React.Fragment>
      ))}
    </nav>
  );
};

const FoldersPage = () => {
  const { user } = useAuth();
  const { searchQuery, showSuccess, showError } = useApp();
  const [folders, setFolders] = useState([]);
  const [loading, setLoading] = useState(true);
  const [currentFolder, setCurrentFolder] = useState(null);
  const [folderPath, setFolderPath] = useState([]);
  const [showFolderModal, setShowFolderModal] = useState(false);
  const [editingFolder, setEditingFolder] = useState(null);

  useEffect(() => {
    loadFolders();
  }, [currentFolder]);

  const loadFolders = async () => {
    try {
      setLoading(true);
      // Mock data - in real app would call folderService.getFolders()
      const mockFolders = [
        {
          id: 1,
          name: "Work Projects",
          description: "All work-related project files",
          documentCount: 15,
          subFolderCount: 3,
          createdAt: new Date().toISOString(),
          owner: { name: user?.name, email: user?.email }
        },
        {
          id: 2,
          name: "Personal Documents",
          description: "Personal files and documents",
          documentCount: 8,
          subFolderCount: 1,
          createdAt: new Date(Date.now() - 86400000).toISOString(),
          owner: { name: user?.name, email: user?.email }
        },
        {
          id: 3,
          name: "Archive",
          description: "Old files and archived documents",
          documentCount: 25,
          subFolderCount: 0,
          createdAt: new Date(Date.now() - 604800000).toISOString(),
          owner: { name: user?.name, email: user?.email }
        },
        {
          id: 4,
          name: "Shared Files",
          description: "Files shared with team members",
          documentCount: 12,
          subFolderCount: 2,
          createdAt: new Date(Date.now() - 172800000).toISOString(),
          owner: { name: user?.name, email: user?.email }
        }
      ];
      
      // Filter folders based on current folder (simulate hierarchy)
      const filteredFolders = currentFolder 
        ? mockFolders.filter(f => f.parentId === currentFolder.id)
        : mockFolders.filter(f => !f.parentId);
        
      setFolders(filteredFolders);
    } catch (error) {
      showError('Failed to load folders');
    } finally {
      setLoading(false);
    }
  };

  const filteredFolders = folders.filter(folder =>
    !searchQuery || folder.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
    folder.description?.toLowerCase().includes(searchQuery.toLowerCase())
  );

  const handleOpenFolder = (folder) => {
    setCurrentFolder(folder);
    setFolderPath(prev => [...prev, folder]);
  };

  const handleNavigateToFolder = (folder) => {
    if (!folder) {
      // Navigate to root
      setCurrentFolder(null);
      setFolderPath([]);
    } else {
      // Navigate to specific folder
      const folderIndex = folderPath.findIndex(f => f.id === folder.id);
      if (folderIndex !== -1) {
        setCurrentFolder(folder);
        setFolderPath(prev => prev.slice(0, folderIndex + 1));
      }
    }
  };

  const handleCreateFolder = () => {
    setEditingFolder(null);
    setShowFolderModal(true);
  };

  const handleEditFolder = (folder) => {
    setEditingFolder(folder);
    setShowFolderModal(true);
  };

  const handleSaveFolder = async (folderData) => {
    try {
      if (editingFolder) {
        // Update existing folder
        const updatedFolder = { ...editingFolder, ...folderData };
        setFolders(prev => prev.map(f => f.id === editingFolder.id ? updatedFolder : f));
      } else {
        // Create new folder
        const newFolder = {
          id: Date.now(),
          ...folderData,
          documentCount: 0,
          subFolderCount: 0,
          createdAt: new Date().toISOString(),
          parentId: currentFolder?.id,
          owner: { name: user?.name, email: user?.email }
        };
        setFolders(prev => [...prev, newFolder]);
      }
    } catch (error) {
      throw error;
    }
  };

  const handleDeleteFolder = async (folder) => {
    if (window.confirm(`Are you sure you want to delete "${folder.name}" and all its contents?`)) {
      try {
        setFolders(prev => prev.filter(f => f.id !== folder.id));
        showSuccess('Folder deleted successfully');
      } catch (error) {
        showError('Delete failed');
      }
    }
  };

  const handleShareFolder = (folder) => {
    showSuccess(`Sharing options for "${folder.name}" would open here`);
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center h-64">
        <LoadingSpinner size="lg" />
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex flex-col sm:flex-row sm:items-center sm:justify-between">
        <div>
          <h1 className="text-2xl font-bold text-gray-900">Folders</h1>
          <p className="text-gray-600 mt-1">Organize your documents with folders</p>
        </div>
        
        <button
          onClick={handleCreateFolder}
          className="mt-4 sm:mt-0 btn-primary"
        >
          <svg className="w-5 h-5 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 6v6m0 0v6m0-6h6m-6 0H6" />
          </svg>
          Create Folder
        </button>
      </div>

      {/* Breadcrumb */}
      {folderPath.length > 0 && (
        <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-4">
          <Breadcrumb path={folderPath} onNavigate={handleNavigateToFolder} />
        </div>
      )}

      {/* Stats */}
      <div className="grid grid-cols-1 md:grid-cols-4 gap-6">
        <div className="card text-center">
          <div className="text-3xl font-bold text-primary-600">
            {folders.length}
          </div>
          <div className="text-sm text-gray-600 mt-1">
            {currentFolder ? 'Subfolders' : 'Total Folders'}
          </div>
        </div>
        
        <div className="card text-center">
          <div className="text-3xl font-bold text-green-600">
            {folders.reduce((sum, f) => sum + (f.documentCount || 0), 0)}
          </div>
          <div className="text-sm text-gray-600 mt-1">Documents</div>
        </div>
        
        <div className="card text-center">
          <div className="text-3xl font-bold text-blue-600">
            {folders.reduce((sum, f) => sum + (f.subFolderCount || 0), 0)}
          </div>
          <div className="text-sm text-gray-600 mt-1">Nested Folders</div>
        </div>
        
        <div className="card text-center">
          <div className="text-3xl font-bold text-purple-600">2.1 GB</div>
          <div className="text-sm text-gray-600 mt-1">Total Size</div>
        </div>
      </div>

      {/* Folders Grid */}
      {filteredFolders.length === 0 ? (
        <div className="text-center py-12">
          <svg className="mx-auto h-12 w-12 text-gray-400" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M3 7v10a2 2 0 002 2h14a2 2 0 002-2V9a2 2 0 00-2-2H5a2 2 0 00-2-2z" />
          </svg>
          <h3 className="mt-2 text-sm font-medium text-gray-900">
            {searchQuery ? 'No folders found' : 'No folders yet'}
          </h3>
          <p className="mt-1 text-sm text-gray-500">
            {searchQuery 
              ? 'Try adjusting your search terms.' 
              : 'Create your first folder to organize your documents.'
            }
          </p>
          {!searchQuery && (
            <div className="mt-6">
              <button
                onClick={handleCreateFolder}
                className="btn-primary"
              >
                Create Folder
              </button>
            </div>
          )}
        </div>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {/* Back button for nested folders */}
          {currentFolder && (
            <div 
              className="card hover:shadow-lg transition-all duration-200 cursor-pointer border-2 border-dashed border-gray-300 hover:border-primary-400"
              onClick={() => handleNavigateToFolder(folderPath[folderPath.length - 2] || null)}
            >
              <div className="flex items-center space-x-3">
                <div className="text-4xl text-gray-400">
                  ‚¨ÖÔ∏è
                </div>
                <div>
                  <h3 className="text-sm font-medium text-gray-600">Go Back</h3>
                  <p className="text-xs text-gray-500 mt-1">
                    Return to {folderPath.length > 1 ? folderPath[folderPath.length - 2].name : 'root'}
                  </p>
                </div>
              </div>
            </div>
          )}
          
          {filteredFolders.map(folder => (
            <FolderCard
              key={folder.id}
              folder={folder}
              onOpen={handleOpenFolder}
              onEdit={handleEditFolder}
              onDelete={handleDeleteFolder}
              onShare={handleShareFolder}
            />
          ))}
        </div>
      )}

      {/* Recent Activity */}
      <div className="card">
        <h3 className="text-lg font-medium text-gray-900 mb-4">Recent Folder Activity</h3>
        <div className="space-y-3">
          {[
            { action: 'Created', folder: 'Work Projects', time: '2 hours ago', user: user?.name },
            { action: 'Modified', folder: 'Personal Documents', time: '1 day ago', user: user?.name },
            { action: 'Shared', folder: 'Shared Files', time: '3 days ago', user: user?.name },
          ].map((activity, index) => (
            <div key={index} className="flex items-center justify-between py-2 border-b border-gray-100 last:border-b-0">
              <div className="flex items-center space-x-3">
                <div className="w-8 h-8 bg-primary-100 rounded-full flex items-center justify-center">
                  <svg className="w-4 h-4 text-primary-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M3 7v10a2 2 0 002 2h14a2 2 0 002-2V9a2 2 0 00-2-2H5a2 2 0 00-2-2z" />
                  </svg>
                </div>
                <div>
                  <p className="text-sm text-gray-900">
                    <span className="font-medium">{activity.action}</span> folder "{activity.folder}"
                  </p>
                  <p className="text-xs text-gray-500">{activity.user} ‚Ä¢ {activity.time}</p>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>

      {/* Folder Modal */}
      <FolderModal
        isOpen={showFolderModal}
        onClose={() => setShowFolderModal(false)}
        folder={editingFolder}
        onSave={handleSaveFolder}
      />
    </div>
  );
};

export default FoldersPage;
