// src/pages/home/HomePage.js
import React, { useState, useEffect } from 'react';
import documentService from '../../services/documentService';
import DocumentGrid from '../../components/documents/DocumentGrid';
import FolderGrid from '../../components/documents/FolderGrid';
import './HomePage.css';

const HomePage = () => {
  const [recentDocuments, setRecentDocuments] = useState([]);
  const [folders, setFolders] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const loadData = async () => {
      try {
        const [docs, folds] = await Promise.all([
          documentService.getMyDocuments(),
          documentService.getFolders()
        ]);
        setRecentDocuments(docs.slice(0, 6)); // Show recent 6
        setFolders(folds.slice(0, 6)); // Show recent 6
      } catch (error) {
        console.error('Failed to load data:', error);
      } finally {
        setLoading(false);
      }
    };

    loadData();
  }, []);

  if (loading) {
    return <div className="loading-spinner">Loading...</div>;
  }

  return (
    <div className="home-page">
      <div className="page-header">
        <h1>Welcome to DocShare</h1>
        <p>Manage and share your documents easily</p>
      </div>

      <div className="home-sections">
        <section className="recent-section">
          <div className="section-header">
            <h2>Recent Folders</h2>
          </div>
          <FolderGrid folders={folders} />
        </section>

        <section className="recent-section">
          <div className="section-header">
            <h2>Recent Documents</h2>
          </div>
          <DocumentGrid documents={recentDocuments} />
        </section>
      </div>
    </div>
  );
};

export default HomePage;
