import React, { useState } from 'react';
import { 
  LayoutDashboard, 
  FolderOpen, 
  Users, 
  Star, 
  Trash2, 
  Cloud, 
  TrendingUp, 
  Zap,
  Home,
  Plus,
  Upload,
  BarChart3,
  Settings,
  Sparkles
} from 'lucide-react';

const Sidebar = ({ activeRoute = '/dashboard' }) => {
  const [storageUsed] = useState(67);
  
  const menuItems = [
    {
      path: '/dashboard',
      icon: LayoutDashboard,
      label: 'Dashboard',
      description: 'Overview & analytics',
      gradient: 'linear-gradient(135deg, #1A2A80 0%, #3B38A0 100%)'
    },
    {
      path: '/my-drive',
      icon: FolderOpen,
      label: 'My Drive',
      description: 'Your files & folders',
      gradient: 'linear-gradient(135deg, #3B38A0 0%, #7A85C1 100%)'
    },
    {
      path: '/shared-with-me',
      icon: Users,
      label: 'Shared with Me',
      description: 'Collaborative docs',
      gradient: 'linear-gradient(135deg, #7A85C1 0%, #B2B0E8 100%)'
    }
  ];

  const quickAccessItems = [
    {
      path: '/starred',
      icon: Star,
      label: 'Starred',
      count: 12,
      color: '#F59E0B'
    },
    {
      path: '/recent',
      icon: Cloud,
      label: 'Recent',
      count: 8,
      color: '#3B82F6'
    },
    {
      path: '/trash',
      icon: Trash2,
      label: 'Trash',
      count: 3,
      color: '#EF4444'
    }
  ];

  const NavLink = ({ item, isActive, onClick }) => {
    const Icon = item.icon;
    return (
      <button
        onClick={() => onClick(item.path)}
        className={`w-full flex items-center space-x-4 px-4 py-4 rounded-xl text-sm font-medium transition-all duration-300 group relative overflow-hidden ${
          isActive ? 'text-white shadow-lg' : 'text-gray-600 hover:text-[#3B38A0]'
        }`}
        style={{
          background: isActive ? item.gradient : 'transparent',
          transform: isActive ? 'translateX(0)' : 'translateX(0)'
        }}
        onMouseEnter={(e) => {
          if (!isActive) {
            e.target.style.backgroundColor = 'rgba(248, 249, 255, 0.8)';
            e.target.style.transform = 'translateX(4px)';
          }
        }}
        onMouseLeave={(e) => {
          if (!isActive) {
            e.target.style.backgroundColor = 'transparent';
            e.target.style.transform = 'translateX(0)';
          }
        }}
      >
        <div className={`w-12 h-12 rounded-2xl flex items-center justify-center transition-all duration-300 ${
          isActive 
            ? 'bg-white/20 shadow-lg' 
            : 'group-hover:shadow-md'
        }`} style={{
          backgroundColor: isActive ? 'rgba(255, 255, 255, 0.2)' : 'rgba(178, 176, 232, 0.2)'
        }}>
          <Icon className={`h-6 w-6 transition-all duration-300 ${
            isActive ? 'text-white' : 'text-[#7A85C1] group-hover:text-[#3B38A0]'
          }`} />
        </div>
        <div className="flex-1 min-w-0 text-left">
          <div className={`font-semibold transition-colors ${
            isActive ? 'text-white' : 'text-[#1A2A80] group-hover:text-[#3B38A0]'
          }`}>
            {item.label}
          </div>
          <div className={`text-xs transition-colors ${
            isActive ? 'text-white/80' : 'text-gray-500'
          }`}>
            {item.description}
          </div>
        </div>
        
        {/* Hover effect overlay */}
        <div className="absolute inset-0 bg-gradient-to-r from-blue-500/5 to-indigo-500/5 opacity-0 group-hover:opacity-100 transition-opacity duration-200 rounded-xl pointer-events-none"></div>
      </button>
    );
  };

  const QuickAccessItem = ({ item, onClick }) => {
    const Icon = item.icon;
    return (
      <button
        onClick={() => onClick(item.path)}
        className="w-full flex items-center space-x-3 px-4 py-3 rounded-xl text-sm transition-all duration-300 group text-gray-600 hover:text-[#1A2A80]"
        onMouseEnter={(e) => {
          e.target.style.backgroundColor = 'rgba(248, 249, 255, 0.6)';
          e.target.style.transform = 'translateX(2px)';
        }}
        onMouseLeave={(e) => {
          e.target.style.backgroundColor = 'transparent';
          e.target.style.transform = 'translateX(0)';
        }}
      >
        <div className="w-10 h-10 bg-white rounded-xl flex items-center justify-center shadow-sm group-hover:shadow-lg transition-all duration-300">
          <Icon className="h-5 w-5 transition-colors" style={{ color: item.color }} />
        </div>
        <div className="flex-1 flex items-center justify-between">
          <span className="font-medium">{item.label}</span>
          {item.count && (
            <span className="px-2 py-1 text-xs font-bold rounded-full shadow-sm transition-all duration-300" style={{
              background: `linear-gradient(135deg, ${item.color} 0%, ${item.color}CC 100%)`,
              color: 'white'
            }}>
              {item.count}
            </span>
          )}
        </div>
      </button>
    );
  };

  const handleNavClick = (path) => {
    console.log(`Navigate to: ${path}`);
  };

  return (
    <aside 
      className="fixed left-0 top-20 h-[calc(100vh-5rem)] w-80 overflow-y-auto z-30"
      style={{
        background: 'linear-gradient(180deg, #FFFFFF 0%, #F8F9FF 100%)',
        backdropFilter: 'blur(20px)',
        borderRight: '1px solid #F3F4F6'
      }}
    >
      <div className="p-6 space-y-8">
        {/* Main Navigation */}
        <div>
          <h2 className="text-xs font-bold uppercase tracking-wider mb-6 px-4 flex items-center" style={{ color: '#7A85C1' }}>
            <Home className="w-4 h-4 mr-2" />
            Main Navigation
          </h2>
          <nav className="space-y-2">
            {menuItems.map((item) => (
              <NavLink
                key={item.path}
                item={item}
                isActive={activeRoute === item.path}
                onClick={handleNavClick}
              />
            ))}
          </nav>
        </div>

        {/* Quick Actions */}
        <div className="bg-white rounded-2xl p-6 shadow-lg border hover:shadow-xl transition-all duration-300" style={{
          borderColor: 'rgba(178, 176, 232, 0.2)',
          background: 'linear-gradient(135deg, rgba(178, 176, 232, 0.05) 0%, rgba(255, 255, 255, 0.8) 100%)'
        }}>
          <h3 className="font-bold mb-4 flex items-center" style={{ color: '#1A2A80' }}>
            <Plus className="w-5 h-5 mr-2" />
            Quick Actions
          </h3>
          <div className="grid grid-cols-2 gap-3">
            <button 
              className="flex flex-col items-center p-4 rounded-xl transition-all duration-300 hover:shadow-lg group"
              style={{ backgroundColor: 'rgba(26, 42, 128, 0.05)' }}
              onMouseEnter={(e) => {
                e.target.style.backgroundColor = 'rgba(26, 42, 128, 0.1)';
                e.target.style.transform = 'translateY(-2px)';
              }}
              onMouseLeave={(e) => {
                e.target.style.backgroundColor = 'rgba(26, 42, 128, 0.05)';
                e.target.style.transform = 'translateY(0)';
              }}
            >
              <div className="w-12 h-12 rounded-xl flex items-center justify-center mb-2 group-hover:scale-110 transition-all duration-300" style={{
                background: 'linear-gradient(135deg, #1A2A80 0%, #3B38A0 100%)'
              }}>
                <Plus className="h-6 w-6 text-white" />
              </div>
              <span className="text-xs font-medium" style={{ color: '#1A2A80' }}>New Folder</span>
            </button>
            
            <button 
              className="flex flex-col items-center p-4 rounded-xl transition-all duration-300 hover:shadow-lg group"
              style={{ backgroundColor: 'rgba(59, 56, 160, 0.05)' }}
              onMouseEnter={(e) => {
                e.target.style.backgroundColor = 'rgba(59, 56, 160, 0.1)';
                e.target.style.transform = 'translateY(-2px)';
              }}
              onMouseLeave={(e) => {
                e.target.style.backgroundColor = 'rgba(59, 56, 160, 0.05)';
                e.target.style.transform = 'translateY(0)';
              }}
            >
              <div className="w-12 h-12 rounded-xl flex items-center justify-center mb-2 group-hover:scale-110 transition-all duration-300" style={{
                background: 'linear-gradient(135deg, #3B38A0 0%, #7A85C1 100%)'
              }}>
                <Upload className="h-6 w-6 text-white" />
              </div>
              <span className="text-xs font-medium" style={{ color: '#1A2A80' }}>Upload</span>
            </button>
          </div>
        </div>

        {/* Quick Access */}
        <div>
          <h2 className="text-xs font-bold uppercase tracking-wider mb-6 px-4 flex items-center" style={{ color: '#7A85C1' }}>
            <Zap className="w-4 h-4 mr-2" />
            Quick Access
          </h2>
          <nav className="space-y-1">
            {quickAccessItems.map((item) => (
              <QuickAccessItem
                key={item.path}
                item={item}
                onClick={handleNavClick}
              />
            ))}
          </nav>
        </div>

        {/* Storage Info */}
        <div className="rounded-2xl p-6 shadow-lg border hover:shadow-xl transition-all duration-300 relative overflow-hidden" style={{
          background: 'linear-gradient(135deg, rgba(26, 42, 128, 0.05) 0%, rgba(255, 255, 255, 0.95) 100%)',
          borderColor: 'rgba(178, 176, 232, 0.2)'
        }}>
          {/* Background Pattern */}
          <div className="absolute inset-0 opacity-5">
            <svg width="100%" height="100%" viewBox="0 0 100 100">
              <defs>
                <pattern id="grid" width="10" height="10" patternUnits="userSpaceOnUse">
                  <path d="M 10 0 L 0 0 0 10" fill="none" stroke="#1A2A80" strokeWidth="0.5"/>
                </pattern>
              </defs>
              <rect width="100%" height="100%" fill="url(#grid)" />
            </svg>
          </div>
          
          <div className="relative z-10">
            <div className="flex items-center justify-between mb-4">
              <div className="flex items-center space-x-3">
                <div className="w-12 h-12 rounded-2xl flex items-center justify-center shadow-lg" style={{
                  background: 'linear-gradient(135deg, #7A85C1 0%, #B2B0E8 100%)'
                }}>
                  <Cloud className="h-6 w-6 text-white" />
                </div>
                <div>
                  <h3 className="font-bold text-lg" style={{ color: '#1A2A80' }}>Storage</h3>
                  <p className="text-sm" style={{ color: '#7A85C1' }}>Cloud Drive</p>
                </div>
              </div>
              <div className="text-right">
                <div className="text-2xl font-bold" style={{ color: '#1A2A80' }}>{storageUsed}%</div>
                <div className="text-xs" style={{ color: '#7A85C1' }}>Used</div>
              </div>
            </div>
            
            <div className="space-y-4">
              <div>
                <div className="flex justify-between text-sm mb-2">
                  <span className="text-gray-600 font-medium">2.4 GB of 15 GB used</span>
                  <span className="font-semibold" style={{ color: '#7A85C1' }}>12.6 GB free</span>
                </div>
                <div className="w-full bg-gray-200 rounded-full h-3 overflow-hidden shadow-inner">
                  <div 
                    className="h-3 rounded-full shadow-lg transition-all duration-1000 ease-out" 
                    style={{ 
                      width: `${storageUsed}%`,
                      background: 'linear-gradient(90deg, #3B38A0 0%, #7A85C1 100%)'
                    }}
                  ></div>
                </div>
              </div>
              
              {/* Storage breakdown */}
              <div className="grid grid-cols-2 gap-3 text-xs">
                {[
                  { label: 'Documents', color: '#1A2A80' },
                  { label: 'Images', color: '#3B38A0' },
                  { label: 'Videos', color: '#7A85C1' },
                  { label: 'Others', color: '#B2B0E8' }
                ].map((item) => (
                  <div key={item.label} className="flex items-center space-x-2">
                    <div className="w-3 h-3 rounded-full" style={{ backgroundColor: item.color }}></div>
                    <span className="text-gray-600">{item.label}</span>
                  </div>
                ))}
              </div>

              <button className="w-full py-2 text-sm font-semibold rounded-xl shadow-lg hover:shadow-xl transition-all duration-300 flex items-center justify-center space-x-2" style={{
                background: 'linear-gradient(135deg, #7A85C1 0%, #B2B0E8 100%)',
                color: 'white'
              }}>
                <TrendingUp className="h-4 w-4" />
                <span>Upgrade Storage</span>
              </button>
            </div>
          </div>
        </div>

        {/* Quick Stats */}
        <div className="bg-white rounded-2xl p-6 shadow-lg border hover:shadow-xl transition-all duration-300" style={{
          borderColor: 'rgba(178, 176, 232, 0.2)'
        }}>
          <h3 className="font-bold mb-4 flex items-center" style={{ color: '#1A2A80' }}>
            <BarChart3 className="w-5 h-5 mr-2" />
            This Week
          </h3>
          <div className="grid grid-cols-2 gap-4">
            <div className="text-center p-3 rounded-xl" style={{ backgroundColor: 'rgba(59, 56, 160, 0.05)' }}>
              <div className="text-2xl font-bold" style={{ color: '#3B38A0' }}>23</div>
              <div className="text-xs text-gray-500">Files Added</div>
            </div>
            <div className="text-center p-3 rounded-xl" style={{ backgroundColor: 'rgba(122, 133, 193, 0.05)' }}>
              <div className="text-2xl font-bold" style={{ color: '#7A85C1' }}>8</div>
              <div className="text-xs text-gray-500">Shared</div>
            </div>
          </div>
        </div>

        {/* Pro Upgrade Banner */}
        <div className="rounded-2xl p-6 shadow-lg relative overflow-hidden" style={{
          background: 'linear-gradient(135deg, #1A2A80 0%, #3B38A0 100%)'
        }}>
          <div className="absolute inset-0 opacity-10">
            <Sparkles className="w-full h-full" />
          </div>
          <div className="relative z-10 text-center text-white">
            <Sparkles className="w-8 h-8 mx-auto mb-3" />
            <h3 className="font-bold mb-2">Upgrade to Pro</h3>
            <p className="text-xs text-white/80 mb-4">Get unlimited storage and premium features</p>
            <button className="w-full py-2 px-4 bg-white text-[#1A2A80] rounded-xl font-semibold text-sm hover:bg-gray-100 transition-colors">
              Upgrade Now
            </button>
          </div>
        </div>
      </div>
    </aside>
  );
};

export default Sidebar;
