// src/pages/documents/MyDocuments.js
import React, { useState, useEffect } from 'react';
import documentService from '../../services/documentService';
import DocumentGrid from '../../components/documents/DocumentGrid';
import FolderGrid from '../../components/documents/FolderGrid';
import './DocumentPages.css';

const MyDocuments = () => {
  const [documents, setDocuments] = useState([]);
  const [folders, setFolders] = useState([]);
  const [loading, setLoading] = useState(true);
  const [view, setView] = useState('grid'); // 'grid' or 'list'

  useEffect(() => {
    const loadDocuments = async () => {
      try {
        const [docs, folds] = await Promise.all([
          documentService.getMyDocuments(),
          documentService.getFolders()
        ]);
        setDocuments(docs);
        setFolders(folds);
      } catch (error) {
        console.error('Failed to load documents:', error);
      } finally {
        setLoading(false);
      }
    };

    loadDocuments();
  }, []);

  if (loading) {
    return <div className="loading-spinner">Loading...</div>;
  }

  return (
    <div className="documents-page">
      <div className="page-header">
        <h1>My Documents</h1>
        <div className="view-controls">
          <button 
            className={`view-btn ${view === 'grid' ? 'active' : ''}`}
            onClick={() => setView('grid')}
          >
            Grid
          </button>
          <button 
            className={`view-btn ${view === 'list' ? 'active' : ''}`}
            onClick={() => setView('list')}
          >
            List
          </button>
        </div>
      </div>

      <div className="documents-content">
        {folders.length > 0 && (
          <section className="folders-section">
            <h2>Folders</h2>
            <FolderGrid folders={folders} view={view} />
          </section>
        )}

        <section className="documents-section">
          <h2>Documents</h2>
          <DocumentGrid documents={documents} view={view} />
        </section>
      </div>
    </div>
  );
};

export default MyDocuments;
