import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate, useLocation } from 'react-router-dom';
import Login from './components/auth/LoginPage';
import Register from './components/auth/Register';
import Header from './components/user/Header';
import Sidebar from './components/user/Sidebar';
import AdminSidebar from './components/admin/AdminSidebar';
import Footer from './components/user/Footer';
import Dashboard from './components/user/Dashboard';
import MyDrive from './components/user/MyDrive';
import SharedWithMe from './components/user/SharedWithMe';
import AdminDashboard from './components/admin/AdminDashboard';
import ProtectedRoute from './components/auth/ProtectedRoute';
import './App.css';

// Simple Settings component
const Settings = () => (
  <div className="p-6">
    <h1 className="text-3xl font-bold text-gray-800 mb-6">Settings</h1>
    <div className="bg-white rounded-xl shadow-lg p-6">
      <h2 className="text-xl font-semibold text-gray-800 mb-4">Account Settings</h2>
      <p className="text-gray-600 mb-4">
        Manage your account preferences and settings.
      </p>
      <div className="space-y-4">
        <div className="border-b border-gray-200 pb-4">
          <h3 className="font-medium text-gray-800 mb-2">Profile Information</h3>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">Name</label>
              <input
                type="text"
                defaultValue="John Doe"
                className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-[#7A85C1] focus:border-transparent"
              />
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">Email</label>
              <input
                type="email"
                defaultValue="john.doe@example.com"
                className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-[#7A85C1] focus:border-transparent"
              />
            </div>
          </div>
        </div>
        <div className="border-b border-gray-200 pb-4">
          <h3 className="font-medium text-gray-800 mb-2">Notifications</h3>
          <div className="space-y-2">
            <label className="flex items-center space-x-3">
              <input type="checkbox" defaultChecked className="rounded text-[#3B38A0] focus:ring-[#7A85C1]" />
              <span className="text-sm text-gray-700">Email notifications for shared files</span>
            </label>
            <label className="flex items-center space-x-3">
              <input type="checkbox" defaultChecked className="rounded text-[#3B38A0] focus:ring-[#7A85C1]" />
              <span className="text-sm text-gray-700">Push notifications for file updates</span>
            </label>
          </div>
        </div>
        <div className="flex space-x-4">
          <button className="px-6 py-2 bg-[#3B38A0] text-white font-medium rounded-lg hover:bg-[#1A2A80] transition-colors">
            Save Changes
          </button>
          <button className="px-6 py-2 border border-gray-300 text-gray-700 font-medium rounded-lg hover:bg-gray-50 transition-colors">
            Cancel
          </button>
        </div>
      </div>
    </div>
  </div>
);

// Authentication helpers
const isAuthenticated = () => !!localStorage.getItem('token');
const getUserRole = () => {
  const user = localStorage.getItem('user');
  if (user) {
    try { return JSON.parse(user).role; } 
    catch { return null; }
  }
  return null;
};

// Dashboard layout
const DashboardLayout = ({ children, userRole }) => {
  const [activeSection, setActiveSection] = useState(userRole === "ADMIN" ? "admin-overview" : "dashboard");
  const location = useLocation();

  useEffect(() => {
    const path = location.pathname;
    if (userRole === "ADMIN") {
      if (path.includes('users')) setActiveSection('admin-users');
      else if (path.includes('documents')) setActiveSection('admin-documents');
      else if (path.includes('logs')) setActiveSection('admin-logs');
      else if (path.includes('settings')) setActiveSection('admin-settings');
      else setActiveSection('admin-overview');
    } else {
      if (path.includes('mydrive')) setActiveSection('mydrive');
      else if (path.includes('shared')) setActiveSection('shared');
      else if (path.includes('settings')) setActiveSection('settings');
      else setActiveSection('dashboard');
    }
  }, [location.pathname, userRole]);

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    window.location.href = '/login';
  };

  const handleSectionChange = (section) => {
    setActiveSection(section);
  };

  return (
    <div className="min-h-screen bg-gray-50 flex flex-col">
      <Header />
      <div className="flex flex-1">
        {userRole === "ADMIN" ? (
          <AdminSidebar activeSection={activeSection} setActiveSection={handleSectionChange} onLogout={handleLogout} />
        ) : (
          <Sidebar activeSection={activeSection} setActiveSection={handleSectionChange} onLogout={handleLogout} />
        )}
        <main className="flex-1 overflow-y-auto">{children}</main>
      </div>
      <Footer />
    </div>
  );
};

// User routes
const UserDashboardRoutes = () => {
  const userRole = getUserRole();
  return (
    <DashboardLayout userRole={userRole}>
      <Routes>
        <Route index element={<Dashboard />} />
        <Route path="mydrive" element={<MyDrive />} />
        <Route path="shared" element={<SharedWithMe />} />
        <Route path="settings" element={<Settings />} />
        <Route path="*" element={<Navigate to="/user-dashboard" replace />} />
      </Routes>
    </DashboardLayout>
  );
};

// Admin routes
const AdminDashboardRoutes = () => {
  const userRole = getUserRole();
  return (
    <DashboardLayout userRole={userRole}>
      <Routes>
        <Route index element={<AdminDashboard activeTab="overview" />} />
        <Route path="users" element={<AdminDashboard activeTab="users" />} />
        <Route path="documents" element={<AdminDashboard activeTab="documents" />} />
        <Route path="logs" element={<AdminDashboard activeTab="logs" />} />
        <Route path="settings" element={<AdminDashboard activeTab="settings" />} />
        <Route path="*" element={<Navigate to="/admin-dashboard" replace />} />
      </Routes>
    </DashboardLayout>
  );
};

// Main App
function App() {
  return (
    <Router>
      <Routes>
        {/* Public routes */}
        <Route path="/login" element={!isAuthenticated() ? <Login /> : <Navigate to={getUserRole() === 'ADMIN' ? '/admin-dashboard' : '/user-dashboard'} replace />} />
        <Route path="/register" element={!isAuthenticated() ? <Register /> : <Navigate to={getUserRole() === 'ADMIN' ? '/admin-dashboard' : '/user-dashboard'} replace />} />

        {/* Protected routes */}
        <Route path="/admin-dashboard/*" element={<ProtectedRoute requiredRole="ADMIN"><AdminDashboardRoutes /></ProtectedRoute>} />
        <Route path="/user-dashboard/*" element={<ProtectedRoute requiredRole="USER"><UserDashboardRoutes /></ProtectedRoute>} />

        {/* Default & fallback */}
        <Route path="/" element={isAuthenticated() ? <Navigate to={getUserRole() === 'ADMIN' ? '/admin-dashboard' : '/user-dashboard'} replace /> : <Navigate to="/login" replace />} />
        <Route path="*" element={<Navigate to="/" replace />} />
      </Routes>
    </Router>
  );
}

export default App;
