import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { documentService } from '../../services/documentService';
import { folderService } from '../../services/folderService';
import FolderCard from '../common/FolderCard';
import FileCard from '../common/FileCard';
import ShareModal from '../common/ShareModal';
import { 
  FileText, 
  FolderOpen, 
  Users, 
  TrendingUp, 
  Plus,
  ArrowRight,
  Clock,
  Globe
} from 'lucide-react';
import toast from 'react-hot-toast';

const Dashboard = () => {
  const [recentDocuments, setRecentDocuments] = useState([]);
  const [recentFolders, setRecentFolders] = useState([]);
  const [publicDocuments, setPublicDocuments] = useState([]);
  const [stats, setStats] = useState({
    totalDocuments: 0,
    totalFolders: 0,
    sharedDocuments: 0
  });
  const [loading, setLoading] = useState(true);
  const [showShareModal, setShowShareModal] = useState(false);
  const [shareItem, setShareItem] = useState(null);
  const [shareItemType, setShareItemType] = useState('');

  useEffect(() => {
    fetchDashboardData();
  }, []);

  const fetchDashboardData = async () => {
    try {
      const [documents, folders, publicDocs] = await Promise.all([
        documentService.getMyDocuments(),
        folderService.getRootFolders(),
        documentService.getPublicDocuments()
      ]);

      // Sort by creation date (most recent first)
      const sortedDocs = documents.sort((a, b) => new Date(b.createdAt) - new Date(a.createdAt));
      const sortedFolders = folders.sort((a, b) => new Date(b.createdAt) - new Date(a.createdAt));

      setRecentDocuments(sortedDocs.slice(0, 4));
      setRecentFolders(sortedFolders.slice(0, 4));
      setPublicDocuments(publicDocs.slice(0, 4));

      setStats({
        totalDocuments: documents.length,
        totalFolders: folders.length,
        sharedDocuments: documents.filter(doc => doc.isPublic).length
      });
    } catch (error) {
      console.error('Error fetching dashboard data:', error);
      toast.error('Failed to load dashboard data');
    } finally {
      setLoading(false);
    }
  };

  const handleViewDocument = (document) => {
    toast.info('Document viewer will be implemented');
    console.log('Viewing document:', document);
  };

  const handleShare = (item, type) => {
    setShareItem(item);
    setShareItemType(type);
    setShowShareModal(true);
  };

  const handleShareSubmit = async (item, shareWith) => {
    try {
      // Implement sharing logic with backend
      console.log('Sharing', item, 'with', shareWith);
      toast.success(`${shareItemType} shared successfully!`);
    } catch (error) {
      toast.error('Failed to share item');
    }
  };

  const handleDeleteItem = async (id, type) => {
    if (!window.confirm(`Are you sure you want to delete this ${type.toLowerCase()}?`)) {
      return;
    }

    try {
      if (type === 'Document') {
        await documentService.deleteDocument(id);
      } else {
        await folderService.deleteFolder(id);
      }
      toast.success(`${type} deleted successfully`);
      fetchDashboardData();
    } catch (error) {
      toast.error(`Failed to delete ${type.toLowerCase()}`);
    }
  };

  const StatCard = ({ icon: Icon, title, value, color, trend }) => (
    <div className="bg-white rounded-xl border border-gray-200 p-6 hover:shadow-lg transition-shadow duration-200">
      <div className="flex items-center justify-between">
        <div>
          <p className="text-sm text-gray-600">{title}</p>
          <p className="text-2xl font-bold text-gray-900 mt-1">{value}</p>
          {trend && (
            <div className="flex items-center mt-2 text-sm text-green-600">
              <TrendingUp className="h-4 w-4 mr-1" />
              <span>{trend}</span>
            </div>
          )}
        </div>
        <div className={`p-3 rounded-lg bg-gradient-to-br ${color}`}>
          <Icon className="h-6 w-6 text-white" />
        </div>
      </div>
    </div>
  );

  const QuickAction = ({ icon: Icon, title, description, onClick, color }) => (
    <button
      onClick={onClick}
      className="w-full p-4 bg-white rounded-lg border border-gray-200 hover:border-blue-300 hover:shadow-md transition-all duration-200 text-left group"
    >
      <div className="flex items-center space-x-3">
        <div className={`p-2 rounded-lg bg-gradient-to-br ${color}`}>
          <Icon className="h-5 w-5 text-white" />
        </div>
        <div className="flex-1">
          <h3 className="font-medium text-gray-900 group-hover:text-blue-700 transition-colors">
            {title}
          </h3>
          <p className="text-sm text-gray-500">{description}</p>
        </div>
        <ArrowRight className="h-4 w-4 text-gray-400 group-hover:text-blue-600 transition-colors" />
      </div>
    </button>
  );

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-96">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-4 border-blue-600 border-t-transparent mx-auto mb-4"></div>
          <p className="text-gray-600">Loading dashboard...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="space-y-8">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-3xl font-bold text-gray-900">Dashboard</h1>
          <p className="text-gray-600 mt-1">
            Welcome back! Here's what's happening with your documents.
          </p>
        </div>
        <div className="flex space-x-3">
          <Link
            to="/my-drive"
            className="px-4 py-2 bg-white border border-gray-300 rounded-lg hover:bg-gray-50 transition-colors flex items-center space-x-2"
          >
            <FolderOpen className="h-4 w-4" />
            <span>My Drive</span>
          </Link>
          <Link
            to="/my-drive"
            className="px-4 py-2 bg-blue-600 hover:bg-blue-700 text-white rounded-lg transition-colors flex items-center space-x-2"
          >
            <Plus className="h-4 w-4" />
            <span>New</span>
          </Link>
        </div>
      </div>

      {/* Stats */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        <StatCard
          icon={FileText}
          title="Total Documents"
          value={stats.totalDocuments}
          color="from-blue-500 to-blue-600"
          trend="+12% from last month"
        />
        <StatCard
          icon={FolderOpen}
          title="Total Folders"
          value={stats.totalFolders}
          color="from-green-500 to-green-600"
          trend="+8% from last month"
        />
        <StatCard
          icon={Globe}
          title="Shared Documents"
          value={stats.sharedDocuments}
          color="from-purple-500 to-purple-600"
          trend="+23% from last month"
        />
      </div>

      {/* Quick Actions */}
      <div className="bg-white rounded-xl border border-gray-200 p-6">
        <h2 className="text-lg font-semibold text-gray-900 mb-4">Quick Actions</h2>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
          <QuickAction
            icon={Plus}
            title="Create Folder"
            description="Organize your files"
            onClick={() => window.location.href = '/my-drive'}
            color="from-blue-500 to-blue-600"
          />
          <QuickAction
            icon={FileText}
            title="Upload Document"
            description="Add new files"
            onClick={() => window.location.href = '/my-drive'}
            color="from-green-500 to-green-600"
          />
          <QuickAction
            icon={Users}
            title="Shared Files"
            description="View shared content"
            onClick={() => window.location.href = '/shared-with-me'}
            color="from-purple-500 to-purple-600"
          />
          <QuickAction
            icon={Clock}
            title="Recent Activity"
            description="See latest changes"
            onClick={() => toast.info('Recent activity view coming soon!')}
            color="from-orange-500 to-orange-600"
          />
        </div>
      </div>

      {/* Content Sections */}
      <div className="grid grid-cols-1 xl:grid-cols-2 gap-8">
        {/* Recent Documents */}
        <div className="bg-white rounded-xl border border-gray-200">
          <div className="flex items-center justify-between p-6 border-b border-gray-200">
            <h2 className="text-lg font-semibold text-gray-900 flex items-center space-x-2">
              <Clock className="h-5 w-5 text-gray-500" />
              <span>Recent Documents</span>
            </h2>
            <Link
              to="/my-drive"
              className="text-sm text-blue-600 hover:text-blue-700 font-medium flex items-center space-x-1"
            >
              <span>View all</span>
              <ArrowRight className="h-4 w-4" />
            </Link>
          </div>
          <div className="p-6">
            {recentDocuments.length === 0 ? (
              <div className="text-center py-8">
                <FileText className="h-12 w-12 text-gray-300 mx-auto mb-4" />
                <p className="text-gray-500">No documents yet</p>
                <Link
                  to="/my-drive"
                  className="text-blue-600 hover:text-blue-700 text-sm font-medium"
                >
                  Upload your first document
                </Link>
              </div>
            ) : (
              <div className="space-y-4">
                {recentDocuments.map(doc => (
                  <FileCard
                    key={doc.id}
                    document={doc}
                    onView={handleViewDocument}
                    onShare={() => handleShare(doc, 'Document')}
                    onDelete={(id) => handleDeleteItem(id, 'Document')}
                  />
                ))}
              </div>
            )}
          </div>
        </div>

        {/* Recent Folders */}
        <div className="bg-white rounded-xl border border-gray-200">
          <div className="flex items-center justify-between p-6 border-b border-gray-200">
            <h2 className="text-lg font-semibold text-gray-900 flex items-center space-x-2">
              <FolderOpen className="h-5 w-5 text-gray-500" />
              <span>Recent Folders</span>
            </h2>
            <Link
              to="/my-drive"
              className="text-sm text-blue-600 hover:text-blue-700 font-medium flex items-center space-x-1"
            >
              <span>View all</span>
              <ArrowRight className="h-4 w-4" />
            </Link>
          </div>
          <div className="p-6">
            {recentFolders.length === 0 ? (
              <div className="text-center py-8">
                <FolderOpen className="h-12 w-12 text-gray-300 mx-auto mb-4" />
                <p className="text-gray-500">No folders yet</p>
                <Link
                  to="/my-drive"
                  className="text-blue-600 hover:text-blue-700 text-sm font-medium"
                >
                  Create your first folder
                </Link>
              </div>
            ) : (
              <div className="space-y-4">
                {recentFolders.map(folder => (
                  <FolderCard
                    key={folder.id}
                    folder={folder}
                    onOpen={() => window.location.href = '/my-drive'}
                    onShare={() => handleShare(folder, 'Folder')}
                    onDelete={(id) => handleDeleteItem(id, 'Folder')}
                  />
                ))}
              </div>
            )}
          </div>
        </div>
      </div>

      {/* Public Documents */}
      <div className="bg-white rounded-xl border border-gray-200">
        <div className="flex items-center justify-between p-6 border-b border-gray-200">
          <h2 className="text-lg font-semibold text-gray-900 flex items-center space-x-2">
            <Globe className="h-5 w-5 text-gray-500" />
            <span>Public Documents</span>
          </h2>
          <Link
            to="/shared-with-me"
            className="text-sm text-blue-600 hover:text-blue-700 font-medium flex items-center space-x-1"
          >
            <span>View all</span>
            <ArrowRight className="h-4 w-4" />
          </Link>
        </div>
        <div className="p-6">
          {publicDocuments.length === 0 ? (
            <div className="text-center py-8">
              <Globe className="h-12 w-12 text-gray-300 mx-auto mb-4" />
              <p className="text-gray-500">No public documents available</p>
            </div>
          ) : (
            <div className="grid grid-cols-1 md:grid-cols-2 xl:grid-cols-4 gap-4">
              {publicDocuments.map(doc => (
                <FileCard
                  key={doc.id}
                  document={doc}
                  onView={handleViewDocument}
                  onShare={() => handleShare(doc, 'Document')}
                  onDelete={() => {}}
                  showActions={false}
                />
              ))}
            </div>
          )}
        </div>
      </div>

      {/* Share Modal */}
      <ShareModal
        isOpen={showShareModal}
        onClose={() => setShowShareModal(false)}
        item={shareItem}
        itemType={shareItemType}
        onShare={handleShareSubmit}
      />
    </div>
  );
};

export default Dashboard;
