import React from 'react';
import { 
  File, 
  Home, 
  Share2, 
  Folder, 
  Activity, 
  Settings, 
  User,
  LogOut,
  X
} from 'lucide-react';

const Sidebar = ({ 
  currentView, 
  setCurrentView, 
  sidebarOpen, 
  setSidebarOpen, 
  user, 
  onLogout 
}) => {
  const navigationItems = [
    { id: 'dashboard', label: 'Dashboard', icon: Home },
    { id: 'documents', label: 'My Documents', icon: File },
    { id: 'shared', label: 'Shared with Me', icon: Share2 },
    { id: 'folders', label: 'Folders', icon: Folder },
    { id: 'activity', label: 'Activity', icon: Activity },
    { id: 'settings', label: 'Settings', icon: Settings }
  ];

  return (
    <div className={`fixed inset-y-0 left-0 z-50 w-64 transform transition-transform duration-300 ease-in-out lg:translate-x-0 lg:static lg:inset-0 ${sidebarOpen ? 'translate-x-0' : '-translate-x-full'}`}>
      <div className="flex flex-col h-full bg-gradient-to-br from-[#1A2A80] via-[#3B38A0] to-[#7A85C1] backdrop-blur-xl border-r border-white/10 shadow-2xl">
        {/* Logo */}
        <div className="flex items-center justify-between px-6 py-6">
          <div className="flex items-center space-x-3">
            <div className="w-8 h-8 bg-gradient-to-br from-[#B2B0E8] to-white rounded-lg flex items-center justify-center shadow-lg">
              <File className="w-4 h-4 text-[#1A2A80]" />
            </div>
            <span className="text-xl font-bold text-white">DocShare</span>
          </div>
          <button
            onClick={() => setSidebarOpen(false)}
            className="lg:hidden text-white hover:text-[#B2B0E8] transition-colors p-2 rounded-lg hover:bg-white/10"
          >
            <X className="w-5 h-5" />
          </button>
        </div>

        {/* Navigation */}
        <nav className="flex-1 px-4 pb-4">
          <div className="space-y-2">
            {navigationItems.map((item) => {
              const Icon = item.icon;
              return (
                <button
                  key={item.id}
                  onClick={() => {
                    setCurrentView(item.id);
                    setSidebarOpen(false);
                  }}
                  className={`w-full flex items-center space-x-3 px-4 py-3 text-left rounded-xl transition-all duration-200 group ${
                    currentView === item.id
                      ? 'bg-white/20 text-white shadow-lg backdrop-blur-sm border border-white/20'
                      : 'text-white/80 hover:text-white hover:bg-white/10'
                  }`}
                >
                  <Icon className={`w-5 h-5 transition-transform group-hover:scale-110 ${
                    currentView === item.id ? 'text-[#B2B0E8]' : ''
                  }`} />
                  <span className="font-medium">{item.label}</span>
                </button>
              );
            })}
          </div>
        </nav>

        {/* User Profile */}
        <div className="p-4 border-t border-white/10">
          <div className="flex items-center space-x-3 p-3 rounded-xl bg-white/10 backdrop-blur-sm mb-3">
            <div className="w-10 h-10 bg-gradient-to-br from-[#B2B0E8] to-white rounded-full flex items-center justify-center">
              <User className="w-5 h-5 text-[#1A2A80]" />
            </div>
            <div className="flex-1 min-w-0">
              <p className="text-white font-semibold text-sm truncate">{user?.name || 'User'}</p>
              <p className="text-white/70 text-xs truncate">{user?.email || 'user@example.com'}</p>
            </div>
          </div>
          
          <button
            onClick={onLogout}
            className="w-full flex items-center space-x-3 px-4 py-3 text-white/80 hover:text-white hover:bg-white/10 rounded-xl transition-all duration-200 group"
          >
            <LogOut className="w-5 h-5 transition-transform group-hover:scale-110" />
            <span className="font-medium">Logout</span>
          </button>
        </div>
      </div>
    </div>
  );
};

export default Sidebar;
