import React, { useState } from 'react';
import { 
  Activity, 
  Upload, 
  Download, 
  Share2, 
  Trash2, 
  Edit3, 
  Eye,
  FolderPlus,
  User,
  Calendar,
  Filter,
  Search,
  Clock,
  File,
  CheckCircle,
  AlertCircle,
  Info
} from 'lucide-react';

const ActivityLog = () => {
  const [searchQuery, setSearchQuery] = useState('');
  const [filterType, setFilterType] = useState('all');
  const [filterUser, setFilterUser] = useState('all');
  const [dateRange, setDateRange] = useState('all');

  // Mock activity data
  const [activities] = useState([
    {
      id: 1,
      type: 'upload',
      action: 'uploaded',
      document: 'Project Proposal.pdf',
      user: 'John Doe',
      timestamp: '2024-08-20 10:30 AM',
      details: 'Uploaded to Projects folder',
      status: 'success',
      size: '2.4 MB'
    },
    {
      id: 2,
      type: 'share',
      action: 'shared',
      document: 'Design Mockups.jpg',
      user: 'John Doe',
      timestamp: '2024-08-20 09:15 AM',
      details: 'Shared with sarah@example.com',
      status: 'success',
      recipient: 'Sarah Johnson'
    },
    {
      id: 3,
      type: 'download',
      action: 'downloaded',
      document: 'Meeting Notes.docx',
      user: 'Mike Chen',
      timestamp: '2024-08-20 08:45 AM',
      details: 'Downloaded from shared folder',
      status: 'success',
      size: '1.2 MB'
    },
    {
      id: 4,
      type: 'create_folder',
      action: 'created folder',
      document: 'Marketing Materials',
      user: 'John Doe',
      timestamp: '2024-08-19 04:20 PM',
      details: 'New folder created',
      status: 'success'
    },
    {
      id: 5,
      type: 'edit',
      action: 'modified',
      document: 'Budget Spreadsheet.xlsx',
      user: 'Emma Davis',
      timestamp: '2024-08-19 02:10 PM',
      details: 'Document permissions updated',
      status: 'success'
    },
    {
      id: 6,
      type: 'view',
      action: 'viewed',
      document: 'Contract Template.pdf',
      user: 'Alex Smith',
      timestamp: '2024-08-19 11:30 AM',
      details: 'Document accessed via shared link',
      status: 'info'
    },
    {
      id: 7,
      type: 'delete',
      action: 'deleted',
      document: 'Old Proposal.pdf',
      user: 'John Doe',
      timestamp: '2024-08-19 10:15 AM',
      details: 'Moved to trash',
      status: 'warning'
    },
    {
      id: 8,
      type: 'upload',
      action: 'upload failed',
      document: 'Large Video.mp4',
      user: 'John Doe',
      timestamp: '2024-08-18 03:45 PM',
      details: 'File size exceeds limit (50MB)',
      status: 'error'
    }
  ]);

  // Get unique users for filter
  const users = [...new Set(activities.map(activity => activity.user))];

  // Filter activities
  const filteredActivities = activities.filter(activity => {
    const matchesSearch = activity.document.toLowerCase().includes(searchQuery.toLowerCase()) ||
                         activity.user.toLowerCase().includes(searchQuery.toLowerCase()) ||
                         activity.action.toLowerCase().includes(searchQuery.toLowerCase());
    const matchesType = filterType === 'all' || activity.type === filterType;
    const matchesUser = filterUser === 'all' || activity.user === filterUser;
    const matchesDate = dateRange === 'all' || checkDateRange(activity.timestamp, dateRange);
    
    return matchesSearch && matchesType && matchesUser && matchesDate;
  });

  const checkDateRange = (timestamp, range) => {
    const activityDate = new Date(timestamp);
    const today = new Date();
    const diffTime = Math.abs(today - activityDate);
    const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));

    switch (range) {
      case 'today': return diffDays <= 1;
      case 'week': return diffDays <= 7;
      case 'month': return diffDays <= 30;
      default: return true;
    }
  };

  const getActivityIcon = (type, status) => {
    const iconClass = "w-5 h-5";
    
    if (status === 'error') {
      return <AlertCircle className={`${iconClass} text-red-500`} />;
    }

    switch (type) {
      case 'upload': return <Upload className={`${iconClass} text-blue-500`} />;
      case 'download': return <Download className={`${iconClass} text-green-500`} />;
      case 'share': return <Share2 className={`${iconClass} text-purple-500`} />;
      case 'delete': return <Trash2 className={`${iconClass} text-red-500`} />;
      case 'edit': return <Edit3 className={`${iconClass} text-orange-500`} />;
      case 'view': return <Eye className={`${iconClass} text-indigo-500`} />;
      case 'create_folder': return <FolderPlus className={`${iconClass} text-teal-500`} />;
      default: return <Activity className={`${iconClass} text-gray-500`} />;
    }
  };

  const getStatusBadge = (status) => {
    switch (status) {
      case 'success':
        return <CheckCircle className="w-4 h-4 text-green-500" />;
      case 'error':
        return <AlertCircle className="w-4 h-4 text-red-500" />;
      case 'warning':
        return <AlertCircle className="w-4 h-4 text-yellow-500" />;
      case 'info':
        return <Info className="w-4 h-4 text-blue-500" />;
      default:
        return <CheckCircle className="w-4 h-4 text-green-500" />;
    }
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'success': return 'border-l-green-400 bg-green-50/50';
      case 'error': return 'border-l-red-400 bg-red-50/50';
      case 'warning': return 'border-l-yellow-400 bg-yellow-50/50';
      case 'info': return 'border-l-blue-400 bg-blue-50/50';
      default: return 'border-l-gray-400 bg-gray-50/50';
    }
  };

  // Activity stats
  const stats = {
    total: activities.length,
    today: activities.filter(a => checkDateRange(a.timestamp, 'today')).length,
    uploads: activities.filter(a => a.type === 'upload').length,
    shares: activities.filter(a => a.type === 'share').length
  };

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex flex-col sm:flex-row sm:items-center sm:justify-between gap-4">
        <div>
          <h1 className="text-3xl font-bold text-gray-800">Activity Log</h1>
          <p className="text-gray-600 mt-1">Track all document activities and user actions</p>
        </div>
      </div>

      {/* Statistics */}
      <div className="grid grid-cols-1 md:grid-cols-4 gap-6">
        <div className="bg-gradient-to-r from-[#7A85C1] to-[#B2B0E8] rounded-2xl p-6 text-white">
          <div className="flex items-center space-x-3">
            <Upload className="w-8 h-8" />
            <div>
              <p className="text-white/80 text-sm">Uploads</p>
              <p className="text-2xl font-bold">{stats.uploads}</p>
            </div>
          </div>
        </div>

        <div className="bg-gradient-to-r from-[#B2B0E8] to-[#1A2A80] rounded-2xl p-6 text-white">
          <div className="flex items-center space-x-3">
            <Share2 className="w-8 h-8" />
            <div>
              <p className="text-white/80 text-sm">Shares</p>
              <p className="text-2xl font-bold">{stats.shares}</p>
            </div>
          </div>
        </div>
      </div>

      {/* Filters */}
      <div className="bg-white/80 backdrop-blur-lg rounded-2xl shadow-lg border border-white/20 p-6">
        <div className="grid grid-cols-1 lg:grid-cols-4 gap-4">
          {/* Search */}
          <div className="relative">
            <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 w-5 h-5 text-gray-400" />
            <input
              type="text"
              placeholder="Search activities..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              className="w-full pl-10 pr-4 py-3 bg-white/50 border border-gray-200 rounded-xl focus:outline-none focus:ring-2 focus:ring-[#7A85C1] focus:border-transparent transition-all duration-200"
            />
          </div>

          {/* Activity Type Filter */}
          <select
            value={filterType}
            onChange={(e) => setFilterType(e.target.value)}
            className="px-4 py-3 bg-white/50 border border-gray-200 rounded-xl focus:outline-none focus:ring-2 focus:ring-[#7A85C1] focus:border-transparent transition-all duration-200"
          >
            <option value="all">All Activities</option>
            <option value="upload">Uploads</option>
            <option value="download">Downloads</option>
            <option value="share">Shares</option>
            <option value="view">Views</option>
            <option value="edit">Edits</option>
            <option value="delete">Deletes</option>
          </select>

          {/* User Filter */}
          <select
            value={filterUser}
            onChange={(e) => setFilterUser(e.target.value)}
            className="px-4 py-3 bg-white/50 border border-gray-200 rounded-xl focus:outline-none focus:ring-2 focus:ring-[#7A85C1] focus:border-transparent transition-all duration-200"
          >
            <option value="all">All Users</option>
            {users.map(user => (
              <option key={user} value={user}>{user}</option>
            ))}
          </select>

          {/* Date Range Filter */}
          <select
            value={dateRange}
            onChange={(e) => setDateRange(e.target.value)}
            className="px-4 py-3 bg-white/50 border border-gray-200 rounded-xl focus:outline-none focus:ring-2 focus:ring-[#7A85C1] focus:border-transparent transition-all duration-200"
          >
            <option value="all">All Time</option>
            <option value="today">Today</option>
            <option value="week">This Week</option>
            <option value="month">This Month</option>
          </select>
        </div>
      </div>

      {/* Activity List */}
      <div className="bg-white/80 backdrop-blur-lg rounded-2xl shadow-lg border border-white/20 overflow-hidden">
        <div className="p-6 border-b border-gray-200/50">
          <div className="flex items-center justify-between">
            <h2 className="text-xl font-bold text-gray-800">Recent Activities</h2>
            <span className="text-sm text-gray-500">{filteredActivities.length} activities</span>
          </div>
        </div>

        <div className="max-h-96 overflow-y-auto">
          {filteredActivities.length > 0 ? (
            <div className="divide-y divide-gray-200/50">
              {filteredActivities.map((activity, index) => (
                <div
                  key={activity.id}
                  className={`p-6 hover:bg-gray-50/50 transition-colors border-l-4 ${getStatusColor(activity.status)}`}
                >
                  <div className="flex items-start space-x-4">
                    {/* Icon */}
                    <div className="flex-shrink-0 w-10 h-10 bg-gray-100 rounded-full flex items-center justify-center">
                      {getActivityIcon(activity.type, activity.status)}
                    </div>

                    {/* Content */}
                    <div className="flex-1 min-w-0">
                      <div className="flex items-start justify-between">
                        <div className="flex-1">
                          <p className="text-sm font-medium text-gray-800 mb-1">
                            <span className="font-semibold text-[#3B38A0]">{activity.user}</span>
                            {' '}{activity.action}{' '}
                            <span className="font-semibold">{activity.document}</span>
                          </p>
                          
                          <p className="text-sm text-gray-600 mb-2">{activity.details}</p>
                          
                          <div className="flex items-center space-x-4 text-xs text-gray-500">
                            <div className="flex items-center space-x-1">
                              <Calendar className="w-3 h-3" />
                              <span>{activity.timestamp}</span>
                            </div>
                            {activity.size && (
                              <div className="flex items-center space-x-1">
                                <File className="w-3 h-3" />
                                <span>{activity.size}</span>
                              </div>
                            )}
                            {activity.recipient && (
                              <div className="flex items-center space-x-1">
                                <User className="w-3 h-3" />
                                <span>to {activity.recipient}</span>
                              </div>
                            )}
                          </div>
                        </div>

                        {/* Status Badge */}
                        <div className="flex-shrink-0 ml-4">
                          {getStatusBadge(activity.status)}
                        </div>
                      </div>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          ) : (
            <div className="p-12 text-center">
              <div className="w-24 h-24 bg-gray-200 rounded-full flex items-center justify-center mx-auto mb-4">
                <Activity className="w-12 h-12 text-gray-400" />
              </div>
              <h3 className="text-xl font-semibold text-gray-800 mb-2">No activities found</h3>
              <p className="text-gray-600">Try adjusting your search or filters</p>
            </div>
          )}
        </div>
      </div>

      {/* Activity Timeline (Recent 5 activities) */}
      <div className="bg-white/80 backdrop-blur-lg rounded-2xl shadow-lg border border-white/20 p-6">
        <h2 className="text-xl font-bold text-gray-800 mb-6">Activity Timeline</h2>
        
        <div className="relative">
          {/* Timeline line */}
          <div className="absolute left-6 top-0 bottom-0 w-0.5 bg-gradient-to-b from-[#1A2A80] to-[#B2B0E8]"></div>
          
          {/* Timeline items */}
          <div className="space-y-6">
            {activities.slice(0, 5).map((activity, index) => (
              <div key={activity.id} className="relative flex items-start space-x-4">
                {/* Timeline dot */}
                <div className={`relative z-10 w-12 h-12 rounded-full flex items-center justify-center shadow-lg ${
                  activity.status === 'success' ? 'bg-gradient-to-r from-green-400 to-green-600' :
                  activity.status === 'error' ? 'bg-gradient-to-r from-red-400 to-red-600' :
                  activity.status === 'warning' ? 'bg-gradient-to-r from-yellow-400 to-yellow-600' :
                  'bg-gradient-to-r from-blue-400 to-blue-600'
                }`}>
                  {getActivityIcon(activity.type, 'success')}
                </div>

                {/* Content */}
                <div className="flex-1 bg-white/50 rounded-xl p-4 border border-gray-200/50">
                  <div className="flex items-center justify-between mb-2">
                    <p className="font-semibold text-gray-800">
                      {activity.user} {activity.action} {activity.document}
                    </p>
                    <span className="text-xs text-gray-500">{activity.timestamp}</span>
                  </div>
                  <p className="text-sm text-gray-600">{activity.details}</p>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default ActivityLog;
