import React, { useState } from 'react';
import { 
  X, 
  Share2, 
  Mail, 
  Link, 
  Eye, 
  Edit, 
  UserPlus,
  Copy,
  Check
} from 'lucide-react';

const ShareModal = ({ isOpen, onClose, document, onShare }) => {
  const [shareMethod, setShareMethod] = useState('email');
  const [email, setEmail] = useState('');
  const [emails, setEmails] = useState([]);
  const [permission, setPermission] = useState('view');
  const [message, setMessage] = useState('');
  const [linkCopied, setLinkCopied] = useState(false);
  const [publicLink, setPublicLink] = useState('');

  const handleAddEmail = () => {
    if (email && email.includes('@') && !emails.includes(email)) {
      setEmails([...emails, email]);
      setEmail('');
    }
  };

  const removeEmail = (emailToRemove) => {
    setEmails(emails.filter(e => e !== emailToRemove));
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      e.preventDefault();
      handleAddEmail();
    }
  };

  const generatePublicLink = () => {
    const link = `${window.location.origin}/shared/${document?.id}?token=abc123`;
    setPublicLink(link);
  };

  const copyLink = async () => {
    try {
      await navigator.clipboard.writeText(publicLink);
      setLinkCopied(true);
      setTimeout(() => setLinkCopied(false), 2000);
    } catch (err) {
      console.error('Failed to copy link:', err);
    }
  };

  const handleShare = () => {
    const shareData = {
      method: shareMethod,
      emails: emails,
      permission: permission,
      message: message,
      publicLink: shareMethod === 'link' ? publicLink : null
    };
    
    onShare(shareData);
  };

  const handleClose = () => {
    setEmail('');
    setEmails([]);
    setMessage('');
    setPublicLink('');
    setLinkCopied(false);
    onClose();
  };

  if (!isOpen || !document) return null;

  return (
    <div className="fixed inset-0 bg-black/50 backdrop-blur-sm z-50 flex items-center justify-center p-4">
      <div className="bg-white rounded-2xl max-w-lg w-full shadow-xl">
        {/* Header */}
        <div className="flex items-center justify-between p-6 border-b border-gray-200">
          <div>
            <h2 className="text-xl font-semibold text-gray-900">Share Document</h2>
            <p className="text-sm text-gray-500 mt-1">{document.name}</p>
          </div>
          <button
            onClick={handleClose}
            className="p-2 hover:bg-gray-100 rounded-lg transition-colors"
          >
            <X className="w-5 h-5 text-gray-500" />
          </button>
        </div>

        <div className="p-6">
          {/* Share Method Tabs */}
          <div className="flex space-x-1 p-1 bg-gray-100 rounded-lg mb-6">
            <button
              onClick={() => setShareMethod('email')}
              className={`flex-1 flex items-center justify-center space-x-2 py-2 px-3 rounded-md text-sm font-medium transition-colors ${
                shareMethod === 'email' 
                  ? 'bg-white text-[#1A2A80] shadow-sm' 
                  : 'text-gray-500 hover:text-gray-700'
              }`}
            >
              <Mail className="w-4 h-4" />
              <span>Email</span>
            </button>
            
            <button
              onClick={() => {
                setShareMethod('link');
                if (!publicLink) generatePublicLink();
              }}
              className={`flex-1 flex items-center justify-center space-x-2 py-2 px-3 rounded-md text-sm font-medium transition-colors ${
                shareMethod === 'link' 
                  ? 'bg-white text-[#1A2A80] shadow-sm' 
                  : 'text-gray-500 hover:text-gray-700'
              }`}
            >
              <Link className="w-4 h-4" />
              <span>Link</span>
            </button>
          </div>

          {/* Email Share */}
          {shareMethod === 'email' && (
            <div className="space-y-4">
              {/* Email Input */}
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Share with people
                </label>
                <div className="flex space-x-2">
                  <div className="flex-1 relative">
                    <Mail className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 w-4 h-4" />
                    <input
                      type="email"
                      value={email}
                      onChange={(e) => setEmail(e.target.value)}
                      onKeyPress={handleKeyPress}
                      placeholder="Enter email address"
                      className="w-full pl-10 pr-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-[#7A85C1] focus:border-transparent"
                    />
                  </div>
                  <button
                    onClick={handleAddEmail}
                    disabled={!email || !email.includes('@')}
                    className="px-4 py-2 bg-[#3B38A0] text-white rounded-lg hover:bg-[#1A2A80] transition-colors disabled:opacity-50 disabled:cursor-not-allowed"
                  >
                    <UserPlus className="w-4 h-4" />
                  </button>
                </div>
              </div>

              {/* Email List */}
              {emails.length > 0 && (
                <div>
                  <p className="text-sm font-medium text-gray-700 mb-2">
                    People with access ({emails.length})
                  </p>
                  <div className="space-y-2">
                    {emails.map((emailAddr, index) => (
                      <div key={index} className="flex items-center justify-between p-2 bg-gray-50 rounded-lg">
                        <span className="text-sm text-gray-700">{emailAddr}</span>
                        <button
                          onClick={() => removeEmail(emailAddr)}
                          className="text-gray-400 hover:text-red-500 p-1"
                        >
                          <X className="w-4 h-4" />
                        </button>
                      </div>
                    ))}
                  </div>
                </div>
              )}

              {/* Permission Level */}
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Permission level
                </label>
                <div className="grid grid-cols-2 gap-3">
                  <label className="relative flex items-center p-3 border border-gray-200 rounded-lg cursor-pointer hover:bg-gray-50">
                    <input
                      type="radio"
                      value="view"
                      checked={permission === 'view'}
                      onChange={(e) => setPermission(e.target.value)}
                      className="sr-only"
                    />
                    <div className={`flex items-center space-x-3 ${permission === 'view' ? 'text-[#1A2A80]' : 'text-gray-500'}`}>
                      <Eye className="w-5 h-5" />
                      <div>
                        <p className="text-sm font-medium">View</p>
                        <p className="text-xs text-gray-400">Can view only</p>
                      </div>
                    </div>
                    {permission === 'view' && (
                      <div className="absolute top-2 right-2 w-2 h-2 bg-[#1A2A80] rounded-full"></div>
                    )}
                  </label>

                  <label className="relative flex items-center p-3 border border-gray-200 rounded-lg cursor-pointer hover:bg-gray-50">
                    <input
                      type="radio"
                      value="edit"
                      checked={permission === 'edit'}
                      onChange={(e) => setPermission(e.target.value)}
                      className="sr-only"
                    />
                    <div className={`flex items-center space-x-3 ${permission === 'edit' ? 'text-[#1A2A80]' : 'text-gray-500'}`}>
                      <Edit className="w-5 h-5" />
                      <div>
                        <p className="text-sm font-medium">Edit</p>
                        <p className="text-xs text-gray-400">Can view and edit</p>
                      </div>
                    </div>
                    {permission === 'edit' && (
                      <div className="absolute top-2 right-2 w-2 h-2 bg-[#1A2A80] rounded-full"></div>
                    )}
                  </label>
                </div>
              </div>

              {/* Message */}
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Message (optional)
                </label>
                <textarea
                  value={message}
                  onChange={(e) => setMessage(e.target.value)}
                  placeholder="Add a message..."
                  rows={3}
                  className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-[#7A85C1] focus:border-transparent resize-none"
                />
              </div>
            </div>
          )}

          {/* Link Share */}
          {shareMethod === 'link' && (
            <div className="space-y-4">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Anyone with the link can access
                </label>
                <div className="flex space-x-2">
                  <div className="flex-1 relative">
                    <Link className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 w-4 h-4" />
                    <input
                      type="text"
                      value={publicLink}
                      readOnly
                      className="w-full pl-10 pr-3 py-2 border border-gray-300 rounded-lg bg-gray-50 text-gray-600"
                    />
                  </div>
                  <button
                    onClick={copyLink}
                    className="px-4 py-2 bg-[#3B38A0] text-white rounded-lg hover:bg-[#1A2A80] transition-colors flex items-center space-x-2"
                  >
                    {linkCopied ? <Check className="w-4 h-4" /> : <Copy className="w-4 h-4" />}
                    <span>{linkCopied ? 'Copied!' : 'Copy'}</span>
                  </button>
                </div>
              </div>

              {/* Link Permission */}
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Link access level
                </label>
                <select
                  value={permission}
                  onChange={(e) => setPermission(e.target.value)}
                  className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-[#7A85C1] focus:border-transparent"
                >
                  <option value="view">View only</option>
                  <option value="edit">Can edit</option>
                </select>
              </div>

              <div className="p-3 bg-blue-50 border border-blue-200 rounded-lg">
                <p className="text-sm text-blue-800">
                  <strong>Note:</strong> Anyone with this link will be able to access the document. 
                  Share it only with people you trust.
                </p>
              </div>
            </div>
          )}
        </div>

        {/* Footer */}
        <div className="flex items-center justify-end space-x-3 p-6 border-t border-gray-200">
          <button
            onClick={handleClose}
            className="px-4 py-2 text-sm font-medium text-gray-700 bg-white border border-gray-300 rounded-lg hover:bg-gray-50 transition-colors"
          >
            Cancel
          </button>
          
          <button
            onClick={handleShare}
            disabled={shareMethod === 'email' ? emails.length === 0 : false}
            className="px-6 py-2 text-sm font-medium text-white bg-gradient-to-r from-[#1A2A80] to-[#3B38A0] rounded-lg hover:shadow-lg transition-all duration-200 disabled:opacity-50 disabled:cursor-not-allowed flex items-center space-x-2"
          >
            <Share2 className="w-4 h-4" />
            <span>Share</span>
          </button>
        </div>
      </div>
    </div>
  );
};

export default ShareModal;
