import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { 
  ArrowLeft, 
  Download, 
  Share2, 
  Edit, 
  Trash2, 
  Eye,
  FileText,
  Calendar,
  User,
  FolderOpen,
  Folder,
  Grid,
  List,
  Plus,
  Search,
  Filter,
  MoreVertical,
  Star
} from 'lucide-react';

// Mock API functions - replace with your actual API calls
const mockApi = {
  getDocument: (id) => Promise.resolve({
    data: {
      id,
      name: 'Sample Document.pdf',
      type: 'pdf',
      size: 2048576,
      content: 'This is the document content...',
      createdAt: '2024-01-15T10:30:00Z',
      updatedAt: '2024-01-20T14:45:00Z',
      owner: 'John Doe',
      shared: true,
      starred: false,
      folder: 'Work Documents'
    }
  }),
  
  getFolder: (id) => Promise.resolve({
    data: {
      id,
      name: 'Work Documents',
      color: '#3B38A0',
      documentCount: 15,
      created: '2024-01-01T00:00:00Z',
      modified: '2024-01-20T14:45:00Z',
      owner: 'John Doe',
      description: 'Important work-related documents'
    }
  }),
  
  getFolderDocuments: (id) => Promise.resolve({
    data: [
      {
        id: '1',
        name: 'Project Report.pdf',
        type: 'pdf',
        size: 1024000,
        modified: '2024-01-20T14:45:00Z',
        starred: true,
        folder: 'Work Documents'
      },
      {
        id: '2',
        name: 'Budget Analysis.xlsx',
        type: 'xlsx',
        size: 512000,
        modified: '2024-01-19T09:30:00Z',
        starred: false,
        folder: 'Work Documents'
      },
      {
        id: '3',
        name: 'Meeting Notes.docx',
        type: 'docx',
        size: 256000,
        modified: '2024-01-18T16:15:00Z',
        starred: false,
        folder: 'Work Documents'
      }
    ]
  })
};

// DocumentView Component
const DocumentView = ({ user, onLogout }) => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [document, setDocument] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    loadDocument();
  }, [id]);

  const loadDocument = async () => {
    try {
      setLoading(true);
      const response = await mockApi.getDocument(id);
      setDocument(response.data);
    } catch (err) {
      setError('Failed to load document');
      console.error('Error loading document:', err);
    } finally {
      setLoading(false);
    }
  };

  const handleBack = () => {
    navigate(-1);
  };

  const handleDownload = () => {
    console.log('Downloading document:', document.name);
    // Implement download logic
  };

  const handleShare = () => {
    console.log('Sharing document:', document.name);
    // Implement share logic
  };

  const handleEdit = () => {
    console.log('Editing document:', document.name);
    // Implement edit logic
  };

  const handleDelete = () => {
    if (window.confirm('Are you sure you want to delete this document?')) {
      console.log('Deleting document:', document.name);
      // Implement delete logic
      navigate('/documents');
    }
  };

  const formatFileSize = (size) => {
    if (!size) return '0 B';
    const units = ['B', 'KB', 'MB', 'GB'];
    let bytes = size;
    let unitIndex = 0;
    
    while (bytes >= 1024 && unitIndex < units.length - 1) {
      bytes /= 1024;
      unitIndex++;
    }
    
    return `${bytes.toFixed(1)} ${units[unitIndex]}`;
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'long',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-[#3B38A0]"></div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <div className="text-red-500 text-xl mb-4">{error}</div>
          <button
            onClick={handleBack}
            className="px-4 py-2 bg-[#3B38A0] text-white rounded-lg hover:bg-[#2A2875]"
          >
            Go Back
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <div className="bg-white shadow-sm border-b">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex items-center justify-between h-16">
            <div className="flex items-center space-x-4">
              <button
                onClick={handleBack}
                className="p-2 text-gray-400 hover:text-gray-600 hover:bg-gray-100 rounded-lg"
              >
                <ArrowLeft className="w-5 h-5" />
              </button>
              <div className="flex items-center space-x-3">
                <FileText className="w-6 h-6 text-[#3B38A0]" />
                <h1 className="text-xl font-semibold text-gray-900">{document?.name}</h1>
              </div>
            </div>

            <div className="flex items-center space-x-2">
              <button
                onClick={handleDownload}
                className="p-2 text-gray-600 hover:text-[#3B38A0] hover:bg-[#B2B0E8]/20 rounded-lg"
                title="Download"
              >
                <Download className="w-5 h-5" />
              </button>
              <button
                onClick={handleShare}
                className="p-2 text-gray-600 hover:text-[#3B38A0] hover:bg-[#B2B0E8]/20 rounded-lg"
                title="Share"
              >
                <Share2 className="w-5 h-5" />
              </button>
              <button
                onClick={handleEdit}
                className="p-2 text-gray-600 hover:text-[#3B38A0] hover:bg-[#B2B0E8]/20 rounded-lg"
                title="Edit"
              >
                <Edit className="w-5 h-5" />
              </button>
              <button
                onClick={handleDelete}
                className="p-2 text-gray-600 hover:text-red-500 hover:bg-red-50 rounded-lg"
                title="Delete"
              >
                <Trash2 className="w-5 h-5" />
              </button>
            </div>
          </div>
        </div>
      </div>

      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
          {/* Document Content */}
          <div className="lg:col-span-2">
            <div className="bg-white rounded-xl shadow-sm border p-6">
              <h2 className="text-lg font-medium text-gray-900 mb-4">Document Preview</h2>
              <div className="bg-gray-50 rounded-lg p-8 text-center min-h-96">
                <FileText className="w-16 h-16 text-gray-400 mx-auto mb-4" />
                <p className="text-gray-500 mb-4">Document preview not available</p>
                <button
                  onClick={handleDownload}
                  className="px-4 py-2 bg-[#3B38A0] text-white rounded-lg hover:bg-[#2A2875] transition-colors"
                >
                  Download to View
                </button>
              </div>
            </div>
          </div>

          {/* Document Info */}
          <div className="space-y-6">
            {/* Basic Info */}
            <div className="bg-white rounded-xl shadow-sm border p-6">
              <h3 className="text-lg font-medium text-gray-900 mb-4">Document Details</h3>
              <div className="space-y-4">
                <div>
                  <label className="text-sm font-medium text-gray-500">File Type</label>
                  <p className="text-sm text-gray-900 mt-1">{document?.type?.toUpperCase()}</p>
                </div>
                <div>
                  <label className="text-sm font-medium text-gray-500">File Size</label>
                  <p className="text-sm text-gray-900 mt-1">{formatFileSize(document?.size)}</p>
                </div>
                <div>
                  <label className="text-sm font-medium text-gray-500">Owner</label>
                  <p className="text-sm text-gray-900 mt-1">{document?.owner}</p>
                </div>
                <div>
                  <label className="text-sm font-medium text-gray-500">Folder</label>
                  <p className="text-sm text-gray-900 mt-1">{document?.folder || 'No folder'}</p>
                </div>
              </div>
            </div>

            {/* Timestamps */}
            <div className="bg-white rounded-xl shadow-sm border p-6">
              <h3 className="text-lg font-medium text-gray-900 mb-4">Timeline</h3>
              <div className="space-y-4">
                <div className="flex items-center space-x-3">
                  <Calendar className="w-4 h-4 text-gray-400" />
                  <div>
                    <p className="text-sm font-medium text-gray-500">Created</p>
                    <p className="text-sm text-gray-900">{formatDate(document?.createdAt)}</p>
                  </div>
                </div>
                <div className="flex items-center space-x-3">
                  <Calendar className="w-4 h-4 text-gray-400" />
                  <div>
                    <p className="text-sm font-medium text-gray-500">Last Modified</p>
                    <p className="text-sm text-gray-900">{formatDate(document?.updatedAt)}</p>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

// FolderView Component
const FolderView = ({ user, onLogout }) => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [folder, setFolder] = useState(null);
  const [documents, setDocuments] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [viewMode, setViewMode] = useState('grid');
  const [searchTerm, setSearchTerm] = useState('');

  useEffect(() => {
    if (id) {
      loadFolder();
      loadDocuments();
    }
  }, [id]);

  const loadFolder = async () => {
    try {
      const response = await mockApi.getFolder(id);
      setFolder(response.data);
    } catch (err) {
      setError('Failed to load folder');
      console.error('Error loading folder:', err);
    }
  };

  const loadDocuments = async () => {
    try {
      setLoading(true);
      const response = await mockApi.getFolderDocuments(id);
      setDocuments(response.data);
    } catch (err) {
      setError('Failed to load documents');
      console.error('Error loading documents:', err);
    } finally {
      setLoading(false);
    }
  };

  const handleBack = () => {
    navigate('/folders');
  };

  const handleViewDocument = (doc) => {
    navigate(`/documents/${doc.id}`);
  };

  const handleEditDocument = (doc) => {
    console.log('Editing document:', doc.name);
  };

  const handleShareDocument = (doc) => {
    console.log('Sharing document:', doc.name);
  };

  const handleDeleteDocument = (doc) => {
    if (window.confirm(`Are you sure you want to delete "${doc.name}"?`)) {
      setDocuments(documents.filter(d => d.id !== doc.id));
    }
  };

  const handleDownloadDocument = (doc) => {
    console.log('Downloading document:', doc.name);
  };

  const handleStarDocument = (doc, starred) => {
    setDocuments(documents.map(d => 
      d.id === doc.id ? { ...d, starred } : d
    ));
  };

  const filteredDocuments = documents.filter(doc =>
    doc.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'long',
      day: 'numeric'
    });
  };

  if (loading && !folder) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-[#3B38A0]"></div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <div className="text-red-500 text-xl mb-4">{error}</div>
          <button
            onClick={handleBack}
            className="px-4 py-2 bg-[#3B38A0] text-white rounded-lg hover:bg-[#2A2875]"
          >
            Go Back
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <div className="bg-white shadow-sm border-b">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex items-center justify-between h-16">
            <div className="flex items-center space-x-4">
              <button
                onClick={handleBack}
                className="p-2 text-gray-400 hover:text-gray-600 hover:bg-gray-100 rounded-lg"
              >
                <ArrowLeft className="w-5 h-5" />
              </button>
              <div className="flex items-center space-x-3">
                <FolderOpen 
                  className="w-6 h-6" 
                  style={{ color: folder?.color || '#3B38A0' }}
                />
                <div>
                  <h1 className="text-xl font-semibold text-gray-900">{folder?.name}</h1>
                  <p className="text-sm text-gray-500">{folder?.documentCount || 0} documents</p>
                </div>
              </div>
            </div>

            <div className="flex items-center space-x-4">
              {/* Search */}
              <div className="relative">
                <Search className="w-4 h-4 absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400" />
                <input
                  type="text"
                  placeholder="Search documents..."
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                  className="pl-10 pr-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-[#3B38A0] focus:border-transparent"
                />
              </div>

              {/* View Toggle */}
              <div className="flex items-center bg-gray-100 rounded-lg p-1">
                <button
                  onClick={() => setViewMode('grid')}
                  className={`p-2 rounded-md ${
                    viewMode === 'grid' 
                      ? 'bg-white text-[#3B38A0] shadow-sm' 
                      : 'text-gray-500 hover:text-gray-700'
                  }`}
                >
                  <Grid className="w-4 h-4" />
                </button>
                <button
                  onClick={() => setViewMode('list')}
                  className={`p-2 rounded-md ${
                    viewMode === 'list' 
                      ? 'bg-white text-[#3B38A0] shadow-sm' 
                      : 'text-gray-500 hover:text-gray-700'
                  }`}
                >
                  <List className="w-4 h-4" />
                </button>
              </div>

              <button className="px-4 py-2 bg-[#3B38A0] text-white rounded-lg hover:bg-[#2A2875] flex items-center space-x-2">
                <Plus className="w-4 h-4" />
                <span>Upload</span>
              </button>
            </div>
          </div>
        </div>
      </div>

      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* Folder Info */}
        {folder?.description && (
          <div className="bg-white rounded-xl shadow-sm border p-6 mb-8">
            <p className="text-gray-600">{folder.description}</p>
            <div className="flex items-center space-x-6 mt-4 text-sm text-gray-500">
              <span>Created {formatDate(folder.created)}</span>
              <span>Modified {formatDate(folder.modified)}</span>
              <span>Owner: {folder.owner}</span>
            </div>
          </div>
        )}

        {/* Documents */}
        <div className="bg-white rounded-xl shadow-sm border p-6">
          <div className="flex items-center justify-between mb-6">
            <h2 className="text-lg font-medium text-gray-900">Documents</h2>
            <div className="text-sm text-gray-500">
              {filteredDocuments.length} of {documents.length} documents
            </div>
          </div>

          {filteredDocuments.length === 0 ? (
            <div className="text-center py-12">
              <FolderOpen className="w-12 h-12 text-gray-400 mx-auto mb-4" />
              <h3 className="text-lg font-medium text-gray-900 mb-2">No documents found</h3>
              <p className="text-gray-500 mb-6">
                {searchTerm ? 'Try adjusting your search terms' : 'This folder is empty'}
              </p>
              <button className="px-4 py-2 bg-[#3B38A0] text-white rounded-lg hover:bg-[#2A2875]">
                Upload Document
              </button>
            </div>
          ) : (
            <div className={
              viewMode === 'grid' 
                ? "grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6"
                : "space-y-3"
            }>
              {filteredDocuments.map((doc) => (
                <DocumentCard
                  key={doc.id}
                  document={doc}
                  viewMode={viewMode}
                  onView={handleViewDocument}
                  onEdit={handleEditDocument}
                  onShare={handleShareDocument}
                  onDelete={handleDeleteDocument}
                  onDownload={handleDownloadDocument}
                  onStar={handleStarDocument}
                />
              ))}
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

// DocumentCard Component (from your existing code with slight modifications)
const DocumentCard = ({ 
  document, 
  viewMode = 'grid', 
  onView, 
  onEdit, 
  onShare, 
  onDelete, 
  onDownload,
  onStar 
}) => {
  const [showActions, setShowActions] = useState(false);
  const [isStarred, setIsStarred] = useState(document.starred || false);

  const getFileIcon = (type) => {
    const iconClass = viewMode === 'grid' ? "w-8 h-8" : "w-6 h-6";
    
    switch (type?.toLowerCase()) {
      case 'pdf':
        return <FileText className={`${iconClass} text-red-500`} />;
      case 'doc':
      case 'docx':
        return <FileText className={`${iconClass} text-blue-500`} />;
      case 'xls':
      case 'xlsx':
        return <FileText className={`${iconClass} text-green-500`} />;
      case 'ppt':
      case 'pptx':
        return <FileText className={`${iconClass} text-orange-500`} />;
      case 'jpg':
      case 'jpeg':
      case 'png':
      case 'gif':
        return <FileText className={`${iconClass} text-purple-500`} />;
      case 'txt':
        return <FileText className={`${iconClass} text-gray-500`} />;
      default:
        return <FileText className={`${iconClass} text-gray-500`} />;
    }
  };

  const getFileTypeColor = (type) => {
    switch (type?.toLowerCase()) {
      case 'pdf': return 'bg-red-100 text-red-700';
      case 'doc':
      case 'docx': return 'bg-blue-100 text-blue-700';
      case 'xls':
      case 'xlsx': return 'bg-green-100 text-green-700';
      case 'ppt':
      case 'pptx': return 'bg-orange-100 text-orange-700';
      case 'jpg':
      case 'jpeg':
      case 'png':
      case 'gif': return 'bg-purple-100 text-purple-700';
      default: return 'bg-gray-100 text-gray-700';
    }
  };

  const formatFileSize = (size) => {
    if (!size) return '0 B';
    if (typeof size === 'string') return size;
    
    const units = ['B', 'KB', 'MB', 'GB'];
    let bytes = size;
    let unitIndex = 0;
    
    while (bytes >= 1024 && unitIndex < units.length - 1) {
      bytes /= 1024;
      unitIndex++;
    }
    
    return `${bytes.toFixed(1)} ${units[unitIndex]}`;
  };

  const formatDate = (dateString) => {
    if (!dateString) return '';
    
    try {
      const date = new Date(dateString);
      const now = new Date();
      const diffTime = Math.abs(now - date);
      const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));
      
      if (diffDays === 1) return 'Yesterday';
      if (diffDays < 7) return `${diffDays} days ago`;
      if (diffDays < 30) return `${Math.ceil(diffDays / 7)} weeks ago`;
      
      return date.toLocaleDateString();
    } catch {
      return dateString;
    }
  };

  const handleStarClick = (e) => {
    e.stopPropagation();
    setIsStarred(!isStarred);
    if (onStar) {
      onStar(document, !isStarred);
    }
  };

  if (viewMode === 'list') {
    return (
      <div className="group bg-white rounded-lg border border-gray-200 hover:border-[#7A85C1] hover:shadow-md transition-all duration-200">
        <div className="flex items-center justify-between p-4">
          <div className="flex items-center space-x-4 flex-1 min-w-0">
            <div className="flex-shrink-0">
              {getFileIcon(document.type)}
            </div>
            
            <div className="flex-1 min-w-0">
              <div className="flex items-center space-x-2 mb-1">
                <h3 className="font-medium text-gray-900 truncate">{document.name}</h3>
                <button
                  onClick={handleStarClick}
                  className={`flex-shrink-0 p-1 rounded ${
                    isStarred 
                      ? 'text-yellow-500 hover:text-yellow-600' 
                      : 'text-gray-300 hover:text-yellow-500'
                  }`}
                >
                  <Star className={`w-4 h-4 ${isStarred ? 'fill-current' : ''}`} />
                </button>
              </div>
              
              <div className="flex items-center space-x-4 text-sm text-gray-500">
                <span>{formatFileSize(document.size)}</span>
                <div className="flex items-center space-x-1">
                  <Calendar className="w-4 h-4" />
                  <span>{formatDate(document.modified || document.updatedAt)}</span>
                </div>
              </div>
            </div>
          </div>

          <div className="flex items-center space-x-1 opacity-0 group-hover:opacity-100 transition-opacity duration-200">
            <button
              onClick={(e) => {
                e.stopPropagation();
                onView(document);
              }}
              className="p-2 text-gray-400 hover:text-[#3B38A0] hover:bg-[#B2B0E8]/20 rounded-lg transition-colors"
              title="View"
            >
              <Eye className="w-4 h-4" />
            </button>
            
            <button
              onClick={(e) => {
                e.stopPropagation();
                onDownload(document);
              }}
              className="p-2 text-gray-400 hover:text-[#3B38A0] hover:bg-[#B2B0E8]/20 rounded-lg transition-colors"
              title="Download"
            >
              <Download className="w-4 h-4" />
            </button>
            
            <button
              onClick={(e) => {
                e.stopPropagation();
                onEdit(document);
              }}
              className="p-2 text-gray-400 hover:text-[#3B38A0] hover:bg-[#B2B0E8]/20 rounded-lg transition-colors"
              title="Edit"
            >
              <Edit className="w-4 h-4" />
            </button>
            
            <button
              onClick={(e) => {
                e.stopPropagation();
                onShare(document);
              }}
              className="p-2 text-gray-400 hover:text-[#3B38A0] hover:bg-[#B2B0E8]/20 rounded-lg transition-colors"
              title="Share"
            >
              <Share2 className="w-4 h-4" />
            </button>
            
            <button
              onClick={(e) => {
                e.stopPropagation();
                onDelete(document);
              }}
              className="p-2 text-gray-400 hover:text-red-500 hover:bg-red-50 rounded-lg transition-colors"
              title="Delete"
            >
              <Trash2 className="w-4 h-4" />
            </button>
          </div>
        </div>
      </div>
    );
  }

  // Grid view
  return (
    <div className="group relative bg-white rounded-xl border border-gray-200 hover:border-[#7A85C1] hover:shadow-lg transition-all duration-300 cursor-pointer">
      <div className="p-6" onClick={() => onView(document)}>
        {/* Header with icon and actions */}
        <div className="flex items-start justify-between mb-4">
          <div className="flex-shrink-0">
            {getFileIcon(document.type)}
          </div>
          
          <div className="flex items-center space-x-1">
            <button
              onClick={handleStarClick}
              className={`p-1 rounded ${
                isStarred 
                  ? 'text-yellow-500 hover:text-yellow-600' 
                  : 'text-gray-300 hover:text-yellow-500 opacity-0 group-hover:opacity-100'
              } transition-all duration-200`}
            >
              <Star className={`w-4 h-4 ${isStarred ? 'fill-current' : ''}`} />
            </button>
            
            <div className="relative">
              <button
                onClick={(e) => {
                  e.stopPropagation();
                  setShowActions(!showActions);
                }}
                className="p-1 text-gray-400 hover:text-gray-600 opacity-0 group-hover:opacity-100 transition-all duration-200"
              >
                <MoreVertical className="w-4 h-4" />
              </button>

              {/* Actions dropdown */}
              {showActions && (
                <div className="absolute right-0 mt-1 w-32 bg-white rounded-lg shadow-lg border border-gray-200 z-10">
                  <div className="p-1">
                    <button
                      onClick={(e) => {
                        e.stopPropagation();
                        onView(document);
                        setShowActions(false);
                      }}
                      className="flex items-center space-x-2 w-full p-2 text-sm text-gray-700 hover:bg-gray-100 rounded-md"
                    >
                      <Eye className="w-4 h-4" />
                      <span>View</span>
                    </button>
                    
                    <button
                      onClick={(e) => {
                        e.stopPropagation();
                        onDownload(document);
                        setShowActions(false);
                      }}
                      className="flex items-center space-x-2 w-full p-2 text-sm text-gray-700 hover:bg-gray-100 rounded-md"
                    >
                      <Download className="w-4 h-4" />
                      <span>Download</span>
                    </button>
                    
                    <button
                      onClick={(e) => {
                        e.stopPropagation();
                        onEdit(document);
                        setShowActions(false);
                      }}
                      className="flex items-center space-x-2 w-full p-2 text-sm text-gray-700 hover:bg-gray-100 rounded-md"
                    >
                      <Edit className="w-4 h-4" />
                      <span>Edit</span>
                    </button>
                    
                    <button
                      onClick={(e) => {
                        e.stopPropagation();
                        onShare(document);
                        setShowActions(false);
                      }}
                      className="flex items-center space-x-2 w-full p-2 text-sm text-gray-700 hover:bg-gray-100 rounded-md"
                    >
                      <Share2 className="w-4 h-4" />
                      <span>Share</span>
                    </button>
                    
                    <div className="border-t border-gray-100 my-1"></div>
                    
                    <button
                      onClick={(e) => {
                        e.stopPropagation();
                        onDelete(document);
                        setShowActions(false);
                      }}
                      className="flex items-center space-x-2 w-full p-2 text-sm text-red-600 hover:bg-red-50 rounded-md"
                    >
                      <Trash2 className="w-4 h-4" />
                      <span>Delete</span>
                    </button>
                  </div>
                </div>
              )}
            </div>
          </div>
        </div>

        {/* Document name */}
        <h3 className="font-semibold text-gray-900 mb-3 truncate text-sm">
          {document.name}
        </h3>

        {/* Document info */}
        <div className="space-y-2">
          <div className="flex items-center justify-between">
            {document.folder && (
              <span className="inline-flex items-center px-2 py-1 rounded-full text-xs font-medium bg-[#B2B0E8]/30 text-[#1A2A80]">
                {document.folder}
              </span>
            )}
            
            <span className={`px-2 py-1 rounded text-xs font-medium ${getFileTypeColor(document.type)}`}>
              {document.type?.toUpperCase() || 'FILE'}
            </span>
          </div>
          
          <div className="flex items-center justify-between text-xs text-gray-500">
            <span>{formatFileSize(document.size)}</span>
            <span>{formatDate(document.modified || document.updatedAt)}</span>
          </div>
        </div>
      </div>

      {/* Click outside handler for actions dropdown */}
      {showActions && (
        <div
          className="fixed inset-0 z-5"
          onClick={(e) => {
            e.stopPropagation();
            setShowActions(false);
          }}
        />
      )}
    </div>
  );
};

// Export components
export { DocumentView, FolderView }
