import React from 'react';
import { 
  FileText, 
  Folder, 
  Share2, 
  Activity, 
  Settings, 
  X,
  Home,
  Star,
  Archive,
  Trash2
} from 'lucide-react';
import { Link, useLocation } from 'react-router-dom';

const Sidebar = ({ isOpen, onClose, activeItem = 'documents' }) => {
  const location = useLocation();

  const navigationItems = [
    { id: 'dashboard', name: 'Dashboard', icon: Home, path: '/' },
    { id: 'documents', name: 'Documents', icon: FileText, path: '/documents' },
    { id: 'folders', name: 'Folders', icon: Folder, path: '/folders' },
    { id: 'shared', name: 'Shared', icon: Share2, path: '/shared' },
    { id: 'activity', name: 'Activity', icon: Activity, path: '/activity' },
  ];

  const quickAccessItems = [
    { id: 'starred', name: 'Starred', icon: Star, count: 12 },
    { id: 'recent', name: 'Recent', icon: FileText, count: 8 },
    { id: 'archived', name: 'Archived', icon: Archive, count: 3 },
    { id: 'trash', name: 'Trash', icon: Trash2, count: 2 },
  ];

  const isActive = (path) => {
    if (path === '/') {
      return location.pathname === '/';
    }
    return location.pathname.startsWith(path);
  };

  return (
    <>
      {isOpen && (
        <div 
          className="fixed inset-0 bg-black/50 backdrop-blur-sm z-40 lg:hidden"
          onClick={onClose}
        />
      )}
      <aside className={`
        fixed left-0 top-0 h-full w-64 bg-gradient-to-b from-[#1A2A80] to-[#3B38A0] text-white z-50 transform transition-transform duration-300 ease-in-out
        ${isOpen ? 'translate-x-0' : '-translate-x-full'}
        lg:translate-x-0 lg:static lg:z-auto
      `}>
        <div className="flex flex-col h-full">
          {/* Header */}
          <div className="flex items-center justify-between p-6 border-b border-white/10">
            <div className="flex items-center space-x-3">
              <div className="w-8 h-8 bg-white/20 rounded-lg flex items-center justify-center">
                <FileText className="w-5 h-5" />
              </div>
              <h1 className="text-xl font-bold">DocManager</h1>
            </div>
            <button 
              onClick={onClose}
              className="lg:hidden p-2 hover:bg-white/10 rounded-lg transition-colors"
            >
              <X className="w-5 h-5" />
            </button>
          </div>
          
          {/* Navigation */}
          <nav className="flex-1 p-4 space-y-1">
            <div className="mb-6">
              {navigationItems.map((item) => {
                const Icon = item.icon;
                return (
                  <Link
                    key={item.id}
                    to={item.path}
                    onClick={() => window.innerWidth < 1024 && onClose()}
                    className={`flex items-center space-x-3 p-3 rounded-lg transition-all duration-200 ${
                      isActive(item.path)
                        ? 'bg-white/20 text-white shadow-lg'
                        : 'text-white/80 hover:text-white hover:bg-white/10'
                    }`}
                  >
                    <Icon className="w-5 h-5" />
                    <span className="font-medium">{item.name}</span>
                  </Link>
                );
              })}
            </div>

            {/* Quick Access */}
            <div className="border-t border-white/10 pt-4">
              <h3 className="text-sm font-semibold text-white/60 uppercase tracking-wide mb-3 px-3">
                Quick Access
              </h3>
              <div className="space-y-1">
                {quickAccessItems.map((item) => {
                  const Icon = item.icon;
                  return (
                    <button
                      key={item.id}
                      className="w-full flex items-center justify-between p-3 rounded-lg text-white/80 hover:text-white hover:bg-white/10 transition-all duration-200"
                    >
                      <div className="flex items-center space-x-3">
                        <Icon className="w-4 h-4" />
                        <span className="text-sm">{item.name}</span>
                      </div>
                      <span className="text-xs bg-white/20 px-2 py-1 rounded-full">
                        {item.count}
                      </span>
                    </button>
                  );
                })}
              </div>
            </div>
          </nav>

          {/* Footer */}
          <div className="p-4 border-t border-white/10">
            <Link
              to="/settings"
              onClick={() => window.innerWidth < 1024 && onClose()}
              className="flex items-center space-x-3 p-3 rounded-lg text-white/80 hover:text-white hover:bg-white/10 transition-all duration-200"
            >
              <Settings className="w-5 h-5" />
              <span className="font-medium">Settings</span>
            </Link>
            
            {/* Storage Indicator */}
            <div className="mt-4 p-3 bg-white/10 rounded-lg">
              <div className="flex items-center justify-between mb-2">
                <span className="text-sm text-white/80">Storage</span>
                <span className="text-xs text-white/60">2.1GB / 5GB</span>
              </div>
              <div className="w-full bg-white/20 rounded-full h-2">
                <div className="bg-gradient-to-r from-[#B2B0E8] to-[#7A85C1] h-2 rounded-full" style={{ width: '42%' }}></div>
              </div>
            </div>
          </div>
        </div>
      </aside>
    </>
  );
};

export default Sidebar;
