import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Dashboard from './components/Dashboard';
import Login from './components/Login';
import Register from './components/Register';
import { DocumentView, FolderView } from './components/Views'; // Import both views
import Settings from './components/Settings';
import SharedDocuments from './components/SharedDocuments';
import ActivityLog from './components/ActivityLog';
import LoadingSpinner from './components/LoadingSpinner';
import ErrorBoundary from './components/ErrorBoundary';

// Mock API function - replace with your actual API
const mockApi = {
  getCurrentUser: () => Promise.resolve({
    data: {
      id: '1',
      name: 'John Doe',
      email: 'john.doe@example.com',
      avatar: null
    }
  })
};

function App() {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    checkAuth();
  }, []);

  const checkAuth = async () => {
    try {
      const token = localStorage.getItem('token');
      if (token) {
        // Replace with your actual API call
        const response = await mockApi.getCurrentUser();
        setUser(response.data);
        setIsAuthenticated(true);
      } else {
        setIsAuthenticated(false);
      }
    } catch (error) {
      console.error('Auth check failed:', error);
      localStorage.removeItem('token');
      setIsAuthenticated(false);
      setError('Authentication failed');
    } finally {
      setLoading(false);
    }
  };

  const handleLogin = (userData, token) => {
    localStorage.setItem('token', token);
    setUser(userData);
    setIsAuthenticated(true);
    setError(null);
  };

  const handleRegister = (userData, token) => {
    // Same as login for this implementation
    handleLogin(userData, token);
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    setUser(null);
    setIsAuthenticated(false);
    setError(null);
  };

  // Error boundary fallback
  if (error && !loading) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="bg-white rounded-lg shadow-md p-8 max-w-md w-full mx-4">
          <div className="text-center">
            <div className="text-red-500 text-6xl mb-4">⚠️</div>
            <h2 className="text-xl font-semibold text-gray-900 mb-2">Something went wrong</h2>
            <p className="text-gray-600 mb-6">{error}</p>
            <button
              onClick={() => window.location.reload()}
              className="px-4 py-2 bg-[#3B38A0] text-white rounded-lg hover:bg-[#2A2875] transition-colors"
            >
              Refresh Page
            </button>
          </div>
        </div>
      </div>
    );
  }

  // Loading state
  if (loading) {
    return <LoadingSpinner />;
  }

  return (
    <ErrorBoundary>
      <div className="min-h-screen bg-gray-50">
        <Router>
          <Routes>
            {!isAuthenticated ? (
              // Unauthenticated routes
              <>
                <Route 
                  path="/login" 
                  element={<Login onLogin={handleLogin} />} 
                />
                <Route 
                  path="/register" 
                  element={<Register onRegister={handleRegister} />} 
                />
                {/* Redirect all other routes to login */}
                <Route path="*" element={<Navigate to="/login" replace />} />
              </>
            ) : (
              // Authenticated routes
              <>
                {/* Main dashboard - default route */}
                <Route 
                  path="/" 
                  element={<Dashboard user={user} onLogout={handleLogout} />} 
                />
                
                {/* Documents routes */}
                <Route 
                  path="/documents" 
                  element={<Dashboard user={user} onLogout={handleLogout} />} 
                />
                <Route 
                  path="/documents/:id" 
                  element={<DocumentView user={user} onLogout={handleLogout} />} 
                />
                
                {/* Folders routes */}
                <Route 
                  path="/folders" 
                  element={<Dashboard user={user} onLogout={handleLogout} />} 
                />
                <Route 
                  path="/folders/:id" 
                  element={<FolderView user={user} onLogout={handleLogout} />} 
                />
                
                {/* Other feature routes */}
                <Route 
                  path="/shared" 
                  element={<SharedDocuments user={user} onLogout={handleLogout} />} 
                />
                <Route 
                  path="/activity" 
                  element={<ActivityLog user={user} onLogout={handleLogout} />} 
                />
                <Route 
                  path="/settings" 
                  element={<Settings user={user} onLogout={handleLogout} />} 
                />
                
                {/* Catch-all redirect to dashboard */}
                <Route path="*" element={<Navigate to="/" replace />} />
              </>
            )}
          </Routes>
        </Router>
      </div>
    </ErrorBoundary>
  );
}

export default App;
