import React, { useState, useEffect } from 'react';
import { useAuth } from '../context/AuthContext';
import { useApp } from '../context/AppContext';
import LoadingSpinner from '../components/common/LoadingSpinner';

const UserCard = ({ user, onEdit, onDelete, onToggleStatus }) => {
  const getUserInitials = (user) => {
    if (!user) return 'U';
    if (user.name) {
      return user.name.split(' ').map(n => n[0]).join('').toUpperCase().slice(0, 2);
    }
    return user.email ? user.email[0].toUpperCase() : 'U';
  };

  const getRoleBadgeColor = (role) => {
    return role === 'ADMIN' ? 'bg-red-100 text-red-800' : 'bg-blue-100 text-blue-800';
  };

  const getStatusBadgeColor = (status) => {
    return status === 'active' ? 'bg-green-100 text-green-800' : 'bg-gray-100 text-gray-800';
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString();
  };

  return (
    <div className="card hover:shadow-lg transition-all duration-200">
      <div className="flex items-center space-x-4">
        <div className="w-12 h-12 bg-primary-600 rounded-full flex items-center justify-center flex-shrink-0">
          <span className="text-lg font-medium text-white">
            {getUserInitials(user)}
          </span>
        </div>
        
        <div className="flex-1 min-w-0">
          <div className="flex items-center space-x-2">
            <h3 className="text-sm font-medium text-gray-900 truncate">
              {user.name || 'Unknown User'}
            </h3>
            <span className={`inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium ${getRoleBadgeColor(user.role)}`}>
              {user.role === 'ADMIN' ? 'Admin' : 'User'}
            </span>
            <span className={`inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium ${getStatusBadgeColor(user.status || 'active')}`}>
              {user.status || 'Active'}
            </span>
          </div>
          
          <p className="text-sm text-gray-500 truncate mt-1">
            {user.email}
          </p>
          
          <div className="flex items-center mt-2 text-xs text-gray-400 space-x-4">
            <span>Joined {formatDate(user.createdAt || new Date())}</span>
            <span>{user.documentCount || 0} documents</span>
            <span>{user.storageUsed || '0 MB'} used</span>
          </div>
        </div>
        
        <div className="flex items-center space-x-2">
          <button
            onClick={() => onToggleStatus(user)}
            className={`p-2 rounded-lg transition-colors ${
              user.status === 'active' 
                ? 'text-gray-400 hover:text-yellow-600' 
                : 'text-yellow-400 hover:text-green-600'
            }`}
            title={user.status === 'active' ? 'Deactivate user' : 'Activate user'}
          >
            <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M8 9l4-4 4 4m0 6l-4 4-4-4" />
            </svg>
          </button>
          
          <button
            onClick={() => onEdit(user)}
            className="p-2 text-gray-400 hover:text-blue-600 rounded-lg transition-colors"
            title="Edit user"
          >
            <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M11 5H6a2 2 0 00-2 2v11a2 2 0 002 2h11a2 2 0 002-2v-5m-1.414-9.414a2 2 0 112.828 2.828L11.828 15H9v-2.828l8.586-8.586z" />
            </svg>
          </button>
          
          <button
            onClick={() => onDelete(user)}
            className="p-2 text-gray-400 hover:text-red-600 rounded-lg transition-colors"
            title="Delete user"
          >
            <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 7l-.867 12.142A2 2 0 0116.138 21H7.862a2 2 0 01-1.995-1.858L5 7m5 4v6m4-6v6m1-10V4a1 1 0 00-1-1h-4a1 1 0 00-1 1v3M4 7h16" />
            </svg>
          </button>
        </div>
      </div>
    </div>
  );
};

const UserModal = ({ isOpen, onClose, user, onSave }) => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    role: 'USER',
    status: 'active'
  });
  const [saving, setSaving] = useState(false);
  const { showSuccess, showError } = useApp();

  useEffect(() => {
    if (user) {
      setFormData({
        name: user.name || '',
        email: user.email || '',
        role: user.role || 'USER',
        status: user.status || 'active'
      });
    } else {
      setFormData({
        name: '',
        email: '',
        role: 'USER',
        status: 'active'
      });
    }
  }, [user]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!formData.name.trim() || !formData.email.trim()) {
      showError('Name and email are required');
      return;
    }

    setSaving(true);
    try {
      await onSave(formData);
      showSuccess(user ? 'User updated successfully' : 'User created successfully');
      onClose();
    } catch (error) {
      showError('Failed to save user');
    } finally {
      setSaving(false);
    }
  };

  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50">
      <div className="bg-white rounded-lg max-w-md w-full p-6">
        <div className="flex justify-between items-center mb-4">
          <h3 className="text-lg font-medium">
            {user ? 'Edit User' : 'Add New User'}
          </h3>
          <button
            onClick={onClose}
            className="text-gray-400 hover:text-gray-600"
          >
            <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
            </svg>
          </button>
        </div>

        <div className="space-y-4">
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              Full Name
            </label>
            <input
              type="text"
              value={formData.name}
              onChange={(e) => setFormData(prev => ({ ...prev, name: e.target.value }))}
              className="input-field"
              placeholder="Enter full name"
              disabled={saving}
            />
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              Email Address
            </label>
            <input
              type="email"
              value={formData.email}
              onChange={(e) => setFormData(prev => ({ ...prev, email: e.target.value }))}
              className="input-field"
              placeholder="Enter email address"
              disabled={saving || !!user} // Disable email editing for existing users
            />
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              Role
            </label>
            <select
              value={formData.role}
              onChange={(e) => setFormData(prev => ({ ...prev, role: e.target.value }))}
              className="input-field"
              disabled={saving}
            >
              <option value="USER">User</option>
              <option value="ADMIN">Admin</option>
            </select>
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              Status
            </label>
            <select
              value={formData.status}
              onChange={(e) => setFormData(prev => ({ ...prev, status: e.target.value }))}
              className="input-field"
              disabled={saving}
            >
              <option value="active">Active</option>
              <option value="inactive">Inactive</option>
            </select>
          </div>
        </div>

        <div className="flex justify-end space-x-3 mt-6">
          <button
            onClick={onClose}
            className="btn-outline"
            disabled={saving}
          >
            Cancel
          </button>
          <button
            onClick={handleSubmit}
            className="btn-primary"
            disabled={saving}
          >
            {saving ? (
              <>
                <LoadingSpinner size="sm" color="white" />
                <span className="ml-2">Saving...</span>
              </>
            ) : (
              user ? 'Update' : 'Create'
            )}
          </button>
        </div>
      </div>
    </div>
  );
};

const UsersPage = () => {
  const { user: currentUser } = useAuth();
  const { searchQuery, showSuccess, showError } = useApp();
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showUserModal, setShowUserModal] = useState(false);
  const [editingUser, setEditingUser] = useState(null);
  const [filterRole, setFilterRole] = useState('all');
  const [filterStatus, setFilterStatus] = useState('all');

  useEffect(() => {
    loadUsers();
  }, []);

  const loadUsers = async () => {
    try {
      setLoading(true);
      // Mock data - in real app would call userService.getAllUsers()
      const mockUsers = [
        {
          id: 1,
          name: "John Doe",
          email: "john.doe@example.com",
          role: "ADMIN",
          status: "active",
          createdAt: new Date().toISOString(),
          documentCount: 25,
          storageUsed: "1.2 GB"
        },
        {
          id: 2,
          name: "Jane Smith",
          email: "jane.smith@example.com",
          role: "USER",
          status: "active",
          createdAt: new Date(Date.now() - 86400000).toISOString(),
          documentCount: 15,
          storageUsed: "850 MB"
        },
        {
          id: 3,
          name: "Mike Johnson",
          email: "mike.johnson@example.com",
          role: "USER",
          status: "inactive",
          createdAt: new Date(Date.now() - 172800000).toISOString(),
          documentCount: 8,
          storageUsed: "420 MB"
        },
        {
          id: 4,
          name: "Sarah Wilson",
          email: "sarah.wilson@example.com",
          role: "USER",
          status: "active",
          createdAt: new Date(Date.now() - 259200000).toISOString(),
          documentCount: 32,
          storageUsed: "2.1 GB"
        },
        {
          id: 5,
          name: "David Brown",
          email: "david.brown@example.com",
          role: "ADMIN",
          status: "active",
          createdAt: new Date(Date.now() - 345600000).toISOString(),
          documentCount: 45,
          storageUsed: "3.5 GB"
        }
      ];
      setUsers(mockUsers);
    } catch (error) {
      showError('Failed to load users');
    } finally {
      setLoading(false);
    }
  };

  const filteredUsers = users.filter(user => {
    // Filter by search query
    if (searchQuery && !user.name.toLowerCase().includes(searchQuery.toLowerCase()) && 
        !user.email.toLowerCase().includes(searchQuery.toLowerCase())) {
      return false;
    }
    
    // Filter by role
    if (filterRole !== 'all' && user.role !== filterRole) {
      return false;
    }
    
    // Filter by status
    if (filterStatus !== 'all' && user.status !== filterStatus) {
      return false;
    }
    
    return true;
  });

  const handleCreateUser = () => {
    setEditingUser(null);
    setShowUserModal(true);
  };

  const handleEditUser = (user) => {
    setEditingUser(user);
    setShowUserModal(true);
  };

  const handleSaveUser = async (userData) => {
    try {
      if (editingUser) {
        // Update existing user
        const updatedUser = { ...editingUser, ...userData };
        setUsers(prev => prev.map(u => u.id === editingUser.id ? updatedUser : u));
      } else {
        // Create new user
        const newUser = {
          id: Date.now(),
          ...userData,
          createdAt: new Date().toISOString(),
          documentCount: 0,
          storageUsed: '0 MB'
        };
        setUsers(prev => [...prev, newUser]);
      }
    } catch (error) {
      throw error;
    }
  };

  const handleDeleteUser = async (user) => {
    if (user.id === currentUser?.id) {
      showError("You cannot delete your own account");
      return;
    }
    
    if (window.confirm(`Are you sure you want to delete "${user.name}"? This action cannot be undone.`)) {
      try {
        setUsers(prev => prev.filter(u => u.id !== user.id));
        showSuccess('User deleted successfully');
      } catch (error) {
        showError('Delete failed');
      }
    }
  };

  const handleToggleUserStatus = async (user) => {
    if (user.id === currentUser?.id) {
      showError("You cannot change your own status");
      return;
    }
    
    try {
      const newStatus = user.status === 'active' ? 'inactive' : 'active';
      const updatedUser = { ...user, status: newStatus };
      setUsers(prev => prev.map(u => u.id === user.id ? updatedUser : u));
      showSuccess(`User ${newStatus === 'active' ? 'activated' : 'deactivated'} successfully`);
    } catch (error) {
      showError('Status change failed');
    }
  };

  const getStats = () => {
    return {
      totalUsers: users.length,
      activeUsers: users.filter(u => u.status === 'active').length,
      adminUsers: users.filter(u => u.role === 'ADMIN').length,
      totalDocuments: users.reduce((sum, u) => sum + (u.documentCount || 0), 0)
    };
  };

  const stats = getStats();

  if (loading) {
    return (
      <div className="flex items-center justify-center h-64">
        <LoadingSpinner size="lg" />
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex flex-col sm:flex-row sm:items-center sm:justify-between">
        <div>
          <h1 className="text-2xl font-bold text-gray-900">Users</h1>
          <p className="text-gray-600 mt-1">Manage system users and permissions</p>
        </div>
        
        <button
          onClick={handleCreateUser}
          className="mt-4 sm:mt-0 btn-primary"
        >
          <svg className="w-5 h-5 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 6v6m0 0v6m0-6h6m-6 0H6" />
          </svg>
          Add User
        </button>
      </div>

      {/* Stats Cards */}
      <div className="grid grid-cols-1 md:grid-cols-4 gap-6">
        <div className="card text-center">
          <div className="text-3xl font-bold text-primary-600">{stats.totalUsers}</div>
          <div className="text-sm text-gray-600 mt-1">Total Users</div>
        </div>
        
        <div className="card text-center">
          <div className="text-3xl font-bold text-green-600">{stats.activeUsers}</div>
          <div className="text-sm text-gray-600 mt-1">Active Users</div>
        </div>
        
        <div className="card text-center">
          <div className="text-3xl font-bold text-red-600">{stats.adminUsers}</div>
          <div className="text-sm text-gray-600 mt-1">Administrators</div>
        </div>
        
        <div className="card text-center">
          <div className="text-3xl font-bold text-blue-600">{stats.totalDocuments}</div>
          <div className="text-sm text-gray-600 mt-1">Total Documents</div>
        </div>
      </div>

      {/* Filters */}
      <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-4">
        <div className="flex flex-col sm:flex-row sm:items-center space-y-4 sm:space-y-0 sm:space-x-4">
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">Filter by Role</label>
            <select
              value={filterRole}
              onChange={(e) => setFilterRole(e.target.value)}
              className="text-sm border border-gray-300 rounded-lg px-3 py-2 focus:ring-primary-500 focus:border-primary-500"
            >
              <option value="all">All Roles</option>
              <option value="ADMIN">Administrators</option>
              <option value="USER">Users</option>
            </select>
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">Filter by Status</label>
            <select
              value={filterStatus}
              onChange={(e) => setFilterStatus(e.target.value)}
              className="text-sm border border-gray-300 rounded-lg px-3 py-2 focus:ring-primary-500 focus:border-primary-500"
            >
              <option value="all">All Status</option>
              <option value="active">Active</option>
              <option value="inactive">Inactive</option>
            </select>
          </div>

          <div className="flex-1">
            <label className="block text-sm font-medium text-gray-700 mb-1">Search</label>
            <div className="relative">
              <input
                type="text"
                placeholder="Search users..."
                className="input-field pl-10"
                value={searchQuery}
                onChange={(e) => {
                  // In real app, would use updateSearchQuery from context
                  console.log('Search query:', e.target.value);
                }}
              />
              <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                <svg className="h-5 w-5 text-gray-400" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z" />
                </svg>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Users List */}
      {filteredUsers.length === 0 ? (
        <div className="text-center py-12">
          <svg className="mx-auto h-12 w-12 text-gray-400" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 4.354a4 4 0 110 5.292M15 21H3v-1a6 6 0 0112 0v1zm0 0h6v-1a6 6 0 00-9-5.197m13.5-9a2.5 2.5 0 11-5 0 2.5 2.5 0 015 0z" />
          </svg>
          <h3 className="mt-2 text-sm font-medium text-gray-900">No users found</h3>
          <p className="mt-1 text-sm text-gray-500">
            {searchQuery || filterRole !== 'all' || filterStatus !== 'all' 
              ? 'Try adjusting your search or filters.' 
              : 'Get started by adding your first user.'
            }
          </p>
          {!searchQuery && filterRole === 'all' && filterStatus === 'all' && (
            <div className="mt-6">
              <button
                onClick={handleCreateUser}
                className="btn-primary"
              >
                Add User
              </button>
            </div>
          )}
        </div>
      ) : (
        <div className="space-y-4">
          {filteredUsers.map(user => (
            <UserCard
              key={user.id}
              user={user}
              onEdit={handleEditUser}
              onDelete={handleDeleteUser}
              onToggleStatus={handleToggleUserStatus}
            />
          ))}
        </div>
      )}

      {/* User Modal */}
      <UserModal
        isOpen={showUserModal}
        onClose={() => setShowUserModal(false)}
        user={editingUser}
        onSave={handleSaveUser}
      />
    </div>
  );
};

export default UsersPage;
