import React from 'react';
import '../../styles/components/FileCard.css';

const FileCard = ({ document, onView, onShare, onDelete, showActions = true }) => {
  const getFileIcon = (fileType) => {
    switch (fileType?.toLowerCase()) {
      case 'pdf':
        return '#ff4444';
      case 'doc':
      case 'docx':
        return '#4285f4';
      case 'xls':
      case 'xlsx':
        return '#34a853';
      case 'ppt':
      case 'pptx':
        return '#ff6d01';
      default:
        return '#9aa0a6';
    }
  };

  return (
    <div className="file-card">
      <div className="file-icon">
        <svg width="48" height="48" viewBox="0 0 24 24" fill="none">
          <path 
            d="M14,2H6A2,2 0 0,0 4,4V20A2,2 0 0,0 6,22H18A2,2 0 0,0 20,20V8L14,2M18,20H6V4H13V9H18V20Z" 
            fill={getFileIcon(document.fileType)}
          />
        </svg>
      </div>
      <div className="file-info">
        <h3 className="file-title">{document.title}</h3>
        <p className="file-name">{document.fileName}</p>
        <p className="file-type">{document.fileType}</p>
        <p className="file-date">Uploaded: {new Date(document.createdAt).toLocaleDateString()}</p>
        {document.owner && (
          <p className="file-owner">By: {document.owner.username}</p>
        )}
      </div>
      {showActions && (
        <div className="file-actions">
          <button onClick={() => onView(document)} className="action-btn view-btn">
            View
          </button>
          <button onClick={() => onShare(document)} className="action-btn share-btn">
            Share
          </button>
          <button onClick={() => onDelete(document.id)} className="action-btn delete-btn">
            Delete
          </button>
        </div>
      )}
    </div>
  );
};

export default FileCard;
