import React, { useState } from 'react';
import { 
  Folder, 
  File, 
  MoreVertical, 
  Grid,
  List,
  Star,
  Download,
  Share2,
  Trash2,
  Eye,
  Edit,
  Copy,
  ArrowUp
} from 'lucide-react';

const MyDrive = () => {
  const [viewMode, setViewMode] = useState('grid'); // 'grid' or 'list'
  const [selectedItems, setSelectedItems] = useState([]);

  // Sample folder and file data
  const folders = [
    { id: 1, name: 'Projects', itemCount: 12, modified: '2 days ago', isStarred: true },
    { id: 2, name: 'Documents', itemCount: 8, modified: '1 week ago', isStarred: false },
    { id: 3, name: 'Images', itemCount: 45, modified: '3 days ago', isStarred: false },
    { id: 4, name: 'Archives', itemCount: 6, modified: '2 weeks ago', isStarred: true },
  ];

  const files = [
    {
      id: 1,
      name: 'Annual Report 2024.pdf',
      type: 'pdf',
      size: '4.2 MB',
      modified: '3 hours ago',
      isStarred: true,
      isPublic: false,
      owner: 'You'
    },
    {
      id: 2,
      name: 'Presentation Template.pptx',
      type: 'pptx',
      size: '2.1 MB',
      modified: '1 day ago',
      isStarred: false,
      isPublic: true,
      owner: 'You'
    },
    {
      id: 3,
      name: 'Budget Spreadsheet.xlsx',
      type: 'xlsx',
      size: '1.8 MB',
      modified: '2 days ago',
      isStarred: true,
      isPublic: false,
      owner: 'You'
    },
    {
      id: 4,
      name: 'Meeting Notes.docx',
      type: 'docx',
      size: '0.8 MB',
      modified: '1 week ago',
      isStarred: false,
      isPublic: false,
      owner: 'You'
    },
    {
      id: 5,
      name: 'Logo Design.png',
      type: 'png',
      size: '3.5 MB',
      modified: '2 weeks ago',
      isStarred: false,
      isPublic: true,
      owner: 'You'
    },
  ];

  const getFileTypeColor = (type) => {
    const colors = {
      'pdf': '#DC2626',
      'docx': '#2563EB',
      'xlsx': '#16A34A',
      'pptx': '#EA580C',
      'png': '#7C3AED',
      'jpg': '#7C3AED',
      'default': '#6B7280'
    };
    return colors[type] || colors.default;
  };

  const toggleItemSelection = (type, id) => {
    const itemId = `${type}-${id}`;
    setSelectedItems(prev => 
      prev.includes(itemId) 
        ? prev.filter(item => item !== itemId)
        : [...prev, itemId]
    );
  };

  const isSelected = (type, id) => {
    return selectedItems.includes(`${type}-${id}`);
  };

  const ActionButton = ({ icon: Icon, label, onClick, color = '#6B7280' }) => (
    <button
      onClick={onClick}
      className="flex items-center space-x-2 px-3 py-2 rounded-lg hover:bg-gray-100 transition-colors text-sm"
      style={{ color }}
    >
      <Icon size={16} />
      <span>{label}</span>
    </button>
  );

  const ContextMenu = ({ onClose }) => (
    <div className="absolute right-0 top-8 bg-white rounded-lg shadow-xl border py-2 z-50 min-w-48">
      <ActionButton icon={Eye} label="Preview" onClick={onClose} />
      <ActionButton icon={Download} label="Download" onClick={onClose} />
      <ActionButton icon={Share2} label="Share" onClick={onClose} color="#3B38A0" />
      <ActionButton icon={Edit} label="Rename" onClick={onClose} />
      <ActionButton icon={Copy} label="Make a copy" onClick={onClose} />
      <ActionButton icon={Star} label="Add to starred" onClick={onClose} color="#F59E0B" />
      <hr className="my-2" />
      <ActionButton icon={Trash2} label="Move to trash" onClick={onClose} color="#DC2626" />
    </div>
  );

  return (
    <div className="p-6">
      {/* Header */}
      <div className="flex items-center justify-between mb-6">
        <div>
          <h1 className="text-3xl font-bold text-gray-800 mb-2">My Drive</h1>
          <p className="text-gray-600">Manage your documents and folders</p>
        </div>
        <div className="flex items-center space-x-3">
          <div className="flex bg-gray-100 rounded-lg p-1">
            <button
              onClick={() => setViewMode('grid')}
              className={`p-2 rounded-md transition-colors ${
                viewMode === 'grid' ? 'bg-white shadow-sm' : 'hover:bg-gray-200'
              }`}
            >
              <Grid size={18} className={viewMode === 'grid' ? 'text-gray-800' : 'text-gray-500'} />
            </button>
            <button
              onClick={() => setViewMode('list')}
              className={`p-2 rounded-md transition-colors ${
                viewMode === 'list' ? 'bg-white shadow-sm' : 'hover:bg-gray-200'
              }`}
            >
              <List size={18} className={viewMode === 'list' ? 'text-gray-800' : 'text-gray-500'} />
            </button>
          </div>
        </div>
      </div>

      {/* Breadcrumb */}
      <div className="flex items-center space-x-2 mb-6 text-sm">
        <span className="font-medium" style={{ color: '#3B38A0' }}>My Drive</span>
        <span className="text-gray-400">/</span>
        <span className="text-gray-600">All files</span>
      </div>

      {/* Folders Section */}
      {folders.length > 0 && (
        <div className="mb-8">
          <h2 className="text-lg font-semibold text-gray-800 mb-4">Folders</h2>
          <div className={`grid gap-4 ${viewMode === 'grid' ? 'grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4' : 'grid-cols-1'}`}>
            {folders.map((folder) => (
              <div
                key={folder.id}
                className={`group bg-white rounded-xl p-4 shadow-lg hover:shadow-xl transition-all duration-300 cursor-pointer border-2 ${
                  isSelected('folder', folder.id) ? 'border-blue-500 bg-blue-50' : 'border-transparent hover:border-gray-200'
                }`}
                onClick={() => toggleItemSelection('folder', folder.id)}
              >
                <div className="flex items-center justify-between mb-3">
                  <div 
                    className="w-12 h-12 rounded-lg flex items-center justify-center"
                    style={{ backgroundColor: '#B2B0E8' }}
                  >
                    <Folder size={24} style={{ color: '#1A2A80' }} />
                  </div>
                  <div className="flex items-center space-x-1">
                    {folder.isStarred && (
                      <Star size={16} className="text-yellow-500 fill-current" />
                    )}
                    <div className="relative">
                      <button className="opacity-0 group-hover:opacity-100 p-1 hover:bg-gray-200 rounded transition-all">
                        <MoreVertical size={16} className="text-gray-400" />
                      </button>
                    </div>
                  </div>
                </div>
                <h3 className="font-semibold text-gray-800 mb-2 truncate">{folder.name}</h3>
                <div className="text-sm text-gray-500">
                  <p>{folder.itemCount} items</p>
                  <p>Modified {folder.modified}</p>
                </div>
              </div>
            ))}
          </div>
        </div>
      )}

      {/* Files Section */}
      <div>
        <h2 className="text-lg font-semibold text-gray-800 mb-4">Files</h2>
        {viewMode === 'grid' ? (
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-4">
            {files.map((file) => (
              <div
                key={file.id}
                className={`group bg-white rounded-xl p-4 shadow-lg hover:shadow-xl transition-all duration-300 cursor-pointer border-2 ${
                  isSelected('file', file.id) ? 'border-blue-500 bg-blue-50' : 'border-transparent hover:border-gray-200'
                }`}
                onClick={() => toggleItemSelection('file', file.id)}
              >
                <div className="flex items-center justify-between mb-3">
                  <div 
                    className="w-12 h-12 rounded-lg flex items-center justify-center"
                    style={{ backgroundColor: `${getFileTypeColor(file.type)}15` }}
                  >
                    <File size={24} style={{ color: getFileTypeColor(file.type) }} />
                  </div>
                  <div className="flex items-center space-x-1">
                    {file.isStarred && (
                      <Star size={16} className="text-yellow-500 fill-current" />
                    )}
                    {file.isPublic && (
                      <div 
                        className="w-2 h-2 rounded-full"
                        style={{ backgroundColor: '#3B38A0' }}
                        title="Public"
                      />
                    )}
                    <div className="relative">
                      <button className="opacity-0 group-hover:opacity-100 p-1 hover:bg-gray-200 rounded transition-all">
                        <MoreVertical size={16} className="text-gray-400" />
                      </button>
                    </div>
                  </div>
                </div>
                <h3 className="font-semibold text-gray-800 mb-2 truncate" title={file.name}>
                  {file.name}
                </h3>
                <div className="text-sm text-gray-500">
                  <p>{file.size}</p>
                  <p>Modified {file.modified}</p>
                </div>
              </div>
            ))}
          </div>
        ) : (
          <div className="bg-white rounded-xl shadow-lg">
            <div className="grid grid-cols-12 gap-4 p-4 border-b border-gray-200 text-sm font-medium text-gray-600">
              <div className="col-span-6">Name</div>
              <div className="col-span-2">Owner</div>
              <div className="col-span-2">Modified</div>
              <div className="col-span-1">Size</div>
              <div className="col-span-1"></div>
            </div>
            {files.map((file, index) => (
              <div
                key={file.id}
                className={`grid grid-cols-12 gap-4 p-4 hover:bg-gray-50 transition-colors cursor-pointer ${
                  index !== files.length - 1 ? 'border-b border-gray-100' : ''
                } ${isSelected('file', file.id) ? 'bg-blue-50 border-blue-200' : ''}`}
                onClick={() => toggleItemSelection('file', file.id)}
              >
                <div className="col-span-6 flex items-center space-x-3">
                  <div 
                    className="w-8 h-8 rounded-lg flex items-center justify-center flex-shrink-0"
                    style={{ backgroundColor: `${getFileTypeColor(file.type)}15` }}
                  >
                    <File size={16} style={{ color: getFileTypeColor(file.type) }} />
                  </div>
                  <div className="flex items-center space-x-2 min-w-0">
                    <span className="font-medium text-gray-800 truncate">{file.name}</span>
                    {file.isStarred && (
                      <Star size={14} className="text-yellow-500 fill-current flex-shrink-0" />
                    )}
                    {file.isPublic && (
                      <div 
                        className="w-2 h-2 rounded-full flex-shrink-0"
                        style={{ backgroundColor: '#3B38A0' }}
                        title="Public"
                      />
                    )}
                  </div>
                </div>
                <div className="col-span-2 text-sm text-gray-600 flex items-center">
                  {file.owner}
                </div>
                <div className="col-span-2 text-sm text-gray-600 flex items-center">
                  {file.modified}
                </div>
                <div className="col-span-1 text-sm text-gray-600 flex items-center">
                  {file.size}
                </div>
                <div className="col-span-1 flex items-center justify-center">
                  <div className="relative">
                    <button className="p-1 hover:bg-gray-200 rounded transition-colors">
                      <MoreVertical size={16} className="text-gray-400" />
                    </button>
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>

      {/* Selection Actions */}
      {selectedItems.length > 0 && (
        <div className="fixed bottom-6 left-1/2 transform -translate-x-1/2 bg-white rounded-xl shadow-2xl border p-4 flex items-center space-x-4 z-50">
          <span className="text-sm font-medium text-gray-700">
            {selectedItems.length} item{selectedItems.length > 1 ? 's' : ''} selected
          </span>
          <div className="flex items-center space-x-2">
            <ActionButton icon={Share2} label="Share" onClick={() => {}} color="#3B38A0" />
            <ActionButton icon={Download} label="Download" onClick={() => {}} />
            <ActionButton icon={Trash2} label="Delete" onClick={() => {}} color="#DC2626" />
            <button
              onClick={() => setSelectedItems([])}
              className="ml-2 p-2 hover:bg-gray-100 rounded-lg transition-colors"
            >
              Ã—
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default MyDrive;
