// DocumentsPage component
// import React, { useState, useEffect, useRef } from 'react';
import { useAuth } from '../context/AuthContext';
import { useApp } from '../context/AppContext';
import LoadingSpinner from '../components/common/LoadingSpinner';
import documentService from '../services/documentService';

const DocumentCard = ({ document, onSelect, isSelected, onDownload, onDelete, onShare }) => {
  const getFileIcon = (fileType) => {
    if (fileType?.includes('pdf')) return '📄';
    if (fileType?.includes('image')) return '🖼️';
    if (fileType?.includes('word')) return '📝';
    if (fileType?.includes('excel') || fileType?.includes('spreadsheet')) return '📊';
    if (fileType?.includes('powerpoint') || fileType?.includes('presentation')) return '📊';
    return '📁';
  };

  const formatFileSize = (bytes) => {
    if (!bytes) return '0 B';
    const k = 1024;
    const sizes = ['B', 'KB', 'MB', 'GB'];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    return parseFloat((bytes / Math.pow(k, i)).toFixed(2)) + ' ' + sizes[i];
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString();
  };

  return (
    <div className={`
      card hover:shadow-lg transition-all duration-200 cursor-pointer border-2
      ${isSelected ? 'border-primary-500 bg-primary-50' : 'border-transparent'}
    `}>
      <div className="flex items-start justify-between">
        <div 
          className="flex items-center space-x-3 flex-1"
          onClick={() => onSelect(document.id)}
        >
          <div className="text-3xl">
            {getFileIcon(document.fileType)}
          </div>
          <div className="flex-1 min-w-0">
            <h3 className="text-sm font-medium text-gray-900 truncate">
              {document.title || document.fileName}
            </h3>
            <p className="text-xs text-gray-500 mt-1">
              {document.fileName}
            </p>
            <div className="flex items-center mt-2 text-xs text-gray-400 space-x-4">
              <span>{formatFileSize(document.size)}</span>
              <span>{formatDate(document.createdAt || new Date())}</span>
              {document.isPublic && (
                <span className="bg-green-100 text-green-800 px-2 py-1 rounded-full">
                  Public
                </span>
              )}
            </div>
          </div>
        </div>
        
        <div className="flex items-center space-x-2">
          <button
            onClick={(e) => {
              e.stopPropagation();
              onDownload(document);
            }}
            className="p-1 text-gray-400 hover:text-primary-600 transition-colors"
            title="Download"
          >
            <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 10v6m0 0l-4-4m4 4l4-4m-6 4H7a2 2 0 01-2-2V8a2 2 0 012-2h6l2 2v6a2 2 0 01-2 2z" />
            </svg>
          </button>
          
          <button
            onClick={(e) => {
              e.stopPropagation();
              onShare(document);
            }}
            className="p-1 text-gray-400 hover:text-blue-600 transition-colors"
            title="Share"
          >
            <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M8.684 13.342C8.886 12.938 9 12.482 9 12c0-.482-.114-.938-.316-1.342m0 2.684a3 3 0 110-2.684m0 2.684l6.632 3.316m-6.632-6l6.632-3.316m0 0a3 3 0 105.367-2.684 3 3 0 00-5.367 2.684zm0 9.316a3 3 0 105.367 2.684 3 3 0 00-5.367-2.684z" />
            </svg>
          </button>
          
          <button
            onClick={(e) => {
              e.stopPropagation();
              onDelete(document);
            }}
            className="p-1 text-gray-400 hover:text-red-600 transition-colors"
            title="Delete"
          >
            <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 7l-.867 12.142A2 2 0 0116.138 21H7.862a2 2 0 01-1.995-1.858L5 7m5 4v6m4-6v6m1-10V4a1 1 0 00-1-1h-4a1 1 0 00-1 1v3M4 7h16" />
            </svg>
          </button>
        </div>
      </div>
    </div>
  );
};

const UploadModal = ({ isOpen, onClose, onUpload }) => {
  const [dragOver, setDragOver] = useState(false);
  const [uploading, setUploading] = useState(false);
  const [selectedFiles, setSelectedFiles] = useState([]);
  const fileInputRef = useRef(null);
  const { showSuccess, showError } = useApp();

  const handleFileSelect = (files) => {
    const fileList = Array.from(files);
    setSelectedFiles(fileList);
  };

  const handleDrop = (e) => {
    e.preventDefault();
    setDragOver(false);
    const files = e.dataTransfer.files;
    handleFileSelect(files);
  };

  const handleUploadFiles = async () => {
    if (selectedFiles.length === 0) return;

    setUploading(true);
    try {
      for (const file of selectedFiles) {
        await documentService.uploadDocument(file);
      }
      showSuccess(`Successfully uploaded ${selectedFiles.length} file(s)`);
      onUpload();
      onClose();
      setSelectedFiles([]);
    } catch (error) {
      showError('Upload failed: ' + error.message);
    } finally {
      setUploading(false);
    }
  };

  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50">
      <div className="bg-white rounded-lg max-w-md w-full p-6">
        <div className="flex justify-between items-center mb-4">
          <h3 className="text-lg font-medium">Upload Documents</h3>
          <button
            onClick={onClose}
            className="text-gray-400 hover:text-gray-600"
          >
            <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
            </svg>
          </button>
        </div>

        <div
          className={`upload-zone ${dragOver ? 'drag-over' : ''}`}
          onDrop={handleDrop}
          onDragOver={(e) => { e.preventDefault(); setDragOver(true); }}
          onDragLeave={() => setDragOver(false)}
          onClick={() => fileInputRef.current?.click()}
        >
          <div className="text-center">
            <svg className="mx-auto h-12 w-12 text-gray-400" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M7 16a4 4 0 01-.88-7.903A5 5 0 1115.9 6L16 6a5 5 0 011 9.9M15 13l-3-3m0 0l-3 3m3-3v12" />
            </svg>
            <p className="mt-2 text-sm text-gray-600">
              <span className="font-medium">Click to upload</span> or drag and drop
            </p>
            <p className="text-xs text-gray-500">PDF, DOC, DOCX, XLS, XLSX, PPT, PPTX, images</p>
          </div>
        </div>

        <input
          ref={fileInputRef}
          type="file"
          multiple
          className="hidden"
          onChange={(e) => handleFileSelect(e.target.files)}
          accept=".pdf,.doc,.docx,.xls,.xlsx,.ppt,.pptx,.jpg,.jpeg,.png,.gif,.txt"
        />

        {selectedFiles.length > 0 && (
          <div className="mt-4">
            <h4 className="text-sm font-medium text-gray-900 mb-2">Selected Files:</h4>
            <div className="space-y-2 max-h-32 overflow-y-auto">
              {selectedFiles.map((file, index) => (
                <div key={index} className="flex items-center justify-between text-sm">
                  <span className="truncate">{file.name}</span>
                  <span className="text-gray-500">{(file.size / 1024 / 1024).toFixed(2)} MB</span>
                </div>
              ))}
            </div>
          </div>
        )}

        <div className="flex justify-end space-x-3 mt-6">
          <button
            onClick={onClose}
            className="btn-outline"
            disabled={uploading}
          >
            Cancel
          </button>
          <button
            onClick={handleUploadFiles}
            className="btn-primary"
            disabled={selectedFiles.length === 0 || uploading}
          >
            {uploading ? (
              <>
                <LoadingSpinner size="sm" color="white" />
                <span className="ml-2">Uploading...</span>
              </>
            ) : (
              `Upload ${selectedFiles.length} file(s)`
            )}
          </button>
        </div>
      </div>
    </div>
  );
};

const DocumentsPage = () => {
  const { user } = useAuth();
  const { searchQuery, selectedDocuments, toggleDocumentSelection, clearSelection, showSuccess, showError } = useApp();
  const [documents, setDocuments] = useState([]);
  const [loading, setLoading] = useState(true);
  const [viewMode, setViewMode] = useState('grid'); // 'grid' or 'list'
  const [sortBy, setSortBy] = useState('name'); // 'name', 'date', 'size'
  const [filterType, setFilterType] = useState('all'); // 'all', 'pdf', 'image', etc.
  const [showUploadModal, setShowUploadModal] = useState(false);

  useEffect(() => {
    loadDocuments();
  }, []);

  const loadDocuments = async () => {
    try {
      setLoading(true);
      // Mock data - in real app would call documentService.getMyDocuments()
      const mockDocuments = [
        {
          id: 1,
          title: "Project Report 2024",
          fileName: "project_report_2024.pdf",
          fileType: "application/pdf",
          size: 2048000,
          isPublic: false,
          createdAt: new Date().toISOString(),
          owner: { name: user?.name, email: user?.email }
        },
        {
          id: 2,
          title: "Team Photo",
          fileName: "team_photo.jpg",
          fileType: "image/jpeg",
          size: 1536000,
          isPublic: true,
          createdAt: new Date(Date.now() - 86400000).toISOString(),
          owner: { name: user?.name, email: user?.email }
        },
        {
          id: 3,
          title: "Budget Spreadsheet",
          fileName: "budget_2024.xlsx",
          fileType: "application/vnd.openxmlformats-officedocument.spreadsheetml.sheet",
          size: 512000,
          isPublic: false,
          createdAt: new Date(Date.now() - 172800000).toISOString(),
          owner: { name: user?.name, email: user?.email }
        }
      ];
      setDocuments(mockDocuments);
    } catch (error) {
      showError('Failed to load documents');
    } finally {
      setLoading(false);
    }
  };

  const filteredAndSortedDocuments = documents
    .filter(doc => {
      // Filter by search query
      if (searchQuery && !doc.title.toLowerCase().includes(searchQuery.toLowerCase()) && 
          !doc.fileName.toLowerCase().includes(searchQuery.toLowerCase())) {
        return false;
      }
      
      // Filter by type
      if (filterType === 'all') return true;
      if (filterType === 'pdf') return doc.fileType?.includes('pdf');
      if (filterType === 'image') return doc.fileType?.includes('image');
      if (filterType === 'document') return doc.fileType?.includes('word') || doc.fileType?.includes('document');
      if (filterType === 'spreadsheet') return doc.fileType?.includes('excel') || doc.fileType?.includes('spreadsheet');
      
      return true;
    })
    .sort((a, b) => {
      switch (sortBy) {
        case 'name':
          return a.title.localeCompare(b.title);
        case 'date':
          return new Date(b.createdAt) - new Date(a.createdAt);
        case 'size':
          return (b.size || 0) - (a.size || 0);
        default:
          return 0;
      }
    });

  const handleDownload = async (document) => {
    try {
      showSuccess(`Downloading ${document.fileName}...`);
      // In real app: await documentService.downloadDocument(document.id);
    } catch (error) {
      showError('Download failed');
    }
  };

  const handleDelete = async (document) => {
    if (window.confirm(`Are you sure you want to delete "${document.title}"?`)) {
      try {
        // In real app: await documentService.deleteDocument(document.id);
        setDocuments(docs => docs.filter(d => d.id !== document.id));
        showSuccess('Document deleted successfully');
      } catch (error) {
        showError('Delete failed');
      }
    }
  };

  const handleShare = (document) => {
    showSuccess(`Sharing options for "${document.title}" would open here`);
  };

  const handleBulkDelete = async () => {
    if (selectedDocuments.length === 0) return;
    
    if (window.confirm(`Delete ${selectedDocuments.length} selected documents?`)) {
      try {
        setDocuments(docs => docs.filter(d => !selectedDocuments.includes(d.id)));
        clearSelection();
        showSuccess(`${selectedDocuments.length} documents deleted`);
      } catch (error) {
        showError('Bulk delete failed');
      }
    }
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center h-64">
        <LoadingSpinner size="lg" />
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex flex-col sm:flex-row sm:items-center sm:justify-between">
        <div>
          <h1 className="text-2xl font-bold text-gray-900">Documents</h1>
          <p className="text-gray-600 mt-1">Manage and organize your files</p>
        </div>
        
        <div className="mt-4 sm:mt-0 flex items-center space-x-3">
          {selectedDocuments.length > 0 && (
            <button
              onClick={handleBulkDelete}
              className="btn-danger text-sm"
            >
              Delete ({selectedDocuments.length})
            </button>
          )}
          
          <button
            onClick={() => setShowUploadModal(true)}
            className="btn-primary"
          >
            <svg className="w-5 h-5 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 6v6m0 0v6m0-6h6m-6 0H6" />
            </svg>
            Upload Files
          </button>
        </div>
      </div>

      {/* Filters and Controls */}
      <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-4">
        <div className="flex flex-col sm:flex-row sm:items-center sm:justify-between space-y-4 sm:space-y-0">
          <div className="flex items-center space-x-4">
            <select
              value={filterType}
              onChange={(e) => setFilterType(e.target.value)}
              className="text-sm border border-gray-300 rounded-lg px-3 py-2 focus:ring-primary-500 focus:border-primary-500"
            >
              <option value="all">All Types</option>
              <option value="pdf">PDF</option>
              <option value="image">Images</option>
              <option value="document">Documents</option>
              <option value="spreadsheet">Spreadsheets</option>
            </select>

            <select
              value={sortBy}
              onChange={(e) => setSortBy(e.target.value)}
              className="text-sm border border-gray-300 rounded-lg px-3 py-2 focus:ring-primary-500 focus:border-primary-500"
            >
              <option value="name">Sort by Name</option>
              <option value="date">Sort by Date</option>
              <option value="size">Sort by Size</option>
            </select>
          </div>

          <div className="flex items-center space-x-2">
            <button
              onClick={() => setViewMode('grid')}
              className={`p-2 rounded-lg ${viewMode === 'grid' ? 'bg-primary-100 text-primary-600' : 'text-gray-400 hover:text-gray-600'}`}
            >
              <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 6a2 2 0 012-2h2a2 2 0 012 2v2a2 2 0 01-2 2H6a2 2 0 01-2-2V6zM14 6a2 2 0 012-2h2a2 2 0 012 2v2a2 2 0 01-2 2h-2a2 2 0 01-2-2V6zM4 16a2 2 0 012-2h2a2 2 0 012 2v2a2 2 0 01-2 2H6a2 2 0 01-2-2v-2zM14 16a2 2 0 012-2h2a2 2 0 012 2v2a2 2 0 01-2 2h-2a2 2 0 01-2-2v-2z" />
              </svg>
            </button>
            <button
              onClick={() => setViewMode('list')}
              className={`p-2 rounded-lg ${viewMode === 'list' ? 'bg-primary-100 text-primary-600' : 'text-gray-400 hover:text-gray-600'}`}
            >
              <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 6h16M4 10h16M4 14h16M4 18h16" />
              </svg>
            </button>
          </div>
        </div>
      </div>

      {/* Documents Grid/List */}
      {filteredAndSortedDocuments.length === 0 ? (
        <div className="text-center py-12">
          <svg className="mx-auto h-12 w-12 text-gray-400" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 12h6m-6 4h6m2 5H7a2 2 0 01-2-2V5a2 2 0 012-2h5.586a1 1 0 01.707.293l5.414 5.414a1 1 0 01.293.707V19a2 2 0 01-2 2z" />
          </svg>
          <h3 className="mt-2 text-sm font-medium text-gray-900">No documents found</h3>
          <p className="mt-1 text-sm text-gray-500">
            {searchQuery ? 'Try adjusting your search or filters.' : 'Get started by uploading your first document.'}
          </p>
          {!searchQuery && (
            <div className="mt-6">
              <button
                onClick={() => setShowUploadModal(true)}
                className="btn-primary"
              >
                Upload Document
              </button>
            </div>
          )}
        </div>
      ) : (
        <div className={`
          ${viewMode === 'grid' 
            ? 'grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6' 
            : 'space-y-4'
          }
        `}>
          {filteredAndSortedDocuments.map(document => (
            <DocumentCard
              key={document.id}
              document={document}
              isSelected={selectedDocuments.includes(document.id)}
              onSelect={toggleDocumentSelection}
              onDownload={handleDownload}
              onDelete={handleDelete}
              onShare={handleShare}
            />
          ))}
        </div>
      )}

      {/* Upload Modal */}
      <UploadModal
        isOpen={showUploadModal}
        onClose={() => setShowUploadModal(false)}
        onUpload={loadDocuments}
      />
    </div>
  );
};

export default DocumentsPage;
