// src/components/documents/FolderGrid.js
import React from 'react';
import './FolderGrid.css';

const FolderGrid = ({ folders, view = 'grid' }) => {
  if (folders.length === 0) {
    return (
      <div className="empty-state">
        <p>No folders found</p>
      </div>
    );
  }

  return (
    <div className={`folder-grid ${view}`}>
      {folders.map(folder => (
        <div key={folder.id} className="folder-item">
          <div className="folder-icon">üìÅ</div>
          <div className="folder-info">
            <h3 className="folder-name">{folder.name}</h3>
            <p className="folder-meta">
              {folder.documents?.length || 0} documents
            </p>
          </div>
          <div className="folder-actions">
            <button className="action-btn">Open</button>
            <button className="action-btn">Rename</button>
            <button className="action-btn delete">Delete</button>
          </div>
        </div>
      ))}
    </div>
  );
};

export default FolderGrid;
